import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, Navigate } from 'react-router-dom';

import axios from 'axios';

// Import komponen-komponen utama Anda
import Layout from './components/Layout';
import Home from './components/Home';
import Article from './components/Article';
import Contact from './components/Contact';
import AdminLogin from './components/AdminLogin';
import Dashboard from './components/Dashboard';

function App() {
  const navigate = useNavigate();

  const [heroText, setHeroText] = useState({
    name: '',
    role_text: '',
    main_title: '',
    year_text: '',
    email: '',
    social_linkedin: '',
    social_github: '',
    social_instagram: ''
  });
  const [loadingHeroText, setLoadingHeroText] = useState(true);
  const [heroTextError, setHeroTextError] = useState(null);

  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);
  const [isAuthChecked, setIsAuthChecked] = useState(false); // State untuk menandakan apakah status autentikasi telah diperiksa

  const API_BASE_URL = 'http://localhost:3001/api';

  useEffect(() => {
    const fetchInitialData = async () => {
      // Fetch hero text data
      try {
        const heroResponse = await axios.get(`${API_BASE_URL}/hero`);
        setHeroText({
          name: heroResponse.data.name || '',
          role_text: heroResponse.data.role_text || '',
          main_title: heroResponse.data.main_title || '',
          year_text: heroResponse.data.year_text || '',
          email: heroResponse.data.email || '',
          social_linkedin: heroResponse.data.social_linkedin || '',
          social_github: heroResponse.data.social_github || '',
          social_instagram: heroResponse.data.social_instagram || ''
        });
      } catch (err) {
        console.error("Failed to fetch hero text in App.js:", err);
        setHeroTextError("Gagal memuat data utama. Pastikan backend berjalan.");
      } finally {
        setLoadingHeroText(false);
      }

      // Periksa status login dari localStorage *SETELAH* semua data lain atau secara independen
      // Penting: Pastikan ini berjalan dan set isAuthChecked ke true
      const loggedInStatus = localStorage.getItem('isAdminLoggedIn') === 'true';
      console.log('App.js: Initial localStorage isAdminLoggedIn:', loggedInStatus);
      setIsAdminLoggedIn(loggedInStatus);
      setIsAuthChecked(true); // Setelah memeriksa localStorage, set true
      console.log('App.js: isAuthChecked set to true, isAdminLoggedIn state:', loggedInStatus);
    };

    fetchInitialData();
  }, []); // Efek ini hanya berjalan sekali saat komponen di-mount

  const handleAdminLogin = (success) => {
    console.log('App.js: handleAdminLogin called, success:', success);
    setIsAdminLoggedIn(success);
    if (success) {
      localStorage.setItem('isAdminLoggedIn', 'true');
      console.log('App.js: Login successful, navigating to /dashboard');
      navigate('/dashboard'); // Navigasi setelah login sukses
    } else {
      localStorage.removeItem('isAdminLoggedIn');
      console.error('App.js: Login failed.');
    }
  };

  const handleAdminLogout = () => {
    console.log('App.js: handleAdminLogout called');
    setIsAdminLoggedIn(false);
    localStorage.removeItem('isAdminLoggedIn');
    console.log('App.js: Logged out, navigating to /');
    navigate('/'); // Kembali ke halaman utama setelah logout
  };

  // Komponen pembantu untuk melindungi rute
  const ProtectedRoute = ({ children }) => {
    console.log('ProtectedRoute rendered. isAdminLoggedIn:', isAdminLoggedIn, 'isAuthChecked:', isAuthChecked);

    // Tampilkan loading screen global jika status autentikasi belum diperiksa
    if (!isAuthChecked) {
      return (
        <div className="flex flex-col items-center justify-center min-h-[calc(100vh-64px)] bg-gray-100 text-gray-800">
          <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
          <p className="mt-4 text-lg">Memverifikasi status login...</p>
        </div>
      );
    }

    // Setelah status autentikasi diperiksa (isAuthChecked === true)
    if (!isAdminLoggedIn) {
      console.log('ProtectedRoute: Not logged in, redirecting to /admin');
      return <Navigate to="/admin" replace />; // Langsung redirect jika tidak login
    }

    console.log('ProtectedRoute: Logged in, rendering children.');
    return children; // Render komponen anak jika sudah login
  };

  // Tampilkan loading screen awal aplikasi jika data heroText belum dimuat
  if (loadingHeroText || !isAuthChecked) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
        <p className="mt-4 text-lg">Memuat aplikasi dan memverifikasi status login...</p>
      </div>
    );
  }

  // Tampilkan pesan error fatal jika gagal memuat data heroText
  if (heroTextError) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-red-100 text-red-800 p-4">
        <p className="text-xl font-bold">Terjadi Kesalahan Fatal:</p>
        <p className="mt-2 text-center">{heroTextError}</p>
        <p className="mt-4">Aplikasi tidak dapat dimuat. Mohon pastikan server backend berjalan dan dapat diakses.</p>
      </div>
    );
  }

  return (
    // Layout menerima heroText, isAdminLoggedIn, dan handleAdminLogout
    <Layout
      heroText={heroText}
      isAdminLoggedIn={isAdminLoggedIn}
      handleAdminLogout={handleAdminLogout}
    >
      <Routes>
        <Route path="/" element={<Home heroText={heroText} />} />
        <Route path="/articles" element={<Article heroText={heroText} />} />
        <Route path="/contact" element={<Contact heroText={heroText} />} />

        {/* Rute khusus untuk halaman login admin */}
        <Route path="/admin" element={<AdminLogin onLogin={handleAdminLogin} />} />

        {/* Rute Dashboard yang dilindungi */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />

        {/* Fallback untuk rute yang tidak ditemukan */}
        <Route path="*" element={<Home heroText={heroText} />} />
      </Routes>
    </Layout>
  );
}

export default App;
