import React from 'react';
import { Link } from 'react-router-dom';

// Komponen Header
const Header = ({ heroText }) => {
  return (
    // Menggunakan gradien warna yang lebih gelap untuk kontras dengan Hero Section
    <header className="bg-gradient-to-r from-gray-900 to-indigo-900 text-white p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        {/* Nama/Logo Situs */}
        <Link to="/" className="text-3xl font-bold tracking-tight hover:text-indigo-300 transition-colors duration-300">
          {heroText.name || 'Your Portfolio'}
        </Link>

        {/* Navigasi Utama */}
        <nav className="space-x-6 text-lg">
          {/* Link navigasi dengan warna hover yang konsisten */}
          <Link to="/" className="hover:text-indigo-300 transition-colors duration-300 font-medium">Beranda</Link>
          <Link to="/articles" className="hover:text-indigo-300 transition-colors duration-300 font-medium">Artikel</Link>
          <Link to="/contact" className="hover:text-indigo-300 transition-colors duration-300 font-medium">Kontak</Link>
          {/* Tambahkan link navigasi lain di sini */}
        </nav>
      </div>
    </header>
  );
};

export default Header;
