import React from 'react';
import Header from './Header';

// Komponen Layout yang membungkus semua halaman untuk tema yang konsisten
// Menerima children (konten halaman) dan heroText (untuk Header/Footer) sebagai props
// Juga menerima isAdminLoggedIn dan handleAdminLogout untuk Header
const Layout = ({ children, heroText, isAdminLoggedIn, handleAdminLogout }) => {
  return (
    <div className="flex flex-col min-h-screen">
      {/* Header aplikasi, meneruskan semua props yang dibutuhkan */}
      <Header
        heroText={heroText}
        isAdminLoggedIn={isAdminLoggedIn}
        handleAdminLogout={handleAdminLogout}
      />

      {/* Konten utama halaman. Flex-grow memastikan konten mengisi ruang yang tersedia, mendorong footer ke bawah. */}
      <main className="flex-grow">
        {children} {/* Ini adalah konten dari halaman yang sedang dirender (Home, Article, Contact) */}
      </main>

      {/* Footer aplikasi, meneruskan heroText untuk menampilkan hak cipta dan sosial media */}
      <footer className="text-center py-8 bg-gray-800 text-gray-300 mt-auto">
        <div className="container mx-auto px-4">
          <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
          <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>

          {/* Social Media Links from heroText */}
          <div className="flex justify-center space-x-6 mt-4">
            {heroText.social_linkedin && (
              <a href={heroText.social_linkedin} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="LinkedIn">
                <i className="fab fa-linkedin"></i>
              </a>
            )}
            {heroText.social_github && (
              <a href={heroText.social_github} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="GitHub">
                  <i className="fab fa-github"></i>
              </a>
            )}
            {heroText.social_instagram && (
              <a href={heroText.social_instagram} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="Instagram">
                <i className="fab fa-instagram"></i>
              </a>
            )}
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Layout;
