import { useEffect, useState } from 'react';
import axios from 'axios';

// Import komponen MUI (jika Anda masih menggunakannya di bagian lain dari dashboard)
import { Typography, Box, Button, TextField, CircularProgress, Switch, FormControlLabel } from '@mui/material';

// Import komponen form yang diperlukan
import AddProjectForm from './AddProjectForm';
import AddArticleForm from './AddArticleForm';
import AddHobbyForm from './AddHobbyForm'; // Import komponen AddHobbyForm yang baru


export default function Dashboard() {
  const API_BASE_URL = 'http://localhost:3001/api';

  // === State Management ===
  const [skills, setSkills] = useState([]);
  const [newSkill, setNewSkill] = useState({ nama_skill: '', level: '' });
  const [editingSkillId, setEditingSkillId] = useState(null);

  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({ nama_project: '', deskripsi: '', link: '', gambar: '' });
  const [editingProjectId, setEditingProjectId] = useState(null);

  const [experiences, setExperiences] = useState([]);
  const [newExperience, setNewExperience] = useState({ posisi: '', tempat: '', tahun: '', deskripsi: '' });
  const [editingExperienceId, setEditingExperienceId] = useState(null);

  const [education, setEducation] = useState([]);
  const [newEducation, setNewEducation] = useState({ jurusan: '', institusi: '', tahun: '' });
  const [editingEducationId, setEditingEducationId] = useState(null);

  const [activities, setActivities] = useState([]);
  const [newActivity, setNewActivity] = useState({ title: '', time: '', image: '', deskripsi: '' });
  const [editingActivityId, setEditingActivityId] = useState(null);

  const [articles, setArticles] = useState([]);
  const [newArticle, setNewArticle] = useState({ title: '', author: '', date: '', content: '', image_url: '' });
  const [editingArticle, setEditingArticle] = useState(null);
  const [showAddArticleForm, setShowAddArticleForm] = useState(false);

  // --- STATE UNTUK HERO CONTENT ---
  const [heroContent, setHeroContent] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [editingHeroContent, setEditingHeroContent] = useState(false);

  // --- STATE UNTUK HOBBIES ---
  const [hobbies, setHobbies] = useState([]);
  const [showAddHobbyForm, setShowAddHobbyForm] = useState(false);
  const [editingHobby, setEditingHobby] = useState(null);


  // === Fetch Data on Load ===
  // Menggunakan satu useEffect untuk fetch semua data awal
  useEffect(() => {
    fetchSkills();
    fetchProjects();
    fetchExperiences();
    fetchEducation();
    fetchActivities();
    fetchArticles();
    fetchHeroContent();
    fetchHobbies();
  }, []); // Array dependensi kosong agar hanya berjalan sekali saat komponen dimuat

  // --- FETCH HERO CONTENT ---
  const fetchHeroContent = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/hero`);
      setHeroContent(response.data || { name: '', role_text: '', main_title: '', year_text: '' }); // Pastikan objek default jika kosong
    } catch (error) {
      console.error('Error fetching hero content:', error);
    }
  };

  // --- FETCH HOBBIES ---
  const fetchHobbies = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/hobbies`);
      setHobbies(response.data);
    } catch (error) {
      console.error('Error fetching hobbies:', error);
    }
  };

  // === Skill Management ===
  const fetchSkills = () => {
    axios.get(`${API_BASE_URL}/skills`)
      .then(res => setSkills(res.data))
      .catch(err => console.error("Error fetching skills:", err));
  };
  const addSkill = () => {
    if (!newSkill.nama_skill || newSkill.level === '') {
      alert('Nama skill dan level harus diisi.');
      return;
    }
    axios.post(`${API_BASE_URL}/skills`, newSkill)
      .then(() => {
        fetchSkills();
        setNewSkill({ nama_skill: '', level: '' });
      })
      .catch(err => console.error("Error adding skill:", err));
  };
  const deleteSkill = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus skill ini?')) { // Ganti dengan modal kustom
      axios.delete(`${API_BASE_URL}/skills/${id}`)
        .then(fetchSkills)
        .catch(err => console.error("Error deleting skill:", err));
    }
  };
  const startEditSkill = (skill) => {
    setEditingSkillId(skill.id);
    setNewSkill({ nama_skill: skill.nama_skill, level: skill.level });
  };
  const updateSkill = () => {
    if (!newSkill.nama_skill || newSkill.level === '') {
      alert('Nama skill dan level harus diisi.');
      return;
    }
    axios.put(`${API_BASE_URL}/skills/${editingSkillId}`, newSkill)
      .then(() => {
        fetchSkills();
        setEditingSkillId(null);
        setNewSkill({ nama_skill: '', level: '' });
      })
      .catch(err => console.error("Error updating skill:", err));
  };

  // === Project Management ===
  const fetchProjects = () => {
    axios.get(`${API_BASE_URL}/projects`)
      .then(res => setProjects(res.data))
      .catch(err => console.error("Error fetching projects:", err));
  };
  const addProject = () => { // Form AddProjectForm sudah menangani POST sendiri
    fetchProjects(); // Hanya perlu refresh daftar setelah AddProjectForm selesai
  };
  const deleteProject = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus proyek ini?')) { // Ganti dengan modal kustom
      axios.delete(`${API_BASE_URL}/projects/${id}`)
        .then(fetchProjects)
        .catch(err => console.error("Error deleting project:", err));
    }
  };
  const startEditProject = (project) => {
    setEditingProjectId(project.id);
    setNewProject(project);
  };
  const updateProject = () => {
    if (!newProject.nama_project || !newProject.deskripsi) {
      alert('Nama project dan deskripsi harus diisi.');
      return;
    }
    axios.put(`${API_BASE_URL}/projects/${editingProjectId}`, newProject)
      .then(() => {
        fetchProjects();
        setEditingProjectId(null);
        setNewProject({ nama_project: '', deskripsi: '', link: '', gambar: '' });
      })
      .catch(err => console.error("Error updating project:", err));
  };

  // === Experience Management ===
  const fetchExperiences = () => {
    axios.get(`${API_BASE_URL}/experiences`)
      .then(res => setExperiences(res.data))
      .catch(err => console.error("Error fetching experiences:", err));
  };
  const addExperience = () => {
    if (!newExperience.posisi || !newExperience.tempat || !newExperience.tahun || !newExperience.deskripsi) {
      alert('Semua field pengalaman harus diisi.');
      return;
    }
    axios.post(`${API_BASE_URL}/experiences`, newExperience)
      .then(() => {
        fetchExperiences();
        setNewExperience({ posisi: '', tempat: '', tahun: '', deskripsi: '' });
      })
      .catch(err => console.error("Error adding experience:", err));
  };
  const deleteExperience = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus pengalaman ini?')) { // Ganti dengan modal kustom
      axios.delete(`${API_BASE_URL}/experiences/${id}`)
        .then(fetchExperiences)
        .catch(err => console.error("Error deleting experience:", err));
    }
  };
  const startEditExperience = (exp) => {
    setEditingExperienceId(exp.id);
    setNewExperience(exp);
  };
  const updateExperience = () => {
    if (!newExperience.posisi || !newExperience.tempat || !newExperience.tahun || !newExperience.deskripsi) {
      alert('Semua field pengalaman harus diisi.');
      return;
    }
    axios.put(`${API_BASE_URL}/experiences/${editingExperienceId}`, newExperience)
      .then(() => {
        fetchExperiences();
        setEditingExperienceId(null);
        setNewExperience({ posisi: '', tempat: '', tahun: '', deskripsi: '' });
      })
      .catch(err => console.error("Error updating experience:", err));
  };

  // === Education Management ===
  const fetchEducation = () => {
    axios.get(`${API_BASE_URL}/education`)
      .then(res => setEducation(res.data))
      .catch(err => console.error("Error fetching education:", err));
  };
  const addEducation = () => {
    if (!newEducation.jurusan || !newEducation.institusi || !newEducation.tahun) {
      alert('Semua field pendidikan harus diisi.');
      return;
    }
    axios.post(`${API_BASE_URL}/education`, newEducation)
      .then(() => {
        fetchEducation();
        setNewEducation({ jurusan: '', institusi: '', tahun: '' });
      })
      .catch(err => console.error("Error adding education:", err));
  };
  const deleteEducation = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus pendidikan ini?')) { // Ganti dengan modal kustom
      axios.delete(`${API_BASE_URL}/education/${id}`)
        .then(fetchEducation)
        .catch(err => console.error("Error deleting education:", err));
    }
  };
  const startEditEducation = (edu) => {
    setEditingEducationId(edu.id);
    setNewEducation(edu);
  };
  const updateEducation = () => {
    if (!newEducation.jurusan || !newEducation.institusi || !newEducation.tahun) {
      alert('Semua field pendidikan harus diisi.');
      return;
    }
    axios.put(`${API_BASE_URL}/education/${editingEducationId}`, newEducation)
      .then(() => {
        fetchEducation();
        setEditingEducationId(null);
        setNewEducation({ jurusan: '', institusi: '', tahun: '' });
      })
      .catch(err => console.error("Error updating education:", err));
  };

  // === Activity Management ===
  const fetchActivities = () => {
    axios.get(`${API_BASE_URL}/activities`)
      .then(res => setActivities(res.data))
      .catch(err => console.error("Error fetching activities:", err));
  };
  const addActivity = () => {
    if (!newActivity.title || !newActivity.time || !newActivity.deskripsi) {
      alert('Judul, waktu, dan deskripsi aktivitas harus diisi.');
      return;
    }
    axios.post(`${API_BASE_URL}/activities`, newActivity)
      .then(() => {
        fetchActivities();
        setNewActivity({ title: '', time: '', image: '', deskripsi: '' });
      })
      .catch(err => console.error("Error adding activity:", err));
  };
  const deleteActivity = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus aktivitas ini?')) { // Ganti dengan modal kustom
      axios.delete(`${API_BASE_URL}/activities/${id}`)
        .then(fetchActivities)
        .catch(err => console.error("Error deleting activity:", err));
    }
  };
  const startEditActivity = (act) => {
    setEditingActivityId(act.id);
    setNewActivity(act);
  };
  const updateActivity = () => {
    if (!newActivity.title || !newActivity.time || !newActivity.deskripsi) {
      alert('Judul, waktu, dan deskripsi aktivitas harus diisi.');
      return;
    }
    axios.put(`${API_BASE_URL}/activities/${editingActivityId}`, newActivity)
      .then(() => {
        fetchActivities();
        setEditingActivityId(null);
        setNewActivity({ title: '', time: '', image: '', deskripsi: '' });
      })
      .catch(err => console.error("Error updating activity:", err));
  };

  // === Article Management ===
  const fetchArticles = () => {
    axios.get(`${API_BASE_URL}/articles`)
      .then(res => setArticles(res.data))
      .catch(err => console.error("Error fetching articles:", err));
  };
  const deleteArticle = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus artikel ini?')) { // Ganti dengan modal kustom
      axios.delete(`${API_BASE_URL}/articles/${id}`)
        .then(fetchArticles)
        .catch(err => console.error("Error deleting article:", err));
    }
  };
  const handleSaveArticle = () => {
    setShowAddArticleForm(false);
    setEditingArticle(null);
    fetchArticles();
  };
  const startEditArticle = (article) => {
    setEditingArticle(article);
    setShowAddArticleForm(true);
  };

  // --- HERO CONTENT MANAGEMENT ---
  const handleHeroContentChange = (e) => {
    const { name, value } = e.target;
    setHeroContent(prev => ({ ...prev, [name]: value }));
  };

  const saveHeroContent = async () => {
    try {
      await axios.put(`${API_BASE_URL}/hero`, heroContent);
      // alert('Konten Hero berhasil diperbarui!'); // Ganti dengan modal kustom jika ingin
      alertWrapper('Konten Hero berhasil diperbarui!');
      setEditingHeroContent(false);
      fetchHeroContent(); // Refresh data after saving
    } catch (error) {
      console.error('Error saving hero content:', error.response ? error.response.data : error.message);
      // alert(`Gagal memperbarui konten Hero: ${error.response?.data?.error || error.message}`); // Ganti dengan modal kustom
      alertWrapper(`Gagal memperbarui konten Hero: ${error.response?.data?.error || error.message}`);
    }
  };

  // --- HOBBIES MANAGEMENT ---
  const handleSaveHobby = () => {
    setShowAddHobbyForm(false);
    setEditingHobby(null);
    fetchHobbies(); // Refresh daftar hobi setelah simpan
  };

  const startEditHobby = (hobby) => {
    setEditingHobby(hobby);
    setShowAddHobbyForm(true);
  };

  const deleteHobby = async (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus hobi ini?')) { // Ganti dengan modal kustom
      try {
        await axios.delete(`${API_BASE_URL}/hobbies/${id}`);
        // alert('Hobi berhasil dihapus!'); // Ganti dengan modal kustom
        alertWrapper('Hobi berhasil dihapus!');
        fetchHobbies(); // Refresh daftar hobi setelah hapus
      } catch (error) {
        console.error('Error deleting hobby:', error.response ? error.response.data : error.message);
        // alert(`Gagal menghapus hobi: ${error.response?.data?.message || error.message}`); // Ganti dengan modal kustom
        alertWrapper(`Gagal menghapus hobi: ${error.response?.data?.message || error.message}`);
      }
    }
  };

  // Custom alert wrapper to replace window.alert
  const alertWrapper = (message) => {
    // In a real application, you'd use a custom modal component here
    // For now, using window.alert as a placeholder for simplicity due to environment constraints.
    // If you have a custom modal, replace this with your modal's open logic.
    window.alert(message);
  };


  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-gray-800 mb-8 text-center">Admin Dashboard</h1>

      {/* Hero Content Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Konten Hero Section
        </Typography>
        {editingHeroContent ? (
          <div className="space-y-4">
            <TextField
              label="Nama (Hero)"
              name="name"
              value={heroContent.name}
              onChange={handleHeroContentChange}
              fullWidth
              variant="outlined"
            />
            <TextField
              label="Teks Peran (Graphic Designing)"
              name="role_text"
              value={heroContent.role_text}
              onChange={handleHeroContentChange}
              fullWidth
              variant="outlined"
            />
            <TextField
              label="Judul Utama (PORTFOLIO)"
              name="main_title"
              value={heroContent.main_title}
              onChange={handleHeroContentChange}
              fullWidth
              variant="outlined"
            />
            <TextField
              label="Teks Tahun (2023)"
              name="year_text"
              value={heroContent.year_text}
              onChange={handleHeroContentChange}
              fullWidth
              variant="outlined"
            />
            <div className="flex space-x-2 justify-end">
              <Button variant="contained" onClick={saveHeroContent}>Simpan Hero Content</Button>
              <Button variant="outlined" onClick={() => setEditingHeroContent(false)}>Batal</Button>
            </div>
          </div>
        ) : (
          <div className="text-gray-700">
            <p><strong>Nama:</strong> {heroContent.name}</p>
            <p><strong>Peran:</strong> {heroContent.role_text}</p>
            <p><strong>Judul Utama:</strong> {heroContent.main_title}</p>
            <p><strong>Tahun:</strong> {heroContent.year_text}</p>
            <Button variant="contained" className="mt-4" onClick={() => setEditingHeroContent(true)}>Edit Hero Content</Button>
          </div>
        )}
      </Box>

      {/* Hobbies Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Hobi
        </Typography>
        {showAddHobbyForm && (
          <AddHobbyForm
            hobbyToEdit={editingHobby}
            onSave={handleSaveHobby}
            onCancel={() => {
              setShowAddHobbyForm(false);
              setEditingHobby(null);
            }}
          />
        )}
        {!showAddHobbyForm && (
          <Button variant="contained" onClick={() => setShowAddHobbyForm(true)} className="mb-4">
            Tambah Hobi Baru
          </Button>
        )}

        {hobbies.length === 0 ? (
          <Typography variant="body1" className="text-gray-600 mt-4">Belum ada hobi.</Typography>
        ) : (
          <ul className="space-y-3 mt-4">
            {hobbies.map(hobby => (
              <li key={hobby.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
                <span className={`text-gray-800 ${hobby.is_bold ? 'font-bold' : ''}`}>
                  {hobby.hobby_name} {hobby.is_bold ? '(Tebal)' : ''}
                </span>
                <div className="flex space-x-2">
                  <Button variant="outlined" size="small" onClick={() => startEditHobby(hobby)}>Edit</Button>
                  <Button variant="contained" color="error" size="small" onClick={() => deleteHobby(hobby.id)}>Hapus</Button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </Box>


      {/* Skills Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Keterampilan
        </Typography>
        <div className="space-y-4">
          <TextField
            label="Nama Keterampilan"
            value={newSkill.nama_skill}
            onChange={e => setNewSkill({ ...newSkill, nama_skill: e.target.value })}
            fullWidth
            variant="outlined"
          />
          <TextField
            label="Level (0-100)"
            type="number"
            value={newSkill.level}
            onChange={e => setNewSkill({ ...newSkill, level: e.target.value })}
            fullWidth
            variant="outlined"
            inputProps={{ min: 0, max: 100 }}
          />
          <Button variant="contained" onClick={editingSkillId ? updateSkill : addSkill}>
            {editingSkillId ? 'Perbarui Keterampilan' : 'Tambah Keterampilan'}
          </Button>
          {editingSkillId && (
            <Button variant="outlined" onClick={() => {
              setEditingSkillId(null);
              setNewSkill({ nama_skill: '', level: '' });
            }}>
              Batal Edit
            </Button>
          )}
        </div>
        <ul className="space-y-3 mt-6">
          {skills.map(skill => (
            <li key={skill.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
              <span className="text-gray-800">{skill.nama_skill} ({skill.level}%)</span>
              <div className="flex space-x-2">
                <Button variant="outlined" size="small" onClick={() => startEditSkill(skill)}>Edit</Button>
                <Button variant="contained" color="error" size="small" onClick={() => deleteSkill(skill.id)}>Hapus</Button>
              </div>
            </li>
          ))}
        </ul>
      </Box>

      {/* Projects Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Proyek
        </Typography>
        <AddProjectForm onAdd={fetchProjects} /> {/* Form tambah proyek */}
        <ul className="space-y-3 mt-6">
          {projects.map(project => (
            <li key={project.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
              <span className="text-gray-800">{project.nama_project}</span>
              <div className="flex space-x-2">
                <Button variant="outlined" size="small" onClick={() => startEditProject(project)}>Edit</Button>
                <Button variant="contained" color="error" size="small" onClick={() => deleteProject(project.id)}>Hapus</Button>
              </div>
            </li>
          ))}
        </ul>
        {editingProjectId && (
          <div className="mt-6 p-4 border border-blue-300 rounded-lg bg-blue-50">
            <Typography variant="h6" className="mb-3">Edit Proyek</Typography>
            <TextField
              label="Nama Proyek"
              name="nama_project"
              value={newProject.nama_project}
              onChange={e => setNewProject({ ...newProject, nama_project: e.target.value })}
              fullWidth
              variant="outlined"
              className="mb-3"
            />
            <TextField
              label="Deskripsi"
              name="deskripsi"
              value={newProject.deskripsi}
              onChange={e => setNewProject({ ...newProject, deskripsi: e.target.value })}
              fullWidth
              multiline
              rows={3}
              variant="outlined"
              className="mb-3"
            />
            <TextField
              label="Link Proyek"
              name="link"
              value={newProject.link}
              onChange={e => setNewProject({ ...newProject, link: e.target.value })}
              fullWidth
              variant="outlined"
              className="mb-3"
            />
            <TextField
              label="URL Gambar"
              name="gambar"
              value={newProject.gambar}
              onChange={e => setNewProject({ ...newProject, gambar: e.target.value })}
              fullWidth
              variant="outlined"
              className="mb-3"
            />
            <div className="flex space-x-2 justify-end">
              <Button variant="contained" onClick={updateProject}>Perbarui Proyek</Button>
              <Button variant="outlined" onClick={() => setEditingProjectId(null)}>Batal</Button>
            </div>
          </div>
        )}
      </Box>

      {/* Experience Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Pengalaman Kerja
        </Typography>
        <div className="space-y-4">
          <TextField label="Posisi" value={newExperience.posisi} onChange={e => setNewExperience({ ...newExperience, posisi: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Tempat" value={newExperience.tempat} onChange={e => setNewExperience({ ...newExperience, tempat: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Tahun" value={newExperience.tahun} onChange={e => setNewExperience({ ...newExperience, tahun: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Deskripsi" value={newExperience.deskripsi} onChange={e => setNewExperience({ ...newExperience, deskripsi: e.target.value })} fullWidth multiline rows={3} variant="outlined" />
          <Button variant="contained" onClick={editingExperienceId ? updateExperience : addExperience}>
            {editingExperienceId ? 'Perbarui Pengalaman' : 'Tambah Pengalaman'}
          </Button>
          {editingExperienceId && (
            <Button variant="outlined" onClick={() => { setEditingExperienceId(null); setNewExperience({ posisi: '', tempat: '', tahun: '', deskripsi: '' }); }}>
              Batal Edit
            </Button>
          )}
        </div>
        <ul className="space-y-3 mt-6">
          {experiences.map(exp => (
            <li key={exp.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
              <span className="text-gray-800">{exp.posisi} di {exp.tempat} ({exp.tahun})</span>
              <div className="flex space-x-2">
                <Button variant="outlined" size="small" onClick={() => startEditExperience(exp)}>Edit</Button>
                <Button variant="contained" color="error" size="small" onClick={() => deleteExperience(exp.id)}>Hapus</Button>
              </div>
            </li>
          ))}
        </ul>
      </Box>

      {/* Education Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Pendidikan
        </Typography>
        <div className="space-y-4">
          <TextField label="Jurusan" value={newEducation.jurusan} onChange={e => setNewEducation({ ...newEducation, jurusan: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Institusi" value={newEducation.institusi} onChange={e => setNewEducation({ ...newEducation, institusi: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Tahun" value={newEducation.tahun} onChange={e => setNewEducation({ ...newEducation, tahun: e.target.value })} fullWidth variant="outlined" />
          <Button variant="contained" onClick={editingEducationId ? updateEducation : addEducation}>
            {editingEducationId ? 'Perbarui Pendidikan' : 'Tambah Pendidikan'}
          </Button>
          {editingEducationId && (
            <Button variant="outlined" onClick={() => { setEditingEducationId(null); setNewEducation({ jurusan: '', institusi: '', tahun: '' }); }}>
              Batal Edit
            </Button>
          )}
        </div>
        <ul className="space-y-3 mt-6">
          {education.map(edu => (
            <li key={edu.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
              <span className="text-gray-800">{edu.jurusan} dari {edu.institusi} ({edu.tahun})</span>
              <div className="flex space-x-2">
                <Button variant="outlined" size="small" onClick={() => startEditEducation(edu)}>Edit</Button>
                <Button variant="contained" color="error" size="small" onClick={() => deleteEducation(edu.id)}>Hapus</Button>
              </div>
            </li>
          ))}
        </ul>
      </Box>

      {/* Activities Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Aktivitas
        </Typography>
        <div className="space-y-4">
          <TextField label="Judul Aktivitas" value={newActivity.title} onChange={e => setNewActivity({ ...newActivity, title: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Waktu/Tanggal" value={newActivity.time} onChange={e => setNewActivity({ ...newActivity, time: e.target.value })} fullWidth variant="outlined" />
          <TextField label="URL Gambar" value={newActivity.image} onChange={e => setNewActivity({ ...newActivity, image: e.target.value })} fullWidth variant="outlined" />
          <TextField label="Deskripsi" value={newActivity.deskripsi} onChange={e => setNewActivity({ ...newActivity, deskripsi: e.target.value })} fullWidth multiline rows={3} variant="outlined" />
          <Button variant="contained" onClick={editingActivityId ? updateActivity : addActivity}>
            {editingActivityId ? 'Perbarui Aktivitas' : 'Tambah Aktivitas'}
          </Button>
          {editingActivityId && (
            <Button variant="outlined" onClick={() => { setEditingActivityId(null); setNewActivity({ title: '', time: '', image: '', deskripsi: '' }); }}>
              Batal Edit
            </Button>
          )}
        </div>
        <ul className="space-y-3 mt-6">
          {activities.map(act => (
            <li key={act.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
              <div className="flex flex-col">
                <span className="font-medium text-gray-800">{act.title}</span>
                <span className="text-gray-500 text-sm">{act.time}</span>
                {act.image && <img src={act.image} alt={act.title} className="w-24 h-24 object-cover mt-1 rounded" />}
                <span className="text-gray-500 text-sm">{act.deskripsi}</span>
              </div>
              <div className="flex space-x-2">
                <Button variant="outlined" size="small" onClick={() => startEditActivity(act)}>Edit</Button>
                <Button variant="contained" color="error" size="small" onClick={() => deleteActivity(act.id)}>Hapus</Button>
              </div>
            </li>
          ))}
        </ul>
      </Box>

      {/* Article Management */}
      <Box className="bg-white p-6 rounded-lg shadow-md mb-8">
        <Typography variant="h5" component="h2" className="text-gray-800 font-semibold mb-4">
          Kelola Artikel
        </Typography>
        {showAddArticleForm && (
          <AddArticleForm
            articleToEdit={editingArticle}
            onSave={handleSaveArticle}
            onCancel={() => {
              setShowAddArticleForm(false);
              setEditingArticle(null);
            }}
          />
        )}
        {!showAddArticleForm && (
          <Button variant="contained" onClick={() => setShowAddArticleForm(true)} className="mb-4">
            Tambah Artikel Baru
          </Button>
        )}
        {articles.length === 0 ? (
          <Typography variant="body1" className="text-gray-600 mt-4">Belum ada artikel.</Typography>
        ) : (
          <ul className="space-y-3 mt-4">
            {articles.map(article => (
              <li key={article.id} className="p-3 border border-gray-200 rounded-md flex justify-between items-center bg-gray-50">
                <div className="flex flex-col">
                  <span className="font-medium text-gray-800">{article.title}</span>
                  <span className="text-gray-500 text-sm">Oleh {article.author}, {article.date}</span>
                </div>
                <div className="flex space-x-2">
                  <Button variant="outlined" size="small" onClick={() => startEditArticle(article)}>Edit</Button>
                  <Button variant="contained" color="error" size="small" onClick={() => deleteArticle(article.id)}>Hapus</Button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </Box>
    </div>
  );
}
