import React, { useState, useEffect } from 'react';

// Komponen halaman Home untuk menampilkan data Hero dan About Me, Skills, Projects, Experiences, Education, Activities
function Home() {
  const [heroText, setHeroText] = useState({ name: '', description: '' });
  const [aboutMe, setAboutMe] = useState({ deskripsi: '', gambar_url: '' });
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_BASE_URL = 'http://localhost:3001/api';

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [
          heroRes, aboutRes, skillsRes, projectsRes,
          experiencesRes, educationRes, activitiesRes
        ] = await Promise.all([
          fetch(`${API_BASE_URL}/hero`),
          fetch(`${API_BASE_URL}/about`),
          fetch(`${API_BASE_URL}/skills`),
          fetch(`${API_BASE_URL}/projects`),
          fetch(`${API_BASE_URL}/experiences`),
          fetch(`${API_BASE_URL}/education`),
          fetch(`${API_BASE_URL}/activities`)
        ]);

        const heroData = await heroRes.json();
        const aboutData = await aboutRes.json();
        const skillsData = await skillsRes.json();
        const projectsData = await projectsRes.json();
        const experiencesData = await experiencesRes.json();
        const educationData = await educationRes.json();
        const activitiesData = await activitiesRes.json();

        setHeroText(heroData || { name: '', description: '' });
        setAboutMe(aboutData || { deskripsi: '', gambar_url: '' });
        setSkills(skillsData || []);
        setProjects(projectsData || []);
        setExperiences(experiencesData || []);
        setEducation(educationData || []);
        setActivities(activitiesData || []);

      } catch (err) {
        setError(err.message);
        console.error("Failed to fetch home data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
        <p className="mt-4 text-lg">Memuat portofolio Anda yang luar biasa...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center text-red-600 bg-red-100 p-4 rounded-md">
        <h2 className="text-xl font-semibold mb-2">Error Memuat Data</h2>
        <p>Terjadi kesalahan saat memuat data portofolio: {error}. Harap pastikan server backend berjalan dengan benar.</p>
      </div>
    );
  }

  // Komponen Section yang bisa digunakan kembali
  const Section = ({ title, children, className = '', id }) => (
    <section id={id} className={`py-12 ${className}`}>
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-8 pb-2 border-b-4 border-indigo-500 inline-block px-4">
          {title}
        </h2>
        {children}
      </div>
    </section>
  );

  return (
    <div className="bg-gray-100 text-gray-800">
      {/* Hero Section */}
      <section className="min-h-[calc(100vh-64px)] flex flex-col items-center justify-center text-center px-4 py-16 bg-gradient-to-r from-indigo-600 to-blue-700 text-white shadow-xl">
        <h1 className="text-4xl md:text-5xl font-bold mb-4 animate-fade-in-down">
          Halo, saya <span className="text-yellow-300">{heroText.name || 'Nama Anda'}</span>!
        </h1>
        <p className="text-xl md:text-2xl opacity-90 animate-fade-in-up">
          {heroText.description || 'Seorang pengembang web yang bersemangat.'}
        </p>
        <div className="mt-8">
          <button className="bg-white text-indigo-700 font-semibold py-3 px-8 rounded-full shadow-lg hover:bg-gray-100 transition duration-300 ease-in-out transform hover:scale-105">
            Lihat Karya Saya
          </button>
        </div>
      </section>

      {/* About Me Section (Biodata Singkat) */}
      <Section title="Tentang Saya" className="bg-white" id="about-me-section">
        <div className="flex flex-col md:flex-row items-center justify-center space-y-8 md:space-y-0 md:space-x-12">
          <div className="flex-shrink-0">
            <img
              src={aboutMe.gambar_url || 'https://placehold.co/200x200/cccccc/333333?text=No+Image'}
              alt="About Me"
              className="w-48 h-48 md:w-64 md:h-64 rounded-full object-cover shadow-lg border-4 border-indigo-300"
              onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/200x200/cccccc/333333?text=No+Image'; }}
            />
          </div>
          <div className="text-center md:text-left max-w-xl">
            <p className="text-lg text-gray-700 leading-relaxed">
              {aboutMe.deskripsi || 'Belum ada deskripsi tentang saya.'}
            </p>
          </div>
        </div>
      </Section>

      {/* Skills Section */}
      <Section title="Keterampilan Saya" className="bg-gray-100" id="skills-section">
        {skills.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada keterampilan untuk ditampilkan.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {skills.map((skill) => (
              <div key={skill.id} className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
                <h3 className="text-xl font-semibold text-gray-800 mb-3">{skill.nama_skill}</h3>
                <div className="w-full bg-gray-200 rounded-full h-2.5">
                  <div
                    className="bg-indigo-600 h-2.5 rounded-full"
                    style={{ width: `${skill.level}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-500 mt-2">{skill.level}%</p>
              </div>
            ))}
          </div>
        )}
      </Section>

      {/* Projects Section */}
      <Section title="Proyek Unggulan" className="bg-white" id="projects-section">
        {projects.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada proyek untuk ditampilkan.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project) => (
              <div key={project.id} className="bg-white rounded-lg shadow-md overflow-hidden transform hover:scale-105 transition duration-300">
                {project.gambar && (
                  <img
                    src={project.gambar}
                    alt={project.nama_project}
                    className="w-full h-48 object-cover"
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/400x250/cccccc/333333?text=No+Image'; }}
                  />
                )}
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">{project.nama_project}</h3>
                  <p className="text-gray-600 text-sm mb-4 line-clamp-3">{project.deskripsi}</p>
                  {project.link && (
                    <a
                      href={project.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
                    >
                      Lihat Proyek
                      <svg className="ml-2 -mr-0.5 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                      </svg>
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </Section>

      {/* Experience Section */}
      <Section title="Pengalaman Kerja" className="bg-gray-100" id="experiences-section">
        {experiences.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada pengalaman kerja untuk ditampilkan.</p>
        ) : (
          <div className="space-y-6">
            {experiences.map((exp) => (
              <div key={exp.id} className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-xl font-semibold text-gray-800">{exp.posisi}</h3>
                <p className="text-indigo-600 font-medium">{exp.tempat}</p>
                <p className="text-gray-500 text-sm mb-2">{exp.tahun}</p>
                <p className="text-gray-700">{exp.deskripsi}</p>
              </div>
            ))}
          </div>
        )}
      </Section>

      {/* Education Section */}
      <Section title="Latar Belakang Pendidikan" className="bg-white" id="education-section">
        {education.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada data pendidikan untuk ditampilkan.</p>
        ) : (
          <div className="space-y-6">
            {education.map((edu) => (
              <div key={edu.id} className="bg-gray-50 p-6 rounded-lg shadow-sm border border-gray-200">
                <h3 className="text-xl font-semibold text-gray-800">{edu.jurusan}</h3>
                <p className="text-indigo-600 font-medium">{edu.institusi}</p>
                <p className="text-gray-500 text-sm">{edu.tahun}</p>
              </div>
            ))}
          </div>
        )}
      </Section>

      {/* Activities Section */}
      <Section title="Aktivitas & Prestasi" className="bg-gray-100" id="activities-section">
        {activities.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada aktivitas untuk ditampilkan.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {activities.map((act) => (
              <div key={act.id} className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
                {act.image && (
                  <img
                    src={act.image}
                    alt={act.title}
                    className="w-full h-32 object-cover mb-4 rounded-md"
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/300x128/cccccc/333333?text=No+Image'; }}
                  />
                )}
                <h3 className="text-xl font-semibold text-gray-800 mb-2">{act.title}</h3>
                <p className="text-gray-500 text-sm mb-3">{act.time}</p>
                <p className="text-gray-700 text-sm line-clamp-3">{act.deskripsi}</p>
              </div>
            ))}
          </div>
        )}
      </Section>


      {/* Footer */}
      <footer className="text-center py-8 bg-gray-800 text-gray-300">
        <div className="container mx-auto px-4">
          <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
          <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>
        </div>
      </footer>
    </div>
  );
}

export default Home;
