import React from 'react';
import SectionHeader from './SectionHeader';

// Komponen SkillsSection
const SkillsSection = ({ skills }) => {
  return (
    // Latar belakang diubah agar sama dengan Hero Section
    <section className="py-16 px-4 bg-gradient-to-r from-[#000080] to-[#0000FF] text-white">
      <SectionHeader
        title="Keterampilan Saya"
        subtitle="Berikut adalah beberapa keterampilan teknis yang saya kuasai."
        titleColor="text-white" // Warna judul diubah menjadi putih
        subtitleColor="text-indigo-200" // Warna subjudul diubah menjadi warna yang kontras
      />
      <div className="container mx-auto mt-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {skills.map((skill) => (
          <div key={skill.id} className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
            <h3 className="text-xl font-semibold text-gray-800 mb-3">{skill.nama_skill}</h3>
            <div className="w-full bg-gray-200 rounded-full h-2.5 dark:bg-gray-700">
              <div
                className="bg-indigo-600 h-2.5 rounded-full"
                style={{ width: `${skill.level}%` }}
              ></div>
            </div>
            <p className="text-gray-600 mt-2">{skill.level}%</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default SkillsSection;
