import React from 'react';
import SectionHeader from './SectionHeader'; // Import SectionHeader

// Komponen ExperiencesSection
const ExperiencesSection = ({ experiences }) => {
  // Menghapus logika auto-scroll karena layout sekarang vertikal
  // Tidak ada useRef atau useEffect yang terkait dengan scroll horizontal di sini

  return (
    <section id="experiences-section" className="py-12 bg-white text-gray-800">
      <div className="container mx-auto px-4">
        {/* Header untuk Bagian Pengalaman Kerja */}
        <SectionHeader
            title="Pengalaman Kerja"
            subtitle="Riwayat Pengalaman Kerja Saya"
            titleColor="text-gray-800"
            subtitleColor="text-gray-600"
        />

        {/* Kondisi jika tidak ada pengalaman */}
        {experiences.length === 0 ? (
          <p className="text-center text-gray-500 mt-8">Belum ada pengalaman kerja untuk ditampilkan.</p>
        ) : (
          <div className="relative py-8">
            {/* Garis tengah vertikal */}
            {/* Lebar garis 1px atau 2px, tinggi full. Left 50% untuk tengah */}
            <div className="absolute left-1/2 transform -translate-x-1/2 w-1 bg-indigo-500 h-full rounded-full"></div>

            {/* Container untuk kartu pengalaman yang disusun vertikal */}
            <div className="flex flex-col items-center">
              {experiences.map((exp, index) => (
                <div
                  key={exp.id}
                  // Menggunakan flex-row atau flex-row-reverse untuk posisi kiri/kanan
                  className={`flex w-full items-center mb-8 last:mb-0 ${index % 2 === 0 ? 'flex-row' : 'flex-row-reverse'}`}
                >
                  {/* Spacer kosong untuk membuat item berada di sisi yang berlawanan */}
                  {/* Ini akan menjadi kolom kiri atau kanan yang kosong tergantung index */}
                  <div className="flex-1 max-w-[calc(50%-2rem)] md:max-w-[calc(50%-1.5rem)]"></div>

                  {/* Lingkaran (dot) di tengah timeline */}
                  <div className="relative z-20">
                    {/* Dot berada di tengah antara dua kolom. border-white menyesuaikan background. */}
                    <div className="w-6 h-6 bg-indigo-500 rounded-full border-4 border-white transform -translate-x-1/2 absolute top-1/2 left-1/2 -translate-y-1/2"></div>
                  </div>

                  {/* Kartu Pengalaman */}
                  <div
                    className={`flex-1 p-6 rounded-lg shadow-xl bg-white relative z-10
                      transform transition-transform duration-300 hover:scale-105
                      // Margin dan teks alignment disesuaikan berdasarkan posisi kiri/kanan
                      ${index % 2 === 0 ? 'ml-4 text-left' : 'mr-4 text-right'}
                      max-w-[calc(50%-2rem)] md:max-w-[calc(50%-1.5rem)] // Memastikan lebar fixed untuk kartu
                      min-h-[12rem] // Minimum tinggi untuk kartu
                    `}
                  >
                    {/* Konten Kartu Pengalaman */}
                    <h3 className="text-2xl font-bold text-gray-800 mb-1">{exp.posisi}</h3>
                    <p className="text-xl font-medium text-indigo-600 mb-1">{exp.tempat}</p>
                    <p className="text-gray-500 text-base mb-3">{exp.tahun}</p>
                    {/* Deskripsi dengan overflow-hidden dan line-clamp untuk mengelola teks panjang */}
                    <p className="text-gray-700 text-sm line-clamp-4 overflow-hidden">
                      {exp.deskripsi}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default ExperiencesSection;
