import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';

// Import komponen SectionHeader
import SectionHeader from './SectionHeader';

// Import komponen-komponen section lainnya
import ExperiencesSection from './ExperiencesSection';
import EducationSection from './EducationSection';
import ActivitiesSection from './ActivitiesSection';
import SkillsSection from './SkillsSection';
import ProjectsSection from './ProjectsSection';
import HobbiesSection from './HobbiesSection'; // Import komponen HobbiesSection

// Komponen halaman Home untuk menampilkan data Hero dan About Me, Skills, Projects, Experiences, Education, Activities, Articles
function Home() {
  const [heroText, setHeroText] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [aboutMe, setAboutMe] = useState({ deskripsi: '', gambar_url: '' });
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  // const [articles, setArticles] = useState([]); // Menghapus state untuk articles
  const [hobbies, setHobbies] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State untuk melacak proyek yang sedang aktif/di tengah
  const [activeProjectIndex, setActiveProjectIndex] = useState(0);

  const API_BASE_URL = 'http://localhost:3001/api';

  // Ref untuk kontainer scroll proyek, aktivitas, pengalaman kerja, dan artikel
  const projectsRef = useRef(null);
  const scrollProjectsIntervalRef = useRef(null);
  const experiencesRef = useRef(null); // Ref baru untuk experiences
  const scrollExperiencesIntervalRef = useRef(null); // Ref baru untuk auto-scroll experiences
  const activitiesRef = useRef(null); // Ref untuk activities
  const scrollActivitiesIntervalRef = useRef(null); // Ref untuk auto-scroll activities
  // const articlesRef = useRef(null); // Menghapus ref untuk articles
  // const scrollArticlesIntervalRef = useRef(null); // Menghapus ref untuk auto-scroll articles


  // Fungsi untuk fetch data dari API
  const fetchData = useCallback(async () => {
    try {
      // Menggunakan Promise.all untuk mengambil semua data secara paralel
      const [
        heroRes,
        aboutMeRes,
        skillsRes,
        projectsRes,
        experiencesRes,
        educationRes,
        activitiesRes,
        // articlesRes, // Menghapus fetching articles
        hobbiesRes,
      ] = await Promise.all([
        axios.get(`${API_BASE_URL}/hero_texts`),
        axios.get(`${API_BASE_URL}/about_me`),
        axios.get(`${API_BASE_URL}/skills`),
        axios.get(`${API_BASE_URL}/projects`),
        axios.get(`${API_BASE_URL}/experiences`),
        axios.get(`${API_BASE_URL}/education`),
        axios.get(`${API_BASE_URL}/activities`),
        // axios.get(`${API_BASE_URL}/articles`), // Menghapus fetching articles
        axios.get(`${API_BASE_URL}/hobbies`),
      ]);

      setHeroText(heroRes.data[0] || { name: '', role_text: '', main_title: '', year_text: '' });
      setAboutMe(aboutMeRes.data[0] || { deskripsi: '', gambar_url: '' });
      setSkills(skillsRes.data || []);
      setProjects(projectsRes.data || []);
      setExperiences(experiencesRes.data || []);
      setEducation(educationRes.data || []);
      setActivities(activitiesRes.data || []);
      // setArticles(articlesRes.data || []); // Menghapus set state articles
      setHobbies(hobbiesRes.data || []);

    } catch (err) {
      setError('Gagal memuat data. Silakan coba lagi nanti.');
      console.error("Error fetching data:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // Fungsi untuk menggeser secara otomatis
  const startAutoScroll = useCallback((ref, intervalRef, speed = 100) => {
    if (ref.current) {
      // Pastikan interval sebelumnya dibersihkan
      clearInterval(intervalRef.current);

      intervalRef.current = setInterval(() => {
        if (ref.current.scrollLeft + ref.current.clientWidth >= ref.current.scrollWidth) {
          ref.current.scrollLeft = 0; // Kembali ke awal jika sudah mencapai akhir
        } else {
          ref.current.scrollLeft += 1; // Geser 1 piksel
        }
      }, speed); // Kecepatan scroll
    }
  }, []);

  const stopAutoScroll = useCallback((intervalRef) => {
    clearInterval(intervalRef.current);
  }, []);

  // Effect untuk auto-scroll projects
  useEffect(() => {
    if (projects.length > 0) {
      startAutoScroll(projectsRef, scrollProjectsIntervalRef);
    }
    return () => stopAutoScroll(scrollProjectsIntervalRef);
  }, [projects, startAutoScroll, stopAutoScroll]);

  // Effect untuk auto-scroll experiences
  useEffect(() => {
    if (experiences.length > 0) {
      startAutoScroll(experiencesRef, scrollExperiencesIntervalRef);
    }
    return () => stopAutoScroll(scrollExperiencesIntervalRef);
  }, [experiences, startAutoScroll, stopAutoScroll]);

  // Effect untuk auto-scroll activities
  useEffect(() => {
    if (activities.length > 0) {
      startAutoScroll(activitiesRef, scrollActivitiesIntervalRef);
    }
    return () => stopAutoScroll(scrollActivitiesIntervalRef);
  }, [activities, startAutoScroll, stopAutoScroll]);

  // Effect untuk auto-scroll articles (Dihapus)
  // useEffect(() => {
  //   if (articles.length > 0) {
  //     startAutoScroll(articlesRef, scrollArticlesIntervalRef);
  //   }
  //   return () => stopAutoScroll(scrollArticlesIntervalRef);
  // }, [articles, startAutoScroll, stopAutoScroll]);

  // Penanganan klik pada proyek untuk mengaktifkannya
  const handleProjectClick = (index) => {
    setActiveProjectIndex(index);
    if (projectsRef.current) {
      // Gulir ke proyek yang diklik agar terlihat di tengah
      const projectElement = projectsRef.current.children[index];
      if (projectElement) {
        const offset = projectElement.offsetLeft - (projectsRef.current.clientWidth / 2) + (projectElement.clientWidth / 2);
        projectsRef.current.scrollTo({ left: offset, behavior: 'smooth' });
      }
    }
  };

  // Loading dan Error State
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen bg-gray-900 text-white">
        <div className="text-xl">Memuat data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-screen bg-gray-900 text-red-500">
        <div className="text-xl">{error}</div>
      </div>
    );
  }

  // Social Media Links (data dummy)
  const socialMediaLinks = [
    { name: 'GitHub', url: 'https://github.com/yourusername', iconClass: 'fab fa-github' },
    { name: 'LinkedIn', url: 'https://www.linkedin.com/in/yourusername', iconClass: 'fab fa-linkedin' },
    { name: 'Twitter', url: 'https://twitter.com/yourusername', iconClass: 'fab fa-twitter' },
    { name: 'Instagram', url: 'https://instagram.com/yourusername', iconClass: 'fab fa-instagram' },
  ];

  return (
    <div className="font-inter antialiased text-gray-900">
      {/* Header */}
      <header className="fixed w-full top-0 z-50 bg-gray-800 bg-opacity-90 shadow-md">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <div className="text-white text-2xl font-bold">
            {heroText.name || 'Nama Anda'}
          </div>
          <nav>
            <ul className="flex space-x-6">
              <li><a href="#hero-section" className="text-gray-300 hover:text-white transition-colors duration-300">Beranda</a></li>
              <li><a href="#about-me-section" className="text-gray-300 hover:text-white transition-colors duration-300">Tentang Saya</a></li>
              <li><a href="#skills-section" className="text-gray-300 hover:text-white transition-colors duration-300">Keahlian</a></li>
              <li><a href="#projects-section" className="text-gray-300 hover:text-white transition-colors duration-300">Proyek</a></li>
              <li><a href="#experiences-section" className="text-gray-300 hover:text-white transition-colors duration-300">Pengalaman</a></li>
              <li><a href="#education-section" className="text-gray-300 hover:text-white transition-colors duration-300">Pendidikan</a></li>
              <li><a href="#activities-section" className="text-gray-300 hover:text-white transition-colors duration-300">Aktivitas</a></li>
              {/* <li><a href="#articles-section" className="text-gray-300 hover:text-white transition-colors duration-300">Artikel</a></li> */}
              <li><a href="#hobbies-section" className="text-gray-300 hover:text-white transition-colors duration-300">Hobi</a></li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section id="hero-section" className="relative h-screen bg-gradient-to-r from-blue-700 to-indigo-900 text-white flex flex-col justify-center items-center text-center p-4">
        <div className="absolute inset-0 z-0 opacity-20">
          {/* Tambahkan latar belakang pola atau gambar jika diinginkan */}
        </div>
        <div className="relative z-10 max-w-4xl mx-auto">
          <p className="text-lg md:text-xl mb-3 animate-fade-in-up">{heroText.role_text || 'Pengembang Perangkat Lunak'}</p>
          <h1 className="text-4xl md:text-6xl font-extrabold leading-tight mb-4 animate-fade-in-up delay-100">
            {heroText.main_title || 'Membangun Masa Depan dengan Kode.'}
          </h1>
          <p className="text-2xl md:text-3xl font-semibold mb-6 animate-fade-in-up delay-200">
            Saya <span className="text-yellow-300">{heroText.name || 'Nama Anda'}</span>, {heroText.year_text || 'dengan pengalaman 5 tahun'}.
          </p>
          <a
            href="#about-me-section"
            className="inline-block bg-white text-blue-700 hover:bg-blue-100 px-8 py-3 rounded-full text-lg font-semibold shadow-lg transition-all duration-300 transform hover:scale-105 animate-fade-in-up delay-300"
          >
            Pelajari Lebih Lanjut
          </a>
        </div>
      </section>

      {/* About Me Section */}
      <section id="about-me-section" className="py-16 bg-gray-100 text-gray-800">
        <div className="container mx-auto px-4 flex flex-col md:flex-row items-center gap-8">
          <div className="md:w-1/3 flex justify-center">
            {aboutMe.gambar_url ? (
              <img
                src={aboutMe.gambar_url}
                alt="Tentang Saya"
                className="rounded-full w-48 h-48 md:w-64 md:h-64 object-cover shadow-lg border-4 border-white"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "https://placehold.co/256x256/E0E0E0/333333?text=Gambar+Profil";
                }}
              />
            ) : (
              <img
                src="https://placehold.co/256x256/E0E0E0/333333?text=Gambar+Profil"
                alt="Placeholder"
                className="rounded-full w-48 h-48 md:w-64 md:h-64 object-cover shadow-lg border-4 border-white"
              />
            )}
          </div>
          <div className="md:w-2/3 text-center md:text-left">
            <SectionHeader
                title="Tentang Saya"
                subtitle="Siapa Saya?"
                titleColor="text-gray-800"
                subtitleColor="text-gray-600"
                align="text-center md:text-left" // Mengatur align ke kiri untuk md
            />
            <p className="text-lg leading-relaxed text-gray-700 mt-4 md:mt-0">
              {aboutMe.deskripsi || 'Belum ada deskripsi tentang saya.'}
            </p>
          </div>
        </div>
      </section>

      {/* Skills Section */}
      <SkillsSection skills={skills} />

      {/* Projects Section */}
      <ProjectsSection
        projects={projects}
        projectsRef={projectsRef}
        activeProjectIndex={activeProjectIndex}
        handleProjectClick={handleProjectClick}
        stopAutoScroll={() => stopAutoScroll(scrollProjectsIntervalRef)}
        startAutoScroll={() => startAutoScroll(projectsRef, scrollProjectsIntervalRef)}
      />

      {/* Experiences Section */}
      <ExperiencesSection experiences={experiences} />

      {/* Education Section */}
      <EducationSection education={education} />

      {/* Activities Section */}
      <ActivitiesSection
        activities={activities}
        activitiesRef={activitiesRef}
        stopAutoScroll={() => stopAutoScroll(scrollActivitiesIntervalRef)}
        startAutoScroll={() => startAutoScroll(activitiesRef, scrollActivitiesIntervalRef)}
      />

      {/* Articles Section (Dihapus dari sini) */}
      {/* <section id="articles-section" className="py-12 bg-gray-100 text-gray-800">
        <div className="container mx-auto px-4">
          <SectionHeader
            title="Artikel Terbaru"
            subtitle="Baca Pemikiran Saya"
            titleColor="text-gray-800"
            subtitleColor="text-gray-600"
          />

          {articles.length === 0 ? (
            <p className="text-center text-gray-500 mt-8">Belum ada artikel untuk ditampilkan.</p>
          ) : (
            <div
              ref={articlesRef}
              onMouseEnter={() => stopAutoScroll(scrollArticlesIntervalRef)}
              onMouseLeave={() => startAutoScroll(articlesRef, scrollArticlesIntervalRef)}
              className="flex overflow-x-auto space-x-6 p-4 hide-scrollbar"
            >
              {articles.map((article) => (
                <div key={article.id} className="flex-none w-80 bg-white rounded-lg shadow-lg overflow-hidden border border-gray-200 hover:shadow-xl transition-shadow duration-300">
                  <div className="p-5">
                    <h3 className="text-xl font-bold text-gray-900 mb-2 truncate">{article.title}</h3>
                    <p className="text-gray-600 text-sm mb-4 line-clamp-3">{article.summary}</p>
                    <a
                      href={article.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-600 hover:text-indigo-800 font-semibold flex items-center"
                    >
                      Baca Selengkapnya
                      <svg className="ml-2 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7m0 0l-7 7m7-7H3"></path>
                      </svg>
                    </a>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section> */}

      {/* Hobbies Section */}
      <HobbiesSection hobbies={hobbies} />


      {/* Footer */}
      <footer className="text-center py-8 bg-gray-800 text-gray-300">
        <div className="container mx-auto px-4">
          <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
          <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>

          {/* Social Media Links */}
          <div className="flex justify-center space-x-6 mt-4">
            {socialMediaLinks.map((link) => (
              <a
                key={link.name}
                href={link.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl"
                aria-label={link.name}
              >
                {/* Menggunakan ikon Font Awesome jika CDN diimpor, jika tidak, tampilkan nama */}
                <i className={link.iconClass} title={link.name}></i>
                {/* Fallback jika Font Awesome tidak dimuat: */}
                {!document.querySelector(`.${link.iconClass.split(' ').join('.')}`) && (
                  <span className="sr-only">{link.name}</span>
                )}
              </a>
            ))}
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Home;
