import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';

// Komponen halaman Home untuk menampilkan data Hero dan About Me, Skills, Projects, Experiences, Education, Activities, Articles
function Home() {
  const [heroText, setHeroText] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [aboutMe, setAboutMe] = useState({ deskripsi: '', gambar_url: '' });
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  const [articles, setArticles] = useState([]);
  const [hobbies, setHobbies] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State untuk melacak proyek yang sedang aktif/di tengah
  const [activeProjectIndex, setActiveProjectIndex] = useState(0);

  const API_BASE_URL = 'http://localhost:3001/api';

  // Ref untuk kontainer scroll proyek, aktivitas, pengalaman kerja, dan artikel
  const projectsRef = useRef(null);
  const scrollProjectsIntervalRef = useRef(null);
  const experiencesRef = useRef(null); // Ref baru untuk experiences
  const scrollExperiencesIntervalRef = useRef(null); // Ref baru untuk auto-scroll experiences
  const activitiesRef = useRef(null);
  const scrollActivitiesIntervalRef = useRef(null);
  const articlesRef = useRef(null);
  const scrollArticlesIntervalRef = useRef(null);

  // Data sosial media (Anda bisa mengubah ini atau mengambil dari backend jika diperlukan)
  const socialMediaLinks = [
    { name: 'LinkedIn', url: 'https://www.linkedin.com/in/anishka-putri/', iconClass: 'fab fa-linkedin' },
    { name: 'Instagram', url: 'https://www.instagram.com/anishka_pr/', iconClass: 'fab fa-instagram' },
    { name: 'GitHub', url: 'https://github.com/anishkaputri', iconClass: 'fab fa-github' },
    // Tambahkan lebih banyak jika diperlukan
  ];

  // Helper function to fetch data and parse JSON, with better error handling
  const fetchJson = async (url, errorMessage) => {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        let errorDetails = `HTTP error! status: ${response.status}`;
        try {
          const errorData = await response.json();
          if (errorData && errorData.message) {
            errorDetails += `: ${errorData.message}`;
          } else if (errorData) {
            errorDetails += `: ${JSON.stringify(errorData)}`;
          }
        } catch (parseError) {
          errorDetails += `: Failed to parse error response.`;
        }
        throw new Error(`${errorMessage} ${errorDetails}`);
      }
      return response.json();
    } catch (err) {
      console.error(`Error fetching ${url}:`, err);
      throw new Error(err.message.includes(errorMessage) ? err.message : `${errorMessage}: ${err.message}`);
    }
  };

  // Mengambil semua data saat komponen dimuat
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        const [
          heroData, aboutData, skillsData, projectsData,
          experiencesData, educationData, activitiesData,
          articlesData, hobbiesData
        ] = await Promise.all([
          axios.get(`${API_BASE_URL}/hero`),
          axios.get(`${API_BASE_URL}/about`),
          axios.get(`${API_BASE_URL}/skills`),
          axios.get(`${API_BASE_URL}/projects`),
          axios.get(`${API_BASE_URL}/experiences`),
          axios.get(`${API_BASE_URL}/education`),
          axios.get(`${API_BASE_URL}/activities`),
          axios.get(`${API_BASE_URL}/articles`),
          axios.get(`${API_BASE_URL}/hobbies`)
        ]);

        setHeroText(heroData.data || { name: '', role_text: '', main_title: '', year_text: '' });
        setAboutMe(aboutData.data || { deskripsi: '', gambar_url: '' });
        setSkills(skillsData.data || []);
        setProjects(projectsData.data || []);
        setExperiences(experiencesData.data || []);
        setEducation(educationData.data || []);
        setActivities(activitiesData.data || []);
        setArticles(articlesData.data || []);
        setHobbies(hobbiesData.data || []);

        if (projectsData.data && projectsData.data.length > 0) {
          // Set proyek pertama sebagai aktif saat load, dan scroll ke sana
          setActiveProjectIndex(0);
          // Beri waktu DOM untuk merender sebelum mencoba scroll
          setTimeout(() => {
              const container = projectsRef.current;
              if (container && container.children[0]) {
                  const targetElement = container.children[0];
                  // Hitung scroll target untuk memusatkan elemen pertama
                  const scrollTarget = targetElement.offsetLeft - (container.clientWidth / 2) + (targetElement.offsetWidth / 2);
                  container.scrollTo({ left: scrollTarget, behavior: 'smooth' });
              }
          }, 100); // Penundaan kecil untuk memastikan elemen dirender
        }

      } catch (err) {
        setError(err.message || "Terjadi kesalahan saat memuat data.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // --- LOGIKA UNTUK DETEKSI ITEM TENGAH PADA PROYEK (untuk update activeProjectIndex saat scroll manual) ---
  const handleProjectScroll = useCallback(() => {
    const container = projectsRef.current;
    if (!container || projects.length === 0) return;

    // Hitung posisi tengah container
    const viewportCenter = container.scrollLeft + container.clientWidth / 2;

    let closestProjectIndex = 0;
    let minDistance = Infinity;

    // Loop melalui setiap item anak untuk menemukan yang paling dekat dengan tengah
    Array.from(container.children).forEach((child, index) => {
      // Pastikan child adalah elemen HTML yang valid
      if (child.offsetWidth > 0) { // Cek jika elemen memiliki lebar (sudah dirender)
        const childCenter = child.offsetLeft + child.offsetWidth / 2; // Gunakan offsetWidth
        const distance = Math.abs(viewportCenter - childCenter);

        if (distance < minDistance) {
          minDistance = distance;
          closestProjectIndex = index;
        }
      }
    });
    setActiveProjectIndex(closestProjectIndex);
  }, [projects]);

  // --- LOGIKA UNTUK SCROLL PROGRAMATIK KE PROYEK TERTENTU (memusatkan item) ---
  const scrollToProject = useCallback((index) => {
    const container = projectsRef.current;
    if (!container || !container.children[index]) return;

    const targetElement = container.children[index];
    // Hitung offset agar elemen target berada di tengah viewport
    const scrollTarget = targetElement.offsetLeft - (container.clientWidth / 2) + (targetElement.offsetWidth / 2); // Gunakan offsetWidth

    // Hentikan auto-scroll sementara saat user berinteraksi
    if (scrollProjectsIntervalRef.current) {
        clearInterval(scrollProjectsIntervalRef.current);
        scrollProjectsIntervalRef.current = null; // Set to null after clearing
    }

    container.scrollTo({
      left: scrollTarget,
      behavior: 'smooth'
    });

    // Mulai lagi auto-scroll setelah beberapa detik (jika tidak ada interaksi lagi)
    setTimeout(() => {
        // Hanya mulai interval baru jika tidak ada interval yang sedang berjalan dari interaksi lain
        if (!scrollProjectsIntervalRef.current) { // Cek lagi sebelum memulai
            scrollProjectsIntervalRef.current = setInterval(() => {
                setActiveProjectIndex(prevIndex => {
                    const nextIndex = (prevIndex + 1) % projects.length;
                    scrollToProject(nextIndex); // Rekursif panggil fungsi scrollToProject
                    return nextIndex; // Update state untuk UI dots
                });
            }, 5000); // Auto-scroll setiap 5 detik
        }
    }, 1500); // Tunggu 1.5 detik setelah scroll manual selesai baru mulai auto-scroll lagi

    setActiveProjectIndex(index);
}, [projects]);

  // Efek untuk mengelola auto-scroll dan event listener scroll pada proyek
  useEffect(() => {
    const container = projectsRef.current;
    if (!container || projects.length === 0) return;

    // Mulai auto-scroll saat pertama kali dimuat jika belum dimulai
    // Ini akan dipicu hanya jika data projects tersedia dan setInterval belum ada
    if (!scrollProjectsIntervalRef.current) {
        scrollProjectsIntervalRef.current = setInterval(() => {
            setActiveProjectIndex(prevIndex => {
                const nextIndex = (prevIndex + 1) % projects.length;
                scrollToProject(nextIndex); // Panggil scrollToProject untuk memastikan smooth scroll ke tengah
                return nextIndex;
            });
        }, 5000); // Auto-scroll setiap 5 detik (sangat pelan)
    }

    // Tambahkan event listener untuk deteksi scroll manual
    container.addEventListener('scroll', handleProjectScroll);

    // Cleanup function untuk menghapus event listener dan interval saat komponen unmount
    return () => {
      if (scrollProjectsIntervalRef.current) {
        clearInterval(scrollProjectsIntervalRef.current);
      }
      container.removeEventListener('scroll', handleProjectScroll);
    };
  }, [projects, handleProjectScroll, scrollToProject]);

  // --- LOGIKA UNTUK AUTO-SCROLL HORIZONTAL PADA PENGALAMAN KERJA ---
  useEffect(() => {
    const container = experiencesRef.current;
    if (!container || experiences.length === 0) return;

    if (scrollExperiencesIntervalRef.current) {
      clearInterval(scrollExperiencesIntervalRef.current);
    }

    const scrollRight = () => {
      const itemWidth = container.children[0]?.offsetWidth + 24; // Lebar item + gap (space-x-6 = 24px)
      const currentScroll = container.scrollLeft;
      const maxScroll = container.scrollWidth - container.clientWidth;

      let targetScroll = currentScroll + itemWidth;

      if (targetScroll >= maxScroll) {
        targetScroll = 0; // Kembali ke awal jika sudah di akhir
      }

      container.scrollTo({ left: targetScroll, behavior: 'smooth' });
    };

    scrollExperiencesIntervalRef.current = setInterval(scrollRight, 3000); // Auto-scroll setiap 3 detik

    return () => {
      if (scrollExperiencesIntervalRef.current) {
        clearInterval(scrollExperiencesIntervalRef.current);
      }
    };
  }, [experiences]); // Dependensi: experiences


  // --- LOGIKA UNTUK AUTO-SCROLL HORIZONTAL PADA AKTIVITAS ---
  useEffect(() => {
    const container = activitiesRef.current;
    if (!container || activities.length === 0) return;

    if (scrollActivitiesIntervalRef.current) {
      clearInterval(scrollActivitiesIntervalRef.current);
    }

    const scrollRight = () => {
      // Gulir satu item penuh
      const itemWidth = container.children[0]?.offsetWidth + 24; // Lebar item + gap (space-x-6 = 24px)
      const currentScroll = container.scrollLeft;
      const maxScroll = container.scrollWidth - container.clientWidth;

      let targetScroll = currentScroll + itemWidth;

      // Jika sudah di akhir, kembali ke awal
      if (targetScroll >= maxScroll) {
        targetScroll = 0;
      }

      container.scrollTo({ left: targetScroll, behavior: 'smooth' });
    };

    scrollActivitiesIntervalRef.current = setInterval(scrollRight, 3000); // Auto-scroll setiap 3 detik

    return () => {
      if (scrollActivitiesIntervalRef.current) {
        clearInterval(scrollActivitiesIntervalRef.current);
      }
    };
  }, [activities]); // Dependensi: activities

  // --- LOGIKA UNTUK AUTO-SCROLL HORIZONTAL PADA ARTIKEL ---
  useEffect(() => {
    const container = articlesRef.current;
    if (!container || articles.length === 0) return;

    if (scrollArticlesIntervalRef.current) {
      clearInterval(scrollArticlesIntervalRef.current);
    }

    const scrollRight = () => {
      // Gulir satu item penuh
      const itemWidth = container.children[0]?.offsetWidth + 24; // Lebar item + gap (space-x-6 = 24px)
      const currentScroll = container.scrollLeft;
      const maxScroll = container.scrollWidth - container.clientWidth;

      let targetScroll = currentScroll + itemWidth;

      // Jika sudah di akhir, kembali ke awal
      if (targetScroll >= maxScroll) {
        targetScroll = 0;
      }

      container.scrollTo({ left: targetScroll, behavior: 'smooth' });
    };

    scrollArticlesIntervalRef.current = setInterval(scrollRight, 3000); // Auto-scroll setiap 3 detik

    return () => {
      if (scrollArticlesIntervalRef.current) {
        clearInterval(scrollArticlesIntervalRef.current);
      }
    };
  }, [articles]);


  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
        <p className="mt-4 text-lg">Memuat portofolio Anda yang luar biasa...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center text-red-600 bg-red-100 p-4 rounded-md mx-4 my-8">
        <h2 className="text-xl font-semibold mb-2">Error Memuat Data</h2>
        <p>Terjadi kesalahan saat memuat data portofolio: {error}. Harap pastikan server backend berjalan dengan benar.</p>
        <p className="mt-2 text-sm">Coba periksa konsol browser dan terminal backend untuk detail lebih lanjut.</p>
      </div>
    );
  }

  // Komponen SectionHeader baru untuk judul dan subjudul seragam
  const SectionHeader = ({ title, subtitle, titleColor = 'text-gray-800', subtitleColor = 'text-gray-600', linkText, linkHref, onLinkClick, linkColor = 'text-indigo-600 hover:text-indigo-800' }) => (
    <div className="flex flex-col items-center justify-center mb-10 text-center w-full">
        <h2 className={`text-3xl font-bold mb-2 ${titleColor}`}>
            {title}
        </h2>
        {subtitle && (
            <p className={`text-xl ${subtitleColor}`}>
                {subtitle}
                {linkText && (
                    <a
                        href={linkHref || '#'}
                        onClick={onLinkClick || ((e) => e.preventDefault())}
                        className={`${linkColor} ml-2`}
                    >
                        {linkText}
                    </a>
                )}
            </p>
        )}
    </div>
  );

  // Komponen Section yang bisa digunakan kembali (disederhanakan)
  const Section = ({ children, className = '', id }) => (
    <section id={id} className={`py-12 ${className}`}>
      <div className="container mx-auto px-4">
        {children}
      </div>
    </section>
  );

  return (
    <div className="bg-gray-100 text-gray-800 font-inter"> {/* Menambahkan font-inter class */}
      {/* Hero Section - Redesign (Diperbarui dengan data dinamis) */}
      <section className="relative w-full h-[600px] bg-gradient-to-r from-[#000080] to-[#0000FF] overflow-hidden flex flex-col justify-between p-8 text-white shadow-xl">
        {/* Background Waves/Shapes (SVG atau Pseudo-elements untuk kompleksitas) */}
        <div className="absolute inset-0 z-0 opacity-70">
          <svg className="w-full h-full" viewBox="0 0 1440 600" preserveAspectRatio="none">
            <defs>
              <linearGradient id="gradient-hero" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" stopColor="#000080" /> {/* Dark Blue */}
                <stop offset="100%" stopColor="#0000FF" /> {/* Bright Blue */}
              </linearGradient>
            </defs>
            {/* Contoh bentuk gelombang sederhana, bisa lebih kompleks dengan path data */}
            <path fill="url(#gradient-hero)" d="M0,0 L0,150 C200,200 400,100 600,150 C800,200 1000,100 1200,150 L1440,200 L1440,0 Z"></path>
            <path fill="url(#gradient-hero)" opacity="0.6" d="M0,0 L0,200 C250,250 500,150 750,200 C1000,250 1250,150 1440,200 L1440,0 Z"></path>
          </svg>
        </div>
        
        <div className="relative z-10 flex flex-col items-center text-center pt-16">
          <p className="text-lg md:text-xl font-light mb-2">{heroText.role_text || 'Graphic Designing'}</p> {/* Data dinamis */}
          <h1 className="text-5xl md:text-7xl font-extrabold tracking-tight mb-4">
            {heroText.main_title || 'PORTFOLIO'}<span className="text-2xl align-super ml-2">{heroText.year_text || '2023'}</span> {/* Data dinamis */}
          </h1>
        </div>

        <div className="relative z-10 text-left pb-8 pl-8">
          <p className="text-2xl font-semibold">{heroText.name || 'Anishka'}</p> {/* Data dinamis */}
        </div>
      </section>

      {/* About Me Section - Refined Layout for Image, Description, and Hobbies */}
      <section className="bg-gray-100 py-16 px-4">
        <div className="container mx-auto flex flex-col md:flex-row items-center md:items-start md:space-x-12">
          {/* Left Column: Profile Picture */}
          <div className="flex-shrink-0 relative w-64 h-64 md:w-80 md:h-80 -mt-20 md:mt-0 mb-8 md:mb-0
                      border-8 border-white bg-gray-200 overflow-hidden shadow-xl"
               style={{
                 borderRadius: '50% 50% 10% 50% / 50% 50% 50% 10%',
               }}>
            <img
              src={aboutMe.gambar_url || 'https://placehold.co/300x300/cccccc/333333?text=Profile'}
              alt="Profile"
              className="absolute w-[110%] h-[110%] object-cover object-center top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2"
              onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/300x300/cccccc/333333?text=Profile'; }}
            />
          </div>

          {/* Right Column: Description and Hobbies */}
          <div className="flex-1 text-center md:text-left">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-800 mb-6">Hi! I'm {heroText.name || 'Anishka'}</h2>
            <p className="text-lg text-gray-700 leading-relaxed mb-6">
              {aboutMe.deskripsi || "I'm a graphic designer with a passion for making things look good. I'm also pretty funny, so if you're looking for someone to design your next marketing campaign, you've come to the right place. Just don't expect me to take myself too seriously."}
            </p>
            <p className="text-lg text-gray-700 leading-relaxed mb-8">
              I am a creative and innovative thinker with a strong attention to detail. I am always looking for new ways to improve my skills and knowledge.
            </p>

            {/* Hobbies Section - Mengambil data dari backend */}
            <div className="border-t border-b border-gray-300 py-4 mb-4">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">Hobbies</h3>
              {hobbies.length === 0 ? (
                <p className="text-gray-500">Belum ada hobi yang ditambahkan.</p>
              ) : (
                <div className="text-base text-gray-600 flex flex-wrap gap-x-2 items-center">
                  {hobbies.map((hobby, index) => (
                    <React.Fragment key={hobby.id}>
                      <span className={hobby.is_bold ? 'font-bold' : ''}>{hobby.hobby_name}</span>
                      {index < hobbies.length - 1 && (
                        <span className="text-gray-400 mx-1">|</span>
                      )}
                    </React.Fragment>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </section>

      {/* Skills Section - Layout Disesuaikan */}
      <section id="skills-section" className="py-12 bg-gray-800 text-white flex flex-col items-center justify-center">
        <SectionHeader
            title="01 PROFESSIONAL"
            subtitle="MY KNOWLEDGE LEVEL IN SOFTWARE"
            titleColor="text-white"
            subtitleColor="text-gray-300"
        />

        {skills.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada keterampilan untuk ditampilkan.</p>
        ) : (
          <div className="w-full max-w-2xl px-4"> {/* Pembatas lebar untuk konten skills */}
            {skills.map((skill) => (
              <div key={skill.id} className="mb-6">
                <div className="flex justify-between items-center mb-1">
                  <h3 className="text-lg font-medium text-gray-200 uppercase">{skill.nama_skill}</h3>
                  <span className="text-sm font-semibold text-gray-200">{skill.level}%</span>
                </div>
                <div className="w-full bg-gray-600 rounded-full h-3"> {/* Warna background progress bar disesuaikan */}
                  <div
                    className="bg-green-400 h-3 rounded-full" /* Warna progress bar disesuaikan */
                    style={{ width: `${skill.level}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Projects Section - Focus Carousel with External Title/Description */}
      <section id="projects-section" className="py-12 bg-gray-700 text-white flex flex-col items-center justify-center relative">
        <SectionHeader
            title="02 PORTFOLIO"
            subtitle="MY LATEST WORK."
            linkText="SEE MORE >"
            onLinkClick={() => window.alert('Melihat semua proyek...')}
            titleColor="text-white"
            subtitleColor="text-gray-300"
            linkColor="text-indigo-400 hover:text-indigo-200"
        />

        {projects.length === 0 ? (
          <p className="text-center text-gray-400">Belum ada proyek untuk ditampilkan.</p>
        ) : (
          <>
            {/* Carousel Container */}
            <div
              ref={projectsRef}
              className="flex overflow-x-scroll snap-x snap-mandatory hide-scrollbar
                         w-full px-4 md:px-8 lg:px-16 xl:px-24 py-4 space-x-6 items-center"
              style={{
                // Menyesuaikan padding agar item di pinggir terlihat sebagian dan snap ke tengah
                scrollPaddingLeft: 'calc(50% - 150px)', // 50% dari container - setengah lebar item tengah (300/2)
                scrollPaddingRight: 'calc(50% - 150px)',
              }}
            >
              {projects.map((project, index) => {
                const isActive = index === activeProjectIndex;
                const widthClass = isActive
                  ? 'w-[300px]' // Ukuran item tengah (fixed width)
                  : 'w-[200px]'; // Ukuran item samping (fixed width)
                const heightClass = 'h-[250px]'; // Tinggi semua gambar sama
                const scaleClass = isActive ? 'scale-100 opacity-100' : 'scale-90 opacity-60'; // Adjusted opacity for inactive

                return (
                  <div
                    key={project.id}
                    className={`flex-none transform transition-all duration-500 ease-in-out
                                ${widthClass} ${heightClass} ${scaleClass}
                                relative rounded-lg overflow-hidden shadow-lg group snap-center`}
                    onClick={() => {
                        // Hentikan auto-scroll saat diklik
                        if (scrollProjectsIntervalRef.current) {
                            clearInterval(scrollProjectsIntervalRef.current);
                            scrollProjectsIntervalRef.current = null; // Set to null after clearing
                        }
                        scrollToProject(index); // Panggil fungsi scrollToProject yang memusatkan
                    }}
                  >
                    <img
                      src={project.gambar || 'https://placehold.co/600x400/333333/cccccc?text=No+Image'}
                      alt={project.nama_project}
                      className="w-full h-full object-cover"
                      onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/600x400/333333/cccccc?text=No+Image'; }}
                    />
                    {/* Overlay default (transparan) dan hover (VIEW PROJECT) */}
                    <div className="absolute inset-0 bg-black bg-opacity-0 flex flex-col justify-center items-center text-center p-6 text-white
                                    group-hover:bg-opacity-70 group-hover:opacity-100 transition-all duration-300">
                        {/* Konten yang muncul saat hover */}
                        <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                            {/* Menghapus "PROJECT KE-X" */}
                            {project.link && (
                                <a
                                    href={project.link}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="block mt-4 px-4 py-2 border-2 border-white text-white font-medium rounded-md
                                               hover:bg-white hover:text-black transition-colors duration-300
                                               inline-flex items-center"
                                >
                                    VIEW PROJECT
                                    <svg className="ml-2 h-5 w-5" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                                    </svg>
                                </a>
                            )}
                        </div>
                    </div>
                  </div>
                );
              })}
            </div>
            
            {/* Judul dan Deskripsi Proyek Aktif - DI BAWAH CAROUSEL, DI ATAS NAVIGASI DOT */}
            <div className="text-center mt-8 px-4 max-w-2xl mx-auto">
              <h3 className="text-4xl md:text-5xl font-extrabold mb-4 uppercase tracking-wider text-white">
                {projects[activeProjectIndex]?.nama_project?.toUpperCase() || ''}
              </h3>
              <p className="text-lg md:text-xl text-gray-300 leading-relaxed">
                {projects[activeProjectIndex]?.deskripsi || ''}
              </p>
            </div>

            {/* Navigasi Dot / Indicator */}
            <div className="flex justify-center mt-8 space-x-3">
              {projects.map((_, index) => (
                <button
                  key={index}
                  className={`w-3 h-3 rounded-full transition-colors duration-300
                              ${index === activeProjectIndex ? 'bg-indigo-500' : 'bg-gray-400 hover:bg-gray-300'}`}
                  onClick={() => scrollToProject(index)}
                ></button>
              ))}
            </div>
          </>
        )}
      </section>

      {/* Experience Section - Horizontal Scrollable like Education (simplified cards) */}
      <section id="experiences-section" className="py-12 bg-gray-100 text-gray-800 flex flex-col items-center justify-center">
        <SectionHeader
            title="Pengalaman Kerja"
            subtitle="Pengalaman Kerja Profesional Saya"
            titleColor="text-gray-800"
            subtitleColor="text-gray-600"
        />
        {experiences.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada pengalaman kerja untuk ditampilkan.</p>
        ) : (
          <div className="container mx-auto px-4"> {/* Container untuk margin samping */}
            <div ref={experiencesRef} className="flex overflow-x-auto snap-x snap-mandatory w-full hide-scrollbar -mx-2 space-x-6 py-4">
              {experiences.map((exp) => (
                <div key={exp.id} className="flex-none w-80 p-6 rounded-lg shadow-md bg-white snap-center transform transition-transform duration-300 hover:scale-105">
                  <h3 className="text-xl font-semibold text-gray-800 mb-1">{exp.posisi}</h3>
                  <p className="text-indigo-600 font-medium mb-1">{exp.tempat}</p>
                  <p className="text-gray-500 text-sm mb-3">{exp.tahun}</p>
                  <p className="text-gray-700 text-sm line-clamp-4">{exp.deskripsi}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </section>

      {/* Education Section - Latar Belakang Pendidikan dengan Timeline Kustom */}
      <section id="education-section" className="py-12 bg-slate-800 text-white"> {/* Background gelap untuk bagian education */}
        <div className="container mx-auto px-4">
            <SectionHeader
                title="Latar Belakang Pendidikan"
                subtitle="Riwayat Pendidikan Saya"
                titleColor="text-white"
                subtitleColor="text-gray-300"
            />

          {education.length === 0 ? (
            <p className="text-center text-gray-500">Belum ada data pendidikan untuk ditampilkan.</p>
          ) : (
            // Outer container for the timeline structure
            <div className="relative space-y-16 py-8 md:px-0 before:absolute before:left-3 before:top-0 before:h-full before:w-1 before:bg-indigo-500 md:before:left-1/2 md:before:-translate-x-1/2 before:rounded-full">
              {education.map((edu, index) => (
                // Each timeline item container
                <div key={edu.id} className={`flex items-start w-full relative`}>
                  {/* Dot on the timeline line (z-index to be on top of the line) */}
                  {/* Adjusted top position to vertically align with the content's first line */}
                  <div className="absolute left-3 md:left-1/2 transform -translate-x-1/2 bg-indigo-500 rounded-full w-4 h-4 z-10 border-2 border-slate-800 -mt-1"></div> 
                  
                  {/* Content wrapper for horizontal positioning */}
                  {/* This div will handle pushing the actual content box to the left/right */}
                  <div className={`
                    w-full flex-grow flex
                    ${index % 2 === 0 // Right side item
                      ? 'justify-end md:justify-start' // Mobile: push right (away from left line), Desktop: push left (closer to center line)
                      : 'justify-start md:justify-end'} // Mobile: push left (away from left line), Desktop: push right (closer to center line)
                  `}>
                    {/* Actual Education content box */}
                    <div className={`
                      p-6 rounded-lg md:w-80 // Fixed width for desktop boxes
                      transform transition-transform duration-300 hover:scale-105
                      text-white relative z-[5]
                      // Adjust margins/padding to control distance from center line
                      // On mobile, the ml-6/mr-6 pushes it away from the fixed left line
                      ${index % 2 === 0
                        ? 'ml-6 md:ml-0 md:mr-4' // Mobile left, Desktop right (pushed left by mr-4)
                        : 'mr-6 md:mr-0 md:ml-4 md:text-right'} // Mobile right, Desktop left (pushed right by ml-4, text-right inside box)
                    `}>
                      <p className="text-xl font-bold mb-1 text-indigo-400">{edu.tahun}</p> {/* Tahun */}
                      <p className="text-2xl font-bold text-gray-300 mb-1">{edu.institusi}</p> {/* Institusi (bold, lebih besar) */}
                      <h3 className="text-lg font-light">{edu.jurusan}</h3> {/* Jurusan (light, lebih kecil) */}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Activities Section - Layout Horizontal Scrollable like Articles */}
      <section id="activities-section" className="py-12 bg-gray-100 text-gray-800 flex flex-col items-center justify-center">
        <SectionHeader
            title="Aktivitas & Prestasi"
            subtitle="Aktivitas dan Prestasi Saya"
            titleColor="text-gray-800"
            subtitleColor="text-gray-600"
        />

        {activities.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada aktivitas untuk ditampilkan.</p>
        ) : (
          <div className="container mx-auto px-4"> {/* Container untuk margin samping */}
            <div ref={activitiesRef} className="flex overflow-x-auto snap-x snap-mandatory w-full hide-scrollbar -mx-2 space-x-6"> {/* Added space-x-6 here */}
              {activities.map((act, index) => (
                <div
                  key={act.id}
                  className="flex-none w-full md:w-1/2 lg:w-1/3 p-2 relative overflow-hidden group snap-center"
                  style={{ height: '384px' }} /* Menjaga tinggi konsisten dengan artikel */
                >
                  <img
                    src={act.image || 'https://placehold.co/600x400/cccccc/333333?text=No+Image'}
                    alt={act.title}
                    className="w-full h-full object-cover transform transition-transform duration-500 group-hover:scale-110"
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/600x400/cccccc/333333?text=No+Image'; }}
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col justify-end p-6 text-white opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <h3 className="text-2xl font-bold mb-2">{act.title}</h3>
                    <p className="text-sm mb-2 text-gray-300">{act.time}</p> {/* Menampilkan waktu */}
                    <p className="text-sm mb-4 line-clamp-3">{act.deskripsi}</p>
                    {/* Link "Lihat Detail" bisa ditambahkan jika ada URL untuk aktivitas */}
                    <a
                      href="#" /* Ganti dengan link aktivitas yang sebenarnya jika ada */
                      onClick={(e) => { e.preventDefault(); window.alert(`Membaca lebih lanjut tentang: ${act.title}`); }}
                      className="inline-flex items-center text-indigo-300 hover:text-indigo-100 text-sm font-medium"
                    >
                      Lihat Detail
                      <svg className="ml-1 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                      </svg>
                    </a>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </section>

      {/* Articles Section - Layout Horizontal Scrollable with Side Margins */}
      <section id="articles-section" className="py-12 bg-white text-gray-800 flex flex-col items-center justify-center">
        <SectionHeader
            title="03 ARTICLES"
            subtitle="MY LATEST POSTS."
            linkText="READ MORE >"
            onLinkClick={() => window.alert('Melihat semua artikel...')}
            titleColor="text-gray-800"
            subtitleColor="text-gray-600"
            linkColor="text-indigo-600 hover:text-indigo-800"
        />

        {articles.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada artikel terbaru untuk ditampilkan.</p>
        ) : (
          <div className="container mx-auto px-4"> {/* Container untuk margin samping */}
            <div ref={articlesRef} className="flex overflow-x-auto snap-x snap-mandatory w-full hide-scrollbar -mx-2 space-x-6"> {/* Added space-x-6 here */}
              {articles.map((article, index) => (
                <div
                  key={article.id}
                  className="flex-none w-full md:w-1/2 lg:w-1/3 p-2 relative overflow-hidden group snap-center"
                  style={{ height: '384px' }} /* Menjaga tinggi konsisten dengan proyek */
                >
                  <img
                    src={article.image_url }
                    alt={article.title}
                    className="w-full h-full object-cover transform transition-transform duration-500 group-hover:scale-110"
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/600x400/cccccc/333333?text=No+Image'; }}
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col justify-end p-6 text-white opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <h3 className="text-2xl font-bold mb-2">{article.title}</h3>
                    <p className="text-sm mb-4 line-clamp-3">{article.content}</p>
                    <a
                      href="#" /* Ganti dengan link artikel yang sebenarnya jika ada */
                      onClick={(e) => { e.preventDefault(); window.alert(`Membaca lebih lanjut tentang: ${article.title}`); }}
                      className="inline-flex items-center text-indigo-300 hover:text-indigo-100 text-sm font-medium"
                    >
                      Baca Selengkapnya
                      <svg className="ml-1 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                      </svg>
                    </a>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </section>


      {/* Footer */}
      <footer className="text-center py-8 bg-gray-800 text-gray-300">
        <div className="container mx-auto px-4">
          <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
          <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>

          {/* Social Media Links */}
          <div className="flex justify-center space-x-6 mt-4">
            {socialMediaLinks.map((link) => (
              <a
                key={link.name}
                href={link.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl"
                aria-label={link.name}
              >
                {/* Menggunakan ikon Font Awesome jika CDN diimpor, jika tidak, tampilkan nama */}
                <i className={link.iconClass} title={link.name}></i>
                {/* Fallback jika Font Awesome tidak dimuat: */}
                {!document.querySelector(`.${link.iconClass}`) && <span>{link.name}</span>}
              </a>
            ))}
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Home;
