import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Projects from './components/Projects';
import Skills from './components/Skills';
import AdminLoginForm from './components/AdminLogin'; // Pastikan nama file ini benar
import Dashboard from './components/Dashboard'; // Import Dashboard

function App() {
  // State untuk mengelola tampilan halaman (routing sederhana)
  const [currentPage, setCurrentPage] = useState('home');
  // State untuk mengelola status login admin
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);

  // Periksa status login admin saat komponen dimuat
  useEffect(() => {
    // Gunakan localStorage untuk persistensi status login
    const loggedIn = localStorage.getItem('isAdminLoggedIn') === 'true';
    setIsAdminLoggedIn(loggedIn);
    // Jika sudah login, arahkan ke dashboard
    if (loggedIn) {
      setCurrentPage('dashboard');
    }
  }, []);

  // Fungsi untuk menangani login
  const handleLogin = (success) => {
    setIsAdminLoggedIn(success);
    if (success) {
      localStorage.setItem('isAdminLoggedIn', 'true');
      setCurrentPage('dashboard'); // Arahkan ke dashboard setelah login sukses
    } else {
      localStorage.removeItem('isAdminLoggedIn');
      // Tidak menggunakan alert, tampilkan pesan di UI atau konsol
      console.error('Login gagal. Periksa username dan password Anda.');
    }
  };

  // Fungsi untuk menangani logout
  const handleLogout = () => {
    setIsAdminLoggedIn(false);
    localStorage.removeItem('isAdminLoggedIn');
    setCurrentPage('home'); // Arahkan kembali ke home setelah logout
  };

  // Render konten berdasarkan currentPage
  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'projects':
        return <Projects />;
      case 'skills':
        return <Skills />;
      case 'login':
        return <AdminLoginForm onLogin={handleLogin} />;
      case 'dashboard':
        // Pastikan hanya admin yang bisa mengakses dashboard
        return isAdminLoggedIn ? <Dashboard /> : <AdminLoginForm onLogin={handleLogin} />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 font-inter">
      {/* Navbar untuk navigasi */}
      <Navbar setCurrentPage={setCurrentPage} isAdminLoggedIn={isAdminLoggedIn} handleLogout={handleLogout} />
      {/* Konten halaman yang dirender */}
      <main className="container mx-auto p-4 py-8">
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
