import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Import komponen-komponen yang telah dibuat terpisah
import HeroSection from './HeroSection';
import AboutMeSection from './AboutMeSection';
import SkillsSection from './SkillsSection';
import ProjectsSection from './ProjectsSection';
import ExperiencesSection from './ExperiencesSection';
import EducationSection from './EducationSection';
import ActivitiesSection from './ActivitiesSection';
import ArticlesSection from './ArticlesSection';
import Footer from './Footer';

// Komponen halaman Home untuk menampilkan semua data portofolio
function Home() {
  const [heroText, setHeroText] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [aboutMe, setAboutMe] = useState({ deskripsi: '', gambar_url: '' });
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  const [articles, setArticles] = useState([]);
  const [hobbies, setHobbies] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_BASE_URL = 'http://localhost:3001/api';

  // Data sosial media (tetap di sini karena ini data konfigurasi global)
  const socialMediaLinks = [
    { name: 'LinkedIn', url: 'https://www.linkedin.com/in/anishka-putri/', iconClass: 'fab fa-linkedin' },
    { name: 'Instagram', url: 'https://www.instagram.com/anishka_pr/', iconClass: 'fab fa-instagram' },
    { name: 'GitHub', url: 'https://github.com/anishkaputri', iconClass: 'fab fa-github' },
    // Tambahkan lebih banyak jika diperlukan
  ];

  // Helper function to fetch data and parse JSON, with better error handling
  const fetchJson = async (url, errorMessage) => {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        let errorDetails = `HTTP error! status: ${response.status}`;
        try {
          const errorData = await response.json();
          if (errorData && errorData.message) {
            errorDetails += `: ${errorData.message}`;
          } else if (errorData) {
            errorDetails += `: ${JSON.stringify(errorData)}`;
          }
        } catch (parseError) {
          errorDetails += `: Failed to parse error response.`;
        }
        throw new Error(`${errorMessage} ${errorDetails}`);
      }
      return response.json();
    } catch (err) {
      console.error(`Error fetching ${url}:`, err);
      throw new Error(err.message.includes(errorMessage) ? err.message : `${errorMessage}: ${err.message}`);
    }
  };

  // Mengambil semua data saat komponen dimuat
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        const [
          heroData, aboutData, skillsData, projectsData,
          experiencesData, educationData, activitiesData,
          articlesData, hobbiesData
        ] = await Promise.all([
          axios.get(`${API_BASE_URL}/hero`),
          axios.get(`${API_BASE_URL}/about`),
          axios.get(`${API_BASE_URL}/skills`),
          axios.get(`${API_BASE_URL}/projects`),
          axios.get(`${API_BASE_URL}/experiences`),
          axios.get(`${API_BASE_URL}/education`),
          axios.get(`${API_BASE_URL}/activities`),
          axios.get(`${API_BASE_URL}/articles`),
          axiosa.get(`${API_BASE_URL}/hobbies`)
        ]);

        setHeroText(heroData.data || { name: '', role_text: '', main_title: '', year_text: '' });
        setAboutMe(aboutData.data || { deskripsi: '', gambar_url: '' });
        setSkills(skillsData.data || []);
        setProjects(projectsData.data || []);
        setExperiences(experiencesData.data || []);
        setEducation(educationData.data || []);
        setActivities(activitiesData.data || []);
        setArticles(articlesData.data || []);
        setHobbies(hobbiesData.data || []);

      } catch (err) {
        setError(err.message || "Terjadi kesalahan saat memuat data.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);


  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
        <p className="mt-4 text-lg">Memuat portofolio Anda yang luar biasa...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center text-red-600 bg-red-100 p-4 rounded-md mx-4 my-8">
        <h2 className="text-xl font-semibold mb-2">Error Memuat Data</h2>
        <p>Terjadi kesalahan saat memuat data portofolio: {error}. Harap pastikan server backend berjalan dengan benar.</p>
        <p className="mt-2 text-sm">Coba periksa konsol browser dan terminal backend untuk detail lebih lanjut.</p>
      </div>
    );
  }

  return (
    <div className="bg-gray-100 text-gray-800 font-inter"> {/* Menambahkan font-inter class */}
      <HeroSection heroText={heroText} />
      <AboutMeSection heroText={heroText} aboutMe={aboutMe} hobbies={hobbies} />
      <SkillsSection skills={skills} />
      <ProjectsSection projects={projects} />
      <ExperiencesSection experiences={experiences} />
      <EducationSection education={education} />
      <ActivitiesSection activities={activities} />
      <ArticlesSection articles={articles} />
      <Footer heroText={heroText} socialMediaLinks={socialMediaLinks} />
    </div>
  );
}

export default Home;
