import React from 'react';
import SectionHeader from './SectionHeader';

// Komponen ProjectsSection
const ProjectsSection = ({ projects, projectsRef, activeProjectIndex, setActiveProjectIndex }) => {
  return (
    // Latar belakang diubah menjadi lebih muda (putih)
    <section className="py-16 px-4 bg-white">
      <SectionHeader
        title="Proyek Terbaru"
        subtitle="Lihat beberapa karya terbaru saya."
        titleColor="text-gray-800"
        subtitleColor="text-gray-600"
      />
      <div
        ref={projectsRef}
        className="container mx-auto mt-8 flex space-x-6 overflow-x-auto pb-4 scrollbar-thin scrollbar-thumb-gray-300 scrollbar-track-gray-100" // Kelas scrollbar kustom
      >
        {projects.map((project, index) => (
          <div
            key={project.id}
            className="flex-shrink-0 w-80 bg-white rounded-lg shadow-lg overflow-hidden transform hover:scale-105 transition duration-300 cursor-pointer"
            onClick={() => setActiveProjectIndex(index)}
          >
            {project.gambar && (
              <img
                src={project.gambar}
                alt={project.nama_project}
                className="w-full h-48 object-cover"
                onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/600x400/cccccc/333333?text=No+Image'; }}
              />
            )}
            <div className="p-4">
              <h3 className="text-xl font-semibold text-gray-800 mb-2">{project.nama_project}</h3>
              <p className="text-gray-600 text-sm line-clamp-3">{project.deskripsi}</p>
              {project.link && (
                <a
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-flex items-center text-indigo-600 hover:text-indigo-800 text-sm font-medium mt-3"
                >
                  Lihat Proyek
                  <svg className="ml-1 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                  </svg>
                </a>
              )}
            </div>
          </div>
        ))}
      </div>

      {activeProjectIndex !== null && projects[activeProjectIndex] && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full p-6 relative">
            <button
              onClick={() => setActiveProjectIndex(null)}
              className="absolute top-3 right-3 text-gray-600 hover:text-gray-900 text-3xl font-bold"
            >
              &times;
            </button>
            <h2 className="text-3xl font-bold text-gray-800 mb-4">{projects[activeProjectIndex].nama_project}</h2>
            {projects[activeProjectIndex].gambar && (
              <img
                src={projects[activeProjectIndex].gambar}
                alt={projects[activeProjectIndex].nama_project}
                className="w-full h-64 object-cover rounded-md mb-4"
                onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/600x400/cccccc/333333?text=No+Image'; }}
              />
            )}
            <p className="text-gray-700 text-base mb-4">{projects[activeProjectIndex].deskripsi}</p>
            {projects[activeProjectIndex].link && (
              <a
                href={projects[activeProjectIndex].link}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
              >
                Kunjungi Proyek
                <svg className="ml-2 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                </svg>
              </a>
            )}
          </div>
        </div>
      )}
    </section>
  );
};

export default ProjectsSection;
