import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';

// Komponen halaman Home untuk menampilkan data Hero dan About Me, Skills, Projects, Experiences, Education, Activities, Articles
function Home() {
  const [heroText, setHeroText] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [aboutMe, setAboutMe] = useState({ deskripsi: '', gambar_url: '', hobbies: [] }); // Menambahkan 'hobbies' ke state aboutMe
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  const [articles, setArticles] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State untuk melacak proyek yang sedang aktif/di tengah
  const [activeProjectIndex, setActiveProjectIndex] = useState(0);

  const API_BASE_URL = 'http://localhost:3001/api';

  // Ref untuk kontainer scroll proyek, aktivitas, pengalaman kerja, dan artikel
  const projectsRef = useRef(null);
  const scrollProjectsIntervalRef = useRef(null);
  const experiencesRef = useRef(null); // Ref baru untuk experiences
  const scrollExperiencesIntervalRef = useRef(null); // Ref baru untuk auto-scroll experiences

  const activitiesRef = useRef(null);
  const scrollActivitiesIntervalRef = useRef(null);

  const articlesRef = useRef(null);
  const scrollArticlesIntervalRef = useRef(null);

  // Data sosial media statis
  const socialMediaLinks = [
    { name: 'GitHub', url: 'https://github.com/your-github', iconClass: 'fab fa-github' },
    { name: 'LinkedIn', url: 'https://linkedin.com/in/your-linkedin', iconClass: 'fab fa-linkedin' },
    { name: 'Twitter', url: 'https://twitter.com/your-twitter', iconClass: 'fab fa-twitter' },
    { name: 'Instagram', url: 'https://instagram.com/your-instagram', iconClass: 'fab fa-instagram' },
    { name: 'Facebook', url: 'https://facebook.com/your-facebook', iconClass: 'fab fa-facebook' },
  ];

  // Fungsi untuk mengambil data Hero
  const fetchHeroText = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/hero_texts`);
      if (response.data.length > 0) {
        setHeroText(response.data[0]);
      }
    } catch (err) {
      console.error("Gagal memuat teks hero:", err);
      setError("Gagal memuat teks hero.");
    }
  }, [API_BASE_URL]);

  // Fungsi untuk mengambil data About Me dan Hobi
  const fetchAboutMe = useCallback(async () => {
    try {
      const aboutMeResponse = await axios.get(`${API_BASE_URL}/about_me`);
      const hobbiesResponse = await axios.get(`${API_BASE_URL}/hobbies`); // Ambil data hobi

      let combinedAboutMe = { deskripsi: '', gambar_url: '', hobbies: [] };

      if (aboutMeResponse.data.length > 0) {
        combinedAboutMe = { ...combinedAboutMe, ...aboutMeResponse.data[0] };
      }

      if (hobbiesResponse.data.length > 0) {
        combinedAboutMe.hobbies = hobbiesResponse.data; // Gabungkan hobi ke dalam aboutMe
      }
      setAboutMe(combinedAboutMe);
    } catch (err) {
      console.error("Gagal memuat data About Me atau Hobi:", err);
      setError("Gagal memuat data About Me atau Hobi.");
    }
  }, [API_BASE_URL]);


  // Fungsi untuk mengambil data Skills
  const fetchSkills = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/skills`);
      setSkills(response.data);
    } catch (err) {
      console.error("Gagal memuat keahlian:", err);
      setError("Gagal memuat keahlian.");
    }
  }, [API_BASE_URL]);

  // Fungsi untuk mengambil data Projects
  const fetchProjects = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/projects`);
      setProjects(response.data);
    } catch (err) {
      console.error("Gagal memuat proyek:", err);
      setError("Gagal memuat proyek.");
    }
  }, [API_BASE_URL]);

  // Fungsi untuk mengambil data Experiences
  const fetchExperiences = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/experiences`);
      setExperiences(response.data);
    } catch (err) {
      console.error("Gagal memuat pengalaman:", err);
      setError("Gagal memuat pengalaman.");
    }
  }, [API_BASE_URL]);

  // Fungsi untuk mengambil data Education
  const fetchEducation = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/education`);
      setEducation(response.data);
    } catch (err) {
      console.error("Gagal memuat pendidikan:", err);
      setError("Gagal memuat pendidikan.");
    }
  }, [API_BASE_URL]);

  // Fungsi untuk mengambil data Activities
  const fetchActivities = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/activities`);
      setActivities(response.data);
    } catch (err) {
      console.error("Gagal memuat aktivitas:", err);
      setError("Gagal memuat aktivitas.");
    }
  }, [API_BASE_URL]);

  // Fungsi untuk mengambil data Articles
  const fetchArticles = useCallback(async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/articles`);
      setArticles(response.data);
    } catch (err) {
      console.error("Gagal memuat artikel:", err);
      setError("Gagal memuat artikel.");
    } finally {
      setLoading(false);
    }
  }, [API_BASE_URL]);


  // Efek untuk memuat semua data saat komponen dimuat
  useEffect(() => {
    const loadAllData = async () => {
      setLoading(true);
      setError(null);
      await Promise.all([
        fetchHeroText(),
        fetchAboutMe(),
        fetchSkills(),
        fetchProjects(),
        fetchExperiences(),
        fetchEducation(),
        fetchActivities(),
        fetchArticles(),
      ]);
      setLoading(false);
    };

    loadAllData();
  }, [fetchHeroText, fetchAboutMe, fetchSkills, fetchProjects, fetchExperiences, fetchEducation, fetchActivities, fetchArticles]);


  // Efek untuk auto-scroll Projects
  useEffect(() => {
    const startScrollProjects = () => {
      if (projectsRef.current) {
        scrollProjectsIntervalRef.current = setInterval(() => {
          const container = projectsRef.current;
          const scrollAmount = container.clientWidth / 2; // Scroll setengah lebar container

          if (container.scrollLeft + container.clientWidth >= container.scrollWidth - 1) {
            container.scrollTo({ left: 0, behavior: 'smooth' });
          } else {
            container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
          }
        }, 3000); // Ganti interval waktu sesuai kebutuhan
      }
    };

    const stopScrollProjects = () => {
      if (scrollProjectsIntervalRef.current) {
        clearInterval(scrollProjectsIntervalRef.current);
      }
    };

    // Mulai auto-scroll setelah komponen dimuat
    startScrollProjects();

    // Hentikan auto-scroll saat mouse masuk dan mulai lagi saat mouse keluar
    const projectsContainer = projectsRef.current;
    if (projectsContainer) {
      projectsContainer.addEventListener('mouseenter', stopScrollProjects);
      projectsContainer.addEventListener('mouseleave', startScrollProjects);
    }

    // Cleanup interval saat komponen di-unmount
    return () => {
      stopScrollProjects();
      if (projectsContainer) {
        projectsContainer.removeEventListener('mouseenter', stopScrollProjects);
        projectsContainer.removeEventListener('mouseleave', startScrollProjects);
      }
    };
  }, [projects]); // Dependency array: jalankan ulang efek jika projects berubah


  // Efek untuk auto-scroll Experiences
  useEffect(() => {
    const startScrollExperiences = () => {
      if (experiencesRef.current) {
        scrollExperiencesIntervalRef.current = setInterval(() => {
          const container = experiencesRef.current;
          const scrollAmount = container.clientWidth / 2;

          if (container.scrollLeft + container.clientWidth >= container.scrollWidth - 1) {
            container.scrollTo({ left: 0, behavior: 'smooth' });
          } else {
            container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
          }
        }, 3000);
      }
    };

    const stopScrollExperiences = () => {
      if (scrollExperiencesIntervalRef.current) {
        clearInterval(scrollExperiencesIntervalRef.current);
      }
    };

    startScrollExperiences();

    const experiencesContainer = experiencesRef.current;
    if (experiencesContainer) {
      experiencesContainer.addEventListener('mouseenter', stopScrollExperiences);
      experiencesContainer.addEventListener('mouseleave', startScrollExperiences);
    }

    return () => {
      stopScrollExperiences();
      if (experiencesContainer) {
        experiencesContainer.removeEventListener('mouseenter', stopScrollExperiences);
        experiencesContainer.removeEventListener('mouseleave', startScrollExperiences);
      }
    };
  }, [experiences]); // Dependency array: jalankan ulang efek jika experiences berubah


  // Efek untuk auto-scroll Activities
  useEffect(() => {
    const startScrollActivities = () => {
      if (activitiesRef.current) {
        scrollActivitiesIntervalRef.current = setInterval(() => {
          const container = activitiesRef.current;
          const scrollAmount = container.clientWidth / 2; // Scroll setengah lebar container

          if (container.scrollLeft + container.clientWidth >= container.scrollWidth - 1) {
            container.scrollTo({ left: 0, behavior: 'smooth' });
          } else {
            container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
          }
        }, 3000); // Ganti interval waktu sesuai kebutuhan
      }
    };

    const stopScrollActivities = () => {
      if (scrollActivitiesIntervalRef.current) {
        clearInterval(scrollActivitiesIntervalRef.current);
      }
    };

    startScrollActivities();

    const activitiesContainer = activitiesRef.current;
    if (activitiesContainer) {
      activitiesContainer.addEventListener('mouseenter', stopScrollActivities);
      activitiesContainer.addEventListener('mouseleave', startScrollActivities);
    }

    return () => {
      stopScrollActivities();
      if (activitiesContainer) {
        activitiesContainer.removeEventListener('mouseenter', stopScrollActivities);
        activitiesContainer.removeEventListener('mouseleave', startScrollActivities);
      }
    };
  }, [activities]); // Dependency array: jalankan ulang efek jika activities berubah


  // Efek untuk auto-scroll Articles
  useEffect(() => {
    const startScrollArticles = () => {
      if (articlesRef.current) {
        scrollArticlesIntervalRef.current = setInterval(() => {
          const container = articlesRef.current;
          const scrollAmount = container.clientWidth / 2; // Scroll setengah lebar container

          if (container.scrollLeft + container.clientWidth >= container.scrollWidth - 1) {
            container.scrollTo({ left: 0, behavior: 'smooth' });
          } else {
            container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
          }
        }, 3000); // Ganti interval waktu sesuai kebutuhan
      }
    };

    const stopScrollArticles = () => {
      if (scrollArticlesIntervalRef.current) {
        clearInterval(scrollArticlesIntervalRef.current);
      }
    };

    startScrollArticles();

    const articlesContainer = articlesRef.current;
    if (articlesContainer) {
      articlesContainer.addEventListener('mouseenter', stopScrollArticles);
      articlesContainer.addEventListener('mouseleave', startScrollArticles);
    }

    return () => {
      stopScrollArticles();
      if (articlesContainer) {
        articlesContainer.removeEventListener('mouseenter', stopScrollArticles);
        articlesContainer.removeEventListener('mouseleave', startScrollArticles);
      }
    };
  }, [articles]); // Dependency array: jalankan ulang efek jika articles berubah


  // Auto-scroll untuk projects
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveProjectIndex((prevIndex) => (prevIndex + 1) % projects.length);
    }, 5000); // Ganti interval waktu sesuai kebutuhan
    return () => clearInterval(interval);
  }, [projects]);

  // Handler untuk klik tombol panah kiri/kanan pada Project Carousel
  const handleProjectArrowClick = (direction) => {
    if (projectsRef.current) {
      const scrollAmount = projectsRef.current.clientWidth; // Scroll satu lebar penuh
      if (direction === 'left') {
        projectsRef.current.scrollBy({ left: -scrollAmount, behavior: 'smooth' });
      } else {
        projectsRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
      }
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
        Memuat data...
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900 text-red-500">
        Error: {error}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-gray-200 font-inter">
      {/* Hero Section */}
      <section id="home" className="relative h-screen flex items-center justify-center text-center bg-cover bg-center" style={{ backgroundImage: `url(${heroText.background_image_url || 'https://placehold.co/1920x1080/1a202c/e2e8f0?text=Background'})` }}>
        <div className="absolute inset-0 bg-black opacity-60"></div>
        <div className="relative z-10 p-6 max-w-4xl mx-auto">
          <h1 className="text-5xl md:text-7xl font-bold text-white mb-4 leading-tight rounded-lg p-2 animate-fade-in-down">
            {heroText.name || 'Nama Anda'}
          </h1>
          <p className="text-xl md:text-3xl text-gray-300 mb-6 font-light rounded-lg p-2 animate-fade-in-up">
            {heroText.role_text || 'Peran Anda'}
          </p>
          <p className="text-lg md:text-xl text-gray-400 rounded-lg p-2 animate-fade-in">
            {heroText.main_title || 'Judul Utama Portofolio Anda'} {heroText.year_text && `(${heroText.year_text})`}
          </p>
        </div>
      </section>

      {/* About Me Section */}
      <section id="about" className="py-16 bg-gray-800 text-gray-200">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Tentang Saya</h2>
          <div className="flex flex-col md:flex-row items-center gap-10">
            <div className="md:w-1/3 flex justify-center">
              <img
                src={aboutMe.gambar_url || "https://placehold.co/300x300/4a5568/edf2f7?text=Foto+Profil"}
                alt="Foto Profil"
                className="w-64 h-64 md:w-80 md:h-80 rounded-full object-cover shadow-lg border-4 border-purple-500"
              />
            </div>
            <div className="md:w-2/3 text-lg leading-relaxed text-center md:text-left">
              <p className="mb-4">{aboutMe.deskripsi || 'Deskripsi tentang diri Anda akan muncul di sini.'}</p>

              {/* Hobbies Section (terintegrasi) */}
              {aboutMe.hobbies && aboutMe.hobbies.length > 0 && (
                <div className="mt-8">
                  <h3 className="text-2xl font-bold text-white mb-4">Hobi Saya</h3>
                  <div className="flex flex-wrap justify-center md:justify-start gap-4">
                    {aboutMe.hobbies.map((hobby) => (
                      <span
                        key={hobby.id}
                        className="bg-blue-600 text-white px-4 py-2 rounded-full shadow-md hover:bg-blue-700 transition-colors duration-300"
                      >
                        {hobby.name}
                      </span>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </section>

      {/* Skills Section */}
      <section id="skills" className="py-16 bg-gray-900 text-gray-200">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Keahlian</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {skills.map((skill) => (
              <div key={skill.id} className="bg-gray-800 p-6 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 transform hover:scale-105">
                <h3 className="text-2xl font-semibold text-white mb-4">{skill.name}</h3>
                <div className="w-full bg-gray-700 rounded-full h-2.5 mb-2">
                  <div
                    className="bg-purple-600 h-2.5 rounded-full"
                    style={{ width: `${skill.level}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-400 text-right">{skill.level}%</p>
                <p className="text-gray-300 mt-3">{skill.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" className="py-16 bg-gray-800 text-gray-200 relative">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Proyek</h2>
          <div className="relative">
            <button
              onClick={() => handleProjectArrowClick('left')}
              className="absolute left-0 top-1/2 -translate-y-1/2 bg-gray-700 p-3 rounded-full shadow-lg z-10 hover:bg-gray-600 focus:outline-none transition-colors"
              aria-label="Previous project"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div
              ref={projectsRef}
              className="flex overflow-x-scroll snap-x snap-mandatory scroll-smooth pb-4 px-2 -mx-4 no-scrollbar"
            >
              {projects.map((project) => (
                <div
                  key={project.id}
                  className="flex-none w-full md:w-1/2 lg:w-1/3 snap-center p-4"
                >
                  <div className="bg-gray-900 rounded-lg shadow-lg overflow-hidden h-full flex flex-col">
                    <img
                      src={project.image_url || `https://placehold.co/600x400/2d3748/cbd5e0?text=Proyek+${project.id}`}
                      alt={project.title}
                      className="w-full h-48 object-cover"
                      onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/600x400/2d3748/cbd5e0?text=Proyek+${project.id}`; }}
                    />
                    <div className="p-6 flex flex-col flex-grow">
                      <h3 className="text-2xl font-bold text-white mb-2">{project.title}</h3>
                      <p className="text-gray-400 text-sm mb-3">{project.date}</p>
                      <p className="text-gray-300 mb-4 flex-grow">{project.description}</p>
                      <div className="flex flex-wrap gap-2 mb-4">
                        {project.technologies.map((tech, index) => (
                          <span key={index} className="bg-purple-600 text-white text-xs px-3 py-1 rounded-full">
                            {tech}
                          </span>
                        ))}
                      </div>
                      <div className="flex justify-between items-center mt-auto">
                        <a
                          href={project.github_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-purple-400 hover:text-purple-300 transition-colors flex items-center"
                        >
                          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
                          </svg>
                          GitHub
                        </a>
                        <a
                          href={project.live_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-purple-400 hover:text-purple-300 transition-colors flex items-center"
                        >
                          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                          </svg>
                          Demo
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <button
              onClick={() => handleProjectArrowClick('right')}
              className="absolute right-0 top-1/2 -translate-y-1/2 bg-gray-700 p-3 rounded-full shadow-lg z-10 hover:bg-gray-600 focus:outline-none transition-colors"
              aria-label="Next project"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" className="py-16 bg-gray-900 text-gray-200">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Pengalaman</h2>
          <div
            ref={experiencesRef}
            className="flex overflow-x-scroll snap-x snap-mandatory scroll-smooth pb-4 px-2 -mx-4 no-scrollbar"
          >
            {experiences.map((exp) => (
              <div key={exp.id} className="flex-none w-full md:w-1/2 lg:w-1/3 snap-center p-4">
                <div className="bg-gray-800 rounded-lg shadow-lg p-6 h-full flex flex-col">
                  <h3 className="text-2xl font-bold text-white mb-2">{exp.title}</h3>
                  <p className="text-purple-400 mb-2">{exp.company}</p>
                  <p className="text-gray-400 text-sm mb-4">{exp.period}</p>
                  <ul className="list-disc list-inside text-gray-300 flex-grow">
                    {exp.description.split('. ').map((item, index) => (
                      item && <li key={index} className="mb-1">{item.trim()}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Education Section */}
      <section id="education" className="py-16 bg-gray-800 text-gray-200">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Pendidikan</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {education.map((edu) => (
              <div key={edu.id} className="bg-gray-900 rounded-lg shadow-lg p-6">
                <h3 className="text-2xl font-bold text-white mb-2">{edu.degree}</h3>
                <p className="text-purple-400 mb-2">{edu.institution}</p>
                <p className="text-gray-400 text-sm">{edu.period}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Activities Section */}
      <section id="activities" className="py-16 bg-gray-900 text-gray-200">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Aktivitas & Organisasi</h2>
          <div
            ref={activitiesRef}
            className="flex overflow-x-scroll snap-x snap-mandatory scroll-smooth pb-4 px-2 -mx-4 no-scrollbar"
          >
            {activities.map((activity) => (
              <div key={activity.id} className="flex-none w-full md:w-1/2 lg:w-1/3 snap-center p-4">
                <div className="bg-gray-800 rounded-lg shadow-lg p-6 h-full flex flex-col">
                  <h3 className="text-2xl font-bold text-white mb-2">{activity.title}</h3>
                  <p className="text-purple-400 mb-2">{activity.organization}</p>
                  <p className="text-gray-400 text-sm mb-4">{activity.period}</p>
                  <p className="text-gray-300 flex-grow">{activity.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Articles Section */}
      <section id="articles" className="py-16 bg-gray-800 text-gray-200">
        <div className="container mx-auto px-4">
          <h2 className="text-4xl font-bold text-center mb-12 text-white">Artikel Terbaru</h2>
          <div
            ref={articlesRef}
            className="flex overflow-x-scroll snap-x snap-mandatory scroll-smooth pb-4 px-2 -mx-4 no-scrollbar"
          >
            {articles.map((article) => (
              <div key={article.id} className="flex-none w-full md:w-1/2 lg:w-1/3 snap-center p-4">
                <div className="bg-gray-900 rounded-lg shadow-lg overflow-hidden h-full flex flex-col">
                  <img
                    src={article.image_url || `https://placehold.co/600x400/2d3748/cbd5e0?text=Artikel+${article.id}`}
                    alt={article.title}
                    className="w-full h-48 object-cover"
                    onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/600x400/2d3748/cbd5e0?text=Artikel+${article.id}`; }}
                  />
                  <div className="p-6 flex flex-col flex-grow">
                    <h3 className="text-2xl font-bold text-white mb-2">{article.title}</h3>
                    <p className="text-gray-400 text-sm mb-3">{article.date}</p>
                    <p className="text-gray-300 mb-4 flex-grow">{article.description}</p>
                    <a
                      href={article.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-purple-400 hover:text-purple-300 transition-colors flex items-center mt-auto"
                    >
                      Baca Selengkapnya
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 ml-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                      </svg>
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>


      {/* Footer */}
      <footer className="text-center py-8 bg-gray-800 text-gray-300">
        <div className="container mx-auto px-4">
          <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
          <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>

          {/* Social Media Links */}
          <div className="flex justify-center space-x-6 mt-4">
            {socialMediaLinks.map((link) => (
              <a
                key={link.name}
                href={link.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl"
                aria-label={link.name}
              >
                {/* Menggunakan ikon Font Awesome jika CDN diimpor, jika tidak, tampilkan nama */}
                <i className={link.iconClass} title={link.name}></i>
                {/* Fallback jika Font Awesome tidak dimuat: */}
                {!document.querySelector(`.${link.iconClass}`) && link.name}
              </a>
            ))}
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Home;
