import React, { useState, useEffect } from 'react';

// Komponen untuk menampilkan daftar keterampilan
function Skills() {
  const [skills, setSkills] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_BASE_URL = 'http://localhost:3001/api';

  useEffect(() => {
    const fetchSkills = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/skills`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setSkills(data);
      } catch (err) {
        setError(err.message);
        console.error("Failed to fetch skills:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchSkills();
  }, []);

  if (loading) {
    return <div className="text-center text-gray-600">Memuat keterampilan...</div>;
  }

  if (error) {
    return <div className="text-center text-red-600 bg-red-100 p-4 rounded-md">Error memuat keterampilan: {error}</div>;
  }

  return (
    <div className="py-8">
      <h1 className="text-4xl font-bold text-center text-gray-800 mb-8">Keterampilan Saya</h1>
      {skills.length === 0 ? (
        <p className="text-center text-gray-500">Belum ada keterampilan yang tersedia.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {skills.map((skill) => (
            <div key={skill.id} className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center text-center">
              <h2 className="text-xl font-semibold text-gray-800 mb-3">{skill.nama_skill}</h2>
              <div className="w-full bg-gray-200 rounded-full h-2.5">
                <div
                  className="bg-indigo-600 h-2.5 rounded-full"
                  style={{ width: `${skill.level}%` }}
                ></div>
              </div>
              <p className="text-sm text-gray-500 mt-2">{skill.level}%</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Skills;
