import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios'; // Pastikan axios terinstal: npm install axios

// Import komponen-komponen utama Anda
import Header from './Header'; // Asumsikan Anda memiliki komponen Header
import Home from './Home';
import Article from './Article';
import Contact from './Contact';
// Anda juga perlu komponen Footer terpisah jika belum ada
// import Footer from './Footer';

function App() {
  const [heroText, setHeroText] = useState({
    name: '',
    role_text: '',
    main_title: '',
    year_text: '',
    email: '',
    social_linkedin: '',
    social_github: '',
    social_instagram: ''
  });
  const [loadingHeroText, setLoadingHeroText] = useState(true);
  const [heroTextError, setHeroTextError] = useState(null);

  const API_BASE_URL = 'http://localhost:3001/api';

  useEffect(() => {
    const fetchHeroData = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/hero`);
        // Pastikan semua properti diinisialisasi, bahkan jika API mengembalikan null/undefined
        setHeroText({
          name: response.data.name || '',
          role_text: response.data.role_text || '',
          main_title: response.data.main_title || '',
          year_text: response.data.year_text || '',
          email: response.data.email || '',
          social_linkedin: response.data.social_linkedin || '',
          social_github: response.data.social_github || '',
          social_instagram: response.data.social_instagram || ''
        });
      } catch (err) {
        console.error("Failed to fetch hero text in App.js:", err);
        setHeroTextError("Gagal memuat data utama.");
      } finally {
        setLoadingHeroText(false);
      }
    };

    fetchHeroData();
  }, []); // [] berarti efek ini hanya berjalan sekali saat komponen di-mount

  if (loadingHeroText) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
        <p className="mt-4 text-lg">Memuat data utama...</p>
      </div>
    );
  }

  if (heroTextError) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-red-100 text-red-800 p-4">
        <p className="text-xl font-bold">Terjadi Kesalahan:</p>
        <p className="mt-2 text-center">{heroTextError}</p>
        <p className="mt-4">Mohon pastikan server backend berjalan dan dapat diakses.</p>
      </div>
    );
  }

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <Header heroText={heroText} /> {/* Teruskan heroText ke Header jika digunakan */}
        <main className="flex-grow">
          <Routes>
            {/* Teruskan heroText ke komponen yang membutuhkannya */}
            <Route path="/" element={<Home heroText={heroText} />} />
            <Route path="/articles" element={<Article heroText={heroText} />} />
            <Route path="/contact" element={<Contact heroText={heroText} />} />
            {/* Tambahkan rute lain di sini */}
          </Routes>
        </main>

        {/* Komponen Footer (Jika Anda memilikinya, letakkan di sini untuk merender sekali) */}
        {/* Jika belum ada, Anda bisa menempelkan kode footer langsung di sini: */}
        <footer className="text-center py-8 bg-gray-800 text-gray-300 mt-auto">
          <div className="container mx-auto px-4">
            <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
            <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>

            <div className="flex justify-center space-x-6 mt-4">
              {heroText.social_linkedin && (
                <a href={heroText.social_linkedin} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="LinkedIn">
                  <i className="fab fa-linkedin"></i>
                </a>
              )}
              {heroText.social_github && (
                <a href={heroText.social_github} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="GitHub">
                  <i className="fab fa-github"></i>
                </a>
              )}
              {heroText.social_instagram && (
                <a href={heroText.social_instagram} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="Instagram">
                  <i className="fab fa-instagram"></i>
                </a>
              )}
            </div>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
