import React, { useState, useEffect, useRef } from 'react';
import SectionHeader from './SectionHeader'; // Pastikan path ini sesuai

const ExperiencesSection = ({ experiences }) => {
  const experiencesRef = useRef(null);
  const scrollExperiencesIntervalRef = useRef(null);

  // LOGIKA UNTUK AUTO-SCROLL HORIZONTAL PADA PENGALAMAN KERJA
  useEffect(() => {
    const container = experiencesRef.current;
    if (!container || experiences.length === 0) return;

    if (scrollExperiencesIntervalRef.current) {
      clearInterval(scrollExperiencesIntervalRef.current);
    }

    const scrollRight = () => {
      const itemWidth = container.children[0]?.offsetWidth + 32; // w-96 = 384px, space-x-8 = 32px
      const currentScroll = container.scrollLeft;
      const maxScroll = container.scrollWidth - container.clientWidth;

      let targetScroll = currentScroll + itemWidth;

      if (targetScroll >= maxScroll) {
        targetScroll = 0; // Kembali ke awal jika sudah di akhir
      }

      container.scrollTo({ left: targetScroll, behavior: 'smooth' });
    };

    scrollExperiencesIntervalRef.current = setInterval(scrollRight, 3000); // Auto-scroll setiap 3 detik

    return () => {
      if (scrollExperiencesIntervalRef.current) {
        clearInterval(scrollExperiencesIntervalRef.current);
      }
    };
  }, [experiences]);

  return (
    <section id="experiences-section" className="py-12 bg-gray-100 text-gray-800">
      <div className="container mx-auto px-4">
        <SectionHeader
          title="Pengalaman Kerja"
          subtitle="Riwayat Pengalaman Kerja Saya"
          titleColor="text-gray-800"
          subtitleColor="text-gray-600"
        />

        {experiences.length === 0 ? (
          <p className="text-center text-gray-500">Belum ada pengalaman kerja untuk ditampilkan.</p>
        ) : (
          <div className="relative py-8 overflow-x-auto hide-scrollbar">
            {/* Garis horizontal tengah */}
            <div className="absolute top-1/2 left-0 right-0 h-1 bg-indigo-500 transform -translate-y-1/2 mx-4 rounded-full"></div>

            <div
              ref={experiencesRef}
              className="flex flex-nowrap space-x-8 px-4 relative z-10 py-8 items-center snap-x snap-mandatory"
            >
              {experiences.map((exp, index) => (
                <div
                  key={exp.id}
                  className={`flex-none w-96 p-6 rounded-lg shadow-xl bg-white snap-center transform transition-transform duration-300 hover:scale-105 relative ${
                    index % 2 === 0 ? 'mt-16' : 'mb-16'
                  }`}
                >
                  {/* Dot pada timeline */}
                  <div
                    className={`absolute left-1/2 transform -translate-x-1/2 rounded-full w-4 h-4 bg-indigo-500 border-2 border-white z-20 ${
                      index % 2 === 0 ? '-top-6' : '-bottom-6'
                    }`}
                  ></div>
                  {/* Garis mini */}
                  <div
                    className={`absolute left-1/2 transform -translate-x-1/2 w-1 bg-indigo-500 z-10 ${
                      index % 2 === 0 ? '-top-6 h-6' : '-bottom-6 h-6'
                    }`}
                  ></div>

                  <h3 className="text-2xl font-bold text-gray-800 mb-1">{exp.posisi}</h3>
                  <p className="text-xl font-medium text-indigo-600 mb-1">{exp.tempat}</p>
                  <p className="text-gray-500 text-base mb-3">{exp.tahun}</p>
                  <p className="text-gray-700 text-sm line-clamp-4">{exp.deskripsi}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default ExperiencesSection;
