import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';

// Import komponen-komponen yang digunakan
import HeroSection from './HeroSection';
import AboutMeSection from './AboutMeSection';
import SkillsSection from './SkillsSection';
import ProjectsSection from './ProjectsSection';
import ExperiencesSection from './ExperiencesSection';
import EducationSection from './EducationSection';
import ActivitiesSection from './ActivitiesSection';
import ArticlesSection from './ArticlesSection';
import Contact from './Contact'; // Jika ada komponen Contact terpisah

// Komponen halaman Home untuk menampilkan data Hero dan About Me, Skills, Projects, Experiences, Education, Activities, Articles, dan Hobi
function Home() {
  // State untuk menyimpan data dari API
  const [heroText, setHeroText] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [aboutMe, setAboutMe] = useState({ id: 1, deskripsi: '', gambar_url: '', name: '' }); // Menambahkan 'name' untuk heroText.name
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  const [articles, setArticles] = useState([]);
  const [hobbies, setHobbies] = useState([]); // State baru untuk hobi

  const [loading, setLoading] = useState(true); // State untuk indikator loading
  const [error, setError] = useState(null);     // State untuk menangani error

  // State untuk melacak proyek yang sedang aktif/di tengah (untuk auto-scroll)
  const [activeProjectIndex, setActiveProjectIndex] = useState(0);

  // URL dasar API backend
  const API_BASE_URL = 'http://localhost:3001/api';

  // Ref untuk kontainer scroll proyek, aktivitas, pengalaman kerja, dan artikel
  // Digunakan untuk fungsionalitas auto-scroll
  const projectsRef = useRef(null);
  const scrollProjectsIntervalRef = useRef(null);
  const experiencesRef = useRef(null);
  const scrollExperiencesIntervalRef = useRef(null);
  const activitiesRef = useRef(null);
  const scrollActivitiesIntervalRef = useRef(null);
  const articlesRef = useRef(null);
  const scrollArticlesIntervalRef = useRef(null);

  // Fungsi untuk melakukan auto-scroll horizontal
  const setupAutoScroll = useCallback((ref, items, intervalRef) => {
    const container = ref.current;
    if (!container || items.length === 0) return;

    // Bersihkan interval sebelumnya jika ada
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }

    const scrollRight = () => {
      // Gulir satu item penuh (asumsi semua item memiliki lebar yang sama)
      const itemWidth = container.children[0]?.offsetWidth + 24; // Lebar item + gap (space-x-6 = 24px)
      const currentScroll = container.scrollLeft;
      const maxScroll = container.scrollWidth - container.clientWidth;

      let targetScroll = currentScroll + itemWidth;

      // Jika sudah di akhir, kembali ke awal
      if (targetScroll >= maxScroll) {
        targetScroll = 0;
      }

      container.scrollTo({ left: targetScroll, behavior: 'smooth' });
    };

    // Atur interval auto-scroll
    intervalRef.current = setInterval(scrollRight, 3000); // Auto-scroll setiap 3 detik

    // Cleanup function untuk membersihkan interval saat komponen dilepas atau dependensi berubah
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []);

  // useEffect untuk mengambil semua data dari API saat komponen dimuat
  useEffect(() => {
    const fetchAllData = async () => {
      try {
        // Menggunakan Promise.all untuk mengambil semua data secara bersamaan
        const [
          heroRes,
          aboutMeRes,
          skillsRes,
          projectsRes,
          experiencesRes,
          educationRes,
          activitiesRes,
          articlesRes,
          hobbiesRes // Ambil data hobi
        ] = await Promise.all([
          axios.get(`${API_BASE_URL}/hero`),
          axios.get(`${API_BASE_URL}/about`),
          axios.get(`${API_BASE_URL}/skills`),
          axios.get(`${API_BASE_URL}/projects`),
          axios.get(`${API_BASE_URL}/experiences`),
          axios.get(`${API_BASE_URL}/education`),
          axios.get(`${API_BASE_URL}/activities`),
          axios.get(`${API_BASE_URL}/articles`),
          axios.get(`${API_BASE_URL}/hobbies`) // Endpoint untuk hobi
        ]);

        // Perbarui state dengan data yang diterima
        setHeroText(heroRes.data || { name: '', role_text: '', main_title: '', year_text: '' });
        // Gabungkan data 'name' dari heroRes ke aboutMeRes jika ada
        setAboutMe({
          ...aboutMeRes.data || { id: 1, deskripsi: '', gambar_url: '' },
          name: heroRes.data?.name || ''
        });
        setSkills(skillsRes.data);
        setProjects(projectsRes.data);
        setExperiences(experiencesRes.data);
        setEducation(educationRes.data);
        setActivities(activitiesRes.data);
        setArticles(articlesRes.data);
        setHobbies(hobbiesRes.data); // Set state hobi

      } catch (err) {
        setError(err.message);
        console.error("Failed to fetch data:", err);
      } finally {
        setLoading(false); // Selesai loading, tidak peduli sukses atau gagal
      }
    };

    fetchAllData();
  }, []); // [] berarti useEffect hanya berjalan sekali saat komponen dimuat

  // useEffects untuk mengatur auto-scroll setelah data dimuat
  useEffect(() => {
    // Pastikan data dimuat dan referensi ada sebelum mengatur auto-scroll
    if (!loading && projects.length > 0) {
      return setupAutoScroll(projectsRef, projects, scrollProjectsIntervalRef);
    }
  }, [loading, projects, setupAutoScroll]);

  useEffect(() => {
    if (!loading && experiences.length > 0) {
      return setupAutoScroll(experiencesRef, experiences, scrollExperiencesIntervalRef);
    }
  }, [loading, experiences, setupAutoScroll]);

  useEffect(() => {
    if (!loading && activities.length > 0) {
      return setupAutoScroll(activitiesRef, activities, scrollActivitiesIntervalRef);
    }
  }, [loading, activities, setupAutoScroll]);

  useEffect(() => {
    if (!loading && articles.length > 0) {
      return setupAutoScroll(articlesRef, articles, scrollArticlesIntervalRef);
    }
  }, [loading, articles, setupAutoScroll]);


  // Tampilan loading saat data sedang diambil
  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500 border-solid"></div>
        <p className="mt-4 text-lg">Memuat data...</p>
      </div>
    );
  }

  // Tampilan error jika ada masalah saat mengambil data
  if (error) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-red-100 text-red-800 p-4">
        <p className="text-xl font-bold">Terjadi Kesalahan:</p>
        <p className="mt-2 text-center">{error}</p>
        <p className="mt-4">Mohon pastikan server backend berjalan dan dapat diakses.</p>
      </div>
    );
  }

  // Tampilan utama setelah data berhasil dimuat
  return (
    <div className="home-page overflow-x-hidden">
      {/* Hero Section */}
      <HeroSection heroText={heroText} />

      {/* About Me Section */}
      {/* Meneruskan prop 'hobbies' secara terpisah */}
      <AboutMeSection heroText={heroText} aboutMe={aboutMe} hobbies={hobbies} />

      {/* Skills Section */}
      <SkillsSection skills={skills} />

      {/* Projects Section */}
      {projects.length > 0 && (
        <ProjectsSection
          projects={projects}
          projectsRef={projectsRef}
          activeProjectIndex={activeProjectIndex}
          setActiveProjectIndex={setActiveProjectIndex}
        />
      )}

      {/* Experiences Section */}
      {experiences.length > 0 && (
        <ExperiencesSection experiences={experiences} experiencesRef={experiencesRef} />
      )}

      {/* Education Section */}
      {education.length > 0 && (
        <EducationSection education={education} />
      )}

      {/* Activities Section */}
      {activities.length > 0 && (
        <ActivitiesSection activities={activities} activitiesRef={activitiesRef} />
      )}

      {/* Articles Section */}
      {articles.length > 0 && (
        <ArticlesSection articles={articles} articlesRef={articlesRef} />
      )}

      {/* Contact Section (opsional, jika Anda memiliki komponen Contact terpisah) */}
      <Contact />

      {/* Footer */}
      <footer className="text-center py-8 bg-gray-800 text-gray-300">
        <div className="container mx-auto px-4">
          <p>&copy; {new Date().getFullYear()} {heroText.name || 'Your Name'}. All rights reserved.</p>
          <p className="text-sm mt-1">Dibangun dengan React & Tailwind CSS.</p>

          {/* Social Media Links (contoh) */}
          {/* Pastikan Anda memiliki state atau prop untuk socialMediaLinks jika ingin menampilkannya secara dinamis */}
          <div className="flex justify-center space-x-6 mt-4">
            {/* Contoh placeholder, ganti dengan data sebenarnya jika ada */}
            <a href="#" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="LinkedIn">
              <i className="fab fa-linkedin"></i>
            </a>
            <a href="#" className="text-gray-400 hover:text-white transition-colors duration-300 text-2xl" aria-label="GitHub">
              <i className="fab fa-github"></i>
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Home;
