// src/components/Home.js
import React, { useEffect, useState, useRef } from "react";
import Slider from "react-slick"; // Import Slider
import axios from "axios"; // Import axios
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Paper,
  LinearProgress,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Link as MuiLink,
  CircularProgress,
  useTheme,
  Avatar
} from '@mui/material'; // Import MUI Components
import OpenInNewIcon from '@mui/icons-material/OpenInNew'; // Import MUI Icon

// Import Timeline components from MUI Lab
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineOppositeContent,
  TimelineDot
} from '@mui/lab';

import "slick-carousel/slick/slick.css"; // Slick CSS
import "slick-carousel/slick/slick-theme.css"; // Slick Theme CSS

export default function Home() {
  const theme = useTheme();
  const projectSliderRef = useRef(null); // Ref for the Project Slider
  const articleSliderRef = useRef(null); // Ref for the Article Slider

  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  const [heroText, setHeroText] = useState({ name: "Loading Name...", role_text: "Loading role...", main_title: "Loading Title...", year_text: "Loading Year..." });
  const [aboutMe, setAboutMe] = useState({ deskripsi: "Loading about me...", gambar_url: "" });
  const [articles, setArticles] = useState([]); // Added for articles
  const [hobbies, setHobbies] = useState([]); // Added for hobbies

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State to track the currently displayed project index for external text
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  // === Data Fetching ===
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [
          skillsRes,
          projectsRes,
          experiencesRes,
          educationRes,
          activitiesRes,
          heroTextRes,
          aboutMeRes,
          articlesRes, // Fetch articles
          hobbiesRes,  // Fetch hobbies
        ] = await Promise.all([
          axios.get("http://localhost:3001/api/skills"),
          axios.get("http://localhost:3001/api/projects"),
          axios.get("http://localhost:3001/api/experiences"),
          axios.get("http://localhost:3001/api/education"),
          axios.get("http://localhost:3001/api/activities"),
          axios.get("http://localhost:3001/api/hero"),
          axios.get("http://localhost:3001/api/about"),
          axios.get("http://localhost:3001/api/articles"), // Axios for articles
          axios.get("http://localhost:3001/api/hobbies"),  // Axios for hobbies
        ]);

        setSkills(skillsRes.data);
        setProjects(projectsRes.data);
        setExperiences(experiencesRes.data);
        setEducation(educationRes.data);
        setActivities(activitiesRes.data);
        setHeroText(heroTextRes.data);
        setAboutMe(aboutMeRes.data);
        setArticles(articlesRes.data);
        setHobbies(hobbiesRes.data);

        // Initialize currentProjectIndex if projects data is available
        if (projectsRes.data && projectsRes.data.length > 0) {
            setCurrentProjectIndex(0);
        }

      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Failed to load data. Please ensure backend server is running.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // === Component for Section Header ===
  const Section = ({ title, subtitle, children, bgcolor = 'background.default' }) => (
    <Box sx={{ py: 8, bgcolor: bgcolor, textAlign: 'center' }}>
      <Container maxWidth="lg"> {/* Increased maxWidth for better content spread */}
        <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'bold', color: 'text.primary' }}>
          {title}
        </Typography>
        {subtitle && (
          <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 4 }}>
            {subtitle}
          </Typography>
        )}
        {children}
      </Container>
    </Box>
  );

  // === Project Slider Settings ===
  const projectSettings = {
    dots: true,
    infinite: true,
    speed: 1000, // Slower auto-scroll
    slidesToShow: 3, // Display 3 slides
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: '0px', // No padding, slides will touch but scale
    autoplay: true,
    autoplaySpeed: 4000, // Slower auto-scroll
    afterChange: (current) => setCurrentProjectIndex(current), // Update index on slide change
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          centerPadding: '0px',
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerPadding: '0px',
        }
      }
    ],
    // Custom dot styling
    appendDots: dots => (
      <div style={{ position: "absolute", bottom: "0px", width: "100%", display: "flex", justifyContent: "center", paddingTop: "20px" }}>
        <ul style={{ margin: "0px", padding: "0px" }}> {dots} </ul>
      </div>
    ),
    customPaging: i => (
      <div
        style={{
          width: "10px",
          height: "10px",
          backgroundColor: i === currentProjectIndex ? theme.palette.primary.main : theme.palette.text.secondary,
          borderRadius: "50%",
          display: "inline-block",
          margin: "0 5px",
          cursor: "pointer"
        }}
      ></div>
    )
  };

  // Function to handle click on a project card to center it
  const handleProjectClick = (index) => {
    if (projectSliderRef.current) {
      projectSliderRef.current.slickGoTo(index);
    }
  };

  // === Article Slider Settings (similar to Projects, but no focus effect) ===
  const articleSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ],
    appendDots: dots => (
        <div style={{ position: "absolute", bottom: "0px", width: "100%", display: "flex", justifyContent: "center", paddingTop: "20px" }}>
          <ul style={{ margin: "0px", padding: "0px" }}> {dots} </ul>
        </div>
      ),
      customPaging: i => (
        <div
          style={{
            width: "10px",
            height: "10px",
            backgroundColor: theme.palette.text.secondary,
            borderRadius: "50%",
            display: "inline-block",
            margin: "0 5px",
          }}
        ></div>
      )
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh', bgcolor: 'background.default' }}>
        <CircularProgress color="primary" />
        <Typography variant="h6" color="text.primary" sx={{ mt: 2 }}>Loading your awesome portfolio...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh', bgcolor: 'background.default' }}>
        <Typography variant="h6" color="error">{error}</Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>Check browser console and backend terminal for details.</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ bgcolor: 'background.default', color: 'text.primary' }}>
      {/* Hero Section */}
      <Box sx={{
        bgcolor: '#22264b', // Dark Blue-Gray from theme.js
        minHeight: '80vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        pt: 8,
        pb: 4,
        position: 'relative',
        overflow: 'hidden',
        color: '#eadfd0' // Light text for contrast
      }}>
        <Container maxWidth="md">
          <Typography variant="h6" component="p" gutterBottom sx={{ color: 'inherit', mb: 1 }}>
            {heroText.role_text || 'GRAPHIC DESIGNING'}
          </Typography>
          <Typography variant="h1" component="h1" sx={{ fontWeight: 'bold', fontSize: { xs: '3.5rem', sm: '4.5rem', md: '5.5rem' }, lineHeight: 1.1, mb: 2, color: 'inherit' }}>
            {heroText.main_title || 'PORTFOLIO'}<Box component="span" sx={{ fontSize: '0.5em', verticalAlign: 'super', ml: 1 }}>{heroText.year_text || '2023'}</Box>
          </Typography>
          <Typography variant="h5" component="p" sx={{ mb: 4, color: 'inherit' }}>
            {heroText.description || 'Creating stunning visuals for your brand.'}
          </Typography>
        </Container>
        {/* Absolute positioned name at bottom left */}
        <Box sx={{ position: 'absolute', bottom: 20, left: 40, color: 'inherit' }}>
            <Typography variant="h5" component="p" sx={{ fontWeight: 'medium' }}>
                {heroText.name || 'ANISHKA'}
            </Typography>
        </Box>
      </Box>

      {/* About Me Section */}
      <Box sx={{ bgcolor: 'background.default', py: 8, px: 4 }}>
        <Container maxWidth="md">
          <Grid container spacing={4} alignItems="center" justifyContent="center">
            {/* Image on left with custom border */}
            <Grid item xs={12} md={4} sx={{
              display: 'flex',
              justifyContent: 'center',
              position: 'relative',
              mt: { xs: -10, md: 0 }, // Adjust margin to overlap hero
              mb: { xs: 4, md: 0 },
            }}>
              <Box sx={{
                width: { xs: 200, sm: 250, md: 300 },
                height: { xs: 200, sm: 250, md: 300 },
                border: `8px solid ${theme.palette.background.paper}`, // Border warna putih dari theme
                bgcolor: 'grey.200',
                overflow: 'hidden',
                boxShadow: 3,
                position: 'relative',
                borderRadius: '50% 50% 10% 50% / 50% 50% 50% 10%', // Custom border-radius
              }}>
                <img
                  src={aboutMe.gambar_url || 'https://placehold.co/300x300/cccccc/333333?text=Profile'}
                  alt="Profile"
                  style={{
                    position: 'absolute',
                    width: '110%', // Make image slightly larger to 'overflow'
                    height: '110%',
                    objectFit: 'cover',
                    objectPosition: 'center',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                  }}
                  onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/300x300/cccccc/333333?text=Profile'; }}
                />
              </Box>
            </Grid>

            {/* Description and Hobbies on right */}
            <Grid item xs={12} md={8}>
              <Box sx={{ textAlign: { xs: 'center', md: 'left' } }}>
                <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'bold', color: 'text.primary' }}>
                  Hi! I'm {heroText.name || 'Anishka'}
                </Typography>
                <Typography variant="body1" color="text.secondary" sx={{ mb: 3, lineHeight: 1.8 }}>
                  {aboutMe.deskripsi || "I'm a graphic designer with a passion for making things look good. I'm also pretty funny, so if you're looking for someone to design your next marketing campaign, you've come to the right place. Just don't expect me to take myself too seriously."}
                </Typography>
                <Typography variant="body1" color="text.secondary" sx={{ mb: 4, lineHeight: 1.8 }}>
                  I am a creative and innovative thinker with a strong attention to detail. I am always looking for new ways to improve my skills and knowledge.
                </Typography>

                {/* Hobbies Section */}
                <Box sx={{ borderTop: 1, borderBottom: 1, borderColor: 'divider', py: 2 }}>
                  <Typography variant="h6" component="h3" sx={{ fontWeight: 'bold', mb: 2, color: 'text.primary' }}>
                    Hobbies
                  </Typography>
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, justifyContent: { xs: 'center', md: 'flex-start' } }}>
                    {hobbies.length === 0 ? (
                      <Typography variant="body2" color="text.secondary">No hobbies to display yet.</Typography>
                    ) : (
                      hobbies.map((hobby, index) => (
                        <React.Fragment key={hobby.id}>
                          <Typography
                            variant="body2"
                            sx={{
                              fontWeight: hobby.is_bold ? 'bold' : 'normal',
                              color: 'text.secondary'
                            }}
                          >
                            {hobby.hobby_name}
                          </Typography>
                          {index < hobbies.length - 1 && (
                            <Typography variant="body2" color="text.disabled">|</Typography>
                          )}
                        </React.Fragment>
                      ))
                    )}
                  </Box>
                </Box>
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>


      {/* Skills Section */}
      <Section title="01 PROFESSIONAL" subtitle="MY KNOWLEDGE LEVEL IN SOFTWARE" bgcolor="#22264b"> {/* Dark background */}
        <Grid container spacing={3} justifyContent="center">
          {skills.length === 0 ? (
            <Grid item xs={12}><Typography variant="body1" align="center" color="text.secondary">No skills to display yet.</Typography></Grid>
          ) : (
            skills.map((skill) => (
              <Grid item xs={12} sm={6} md={4} key={skill.id}>
                <Box sx={{
                  p: 3,
                  borderRadius: 2,
                  bgcolor: '#292c4f', // Slightly lighter dark for cards
                  color: '#E0E0E0', // Light text
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  '&:hover': { transform: 'translateY(-3px)', transition: 'transform 0.2s' }
                }}>
                  <Typography variant="h6" gutterBottom sx={{ fontWeight: 'medium', color: 'inherit' }}>
                    {skill.nama_skill.toUpperCase()}
                  </Typography>
                  <Box sx={{ width: '100%', mt: 1 }}>
                    <LinearProgress
                      variant="determinate"
                      value={skill.level}
                      sx={{
                        height: 10,
                        borderRadius: 5,
                        bgcolor: '#534546', // Neutral Gray for background
                        '& .MuiLinearProgress-bar': {
                          bgcolor: theme.palette.primary.main, // Orange for progress
                        },
                      }}
                    />
                    <Typography variant="body2" sx={{ mt: 0.5, textAlign: 'right', color: 'inherit' }}>
                      {skill.level}%
                    </Typography>
                  </Box>
                </Box>
              </Grid>
            ))
          )}
        </Grid>
      </Section>

      {/* Projects Section */}
      <Section title="02 PORTFOLIO" subtitle="MY LATEST WORK.">
        {projects.length === 0 ? (
          <Typography variant="body1" align="center" color="text.secondary">No projects to display yet.</Typography>
        ) : (
          <Box sx={{ width: '100%' }}>
            {/* Judul dan Deskripsi Proyek Aktif - DI BAWAH CAROUSEL, DI ATAS DOT */}
            <Box sx={{ textAlign: 'center', mb: 5, px: 4, maxWidth: { xs: '100%', md: '80%', lg: '60%' }, mx: 'auto' }}>
              <Typography variant="h4" component="h3" sx={{ fontWeight: 'bold', mb: 2, color: 'text.primary' }}>
                {projects[currentProjectIndex]?.nama_project?.toUpperCase() || ''}
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {projects[currentProjectIndex]?.deskripsi || ''}
              </Typography>
            </Box>

            <Slider {...projectSettings} ref={projectSliderRef}>
              {projects.map((project, index) => {
                const isActive = index === currentProjectIndex;
                const scale = isActive ? 1 : 0.8; // Active item is 100%, inactive is 80%
                const opacity = isActive ? 1 : 0.6; // Active item is opaque, inactive is semi-transparent
                const width = isActive ? '300px' : '200px'; // Adjust these fixed widths as needed
                const height = '350px'; // Fixed height for all cards

                return (
                  <Box key={project.id} sx={{
                    px: 1, // Padding between slides
                    display: 'flex !important', // Important for slick to render correctly
                    justifyContent: 'center',
                    alignItems: 'center',
                  }} onClick={() => handleProjectClick(index)}>
                    <Card sx={{
                      width: width, // Apply width directly
                      height: height, // Apply height directly
                      borderRadius: 2,
                      boxShadow: 3,
                      bgcolor: 'background.paper',
                      display: 'flex',
                      flexDirection: 'column',
                      position: 'relative',
                      overflow: 'hidden',
                      transform: `scale(${scale})`, // Apply scale
                      opacity: opacity, // Apply opacity
                      transition: 'all 0.5s ease-in-out', // Smooth transition
                      cursor: 'pointer',
                      '&:hover': {
                        transform: `scale(${scale * 1.05})`, // Slight grow on hover
                        boxShadow: `0 8px 20px rgba(0,0,0,0.4)`
                      }
                    }}>
                      {project.gambar && (
                        <CardMedia
                          component="img"
                          image={project.gambar}
                          alt={project.nama_project}
                          sx={{
                            height: '100%', // Fill card height
                            objectFit: 'cover',
                            width: '100%',
                          }}
                        />
                      )}
                      {/* Overlay: PROJECT X / VIEW PROJECT on hover */}
                      <Box sx={{
                        position: 'absolute',
                        inset: 0,
                        bgcolor: 'rgba(0,0,0,0.6)', // Dark overlay
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        color: 'white',
                        textAlign: 'center',
                        transition: 'background-color 0.3s ease',
                        opacity: 0, // Default hidden
                        '&:hover': {
                          opacity: 1, // Show on hover
                        }
                      }}>
                          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>
                              PROJECT {index + 1 < 10 ? '0' : ''}{index + 1}
                          </Typography>
                          {project.link && (
                              <MuiLink href={project.link} target="_blank" rel="noopener" sx={{
                                  display: 'flex',
                                  alignItems: 'center',
                                  textDecoration: 'none',
                                  color: 'white', // White text for the link
                                  border: '1px solid white', // White border for the button effect
                                  px: 2, py: 1, borderRadius: '4px',
                                  '&:hover': {
                                      bgcolor: 'rgba(255,255,255,0.2)',
                                      textDecoration: 'none',
                                  }
                              }}>
                                  VIEW PROJECT <OpenInNewIcon sx={{ ml: 0.5, fontSize: 'inherit' }} />
                              </MuiLink>
                          )}
                      </Box>
                    </Card>
                  </Box>
                );
              })}
            </Slider>
            {/* <Link to="/projects" style={{ display: 'block', textAlign: 'center', marginTop: theme.spacing(4), textDecoration: 'none' }}>
                <Button variant="outlined" color="primary" sx={{ px: 4, py: 1.5, borderRadius: '25px' }}>
                    View All Projects
                </Button>
            </Link> */}
          </Box>
        )}
      </Section>

      {/* Experience Section */}
      <Section title="WORK EXPERIENCE" subtitle="MY PROFESSIONAL JOURNEY." bgcolor="background.default">
        <Timeline position="alternate" sx={{
          '& .MuiTimelineItem-root:before': {
            flex: { xs: 0, md: 1 },
            padding: { xs: 0, md: '6px 16px' }
          }
        }}>
          {experiences.length === 0 ? (
            <TimelineItem>
              <TimelineOppositeContent color="text.secondary">
                <Typography variant="body2">No experiences to display yet.</Typography>
              </TimelineOppositeContent>
              <TimelineSeparator>
                <TimelineDot color="grey" />
              </TimelineSeparator>
              <TimelineContent></TimelineContent>
            </TimelineItem>
          ) : (
            experiences.map((exp, index) => (
              <TimelineItem key={exp.id}>
                <TimelineOppositeContent
                  sx={{ m: 'auto 0' }}
                  align={index % 2 === 0 ? "right" : "left"}
                  variant="body2"
                  color="text.secondary"
                >
                  {exp.tahun}
                </TimelineOppositeContent>
                <TimelineSeparator>
                  <TimelineConnector sx={{ bgcolor: 'primary.main' }} />
                  <TimelineDot color="primary" />
                  <TimelineConnector sx={{ bgcolor: 'primary.main' }} />
                </TimelineSeparator>
                <TimelineContent sx={{ py: '12px', px: 2 }}>
                  <Paper elevation={3} sx={{ p: 3, borderRadius: 2, bgcolor: 'background.paper' }}>
                    <Typography variant="h6" component="h3" color="text.primary">
                      {exp.posisi}
                    </Typography>
                    <Typography>{exp.tempat}</Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      {exp.deskripsi}
                    </Typography>
                  </Paper>
                </TimelineContent>
              </TimelineItem>
            ))
          )}
        </Timeline>
      </Section>

      {/* Education Section */}
      <Section title="EDUCATION" subtitle="MY ACADEMIC BACKGROUND." bgcolor="background.default">
        <Timeline position="alternate" sx={{
          '& .MuiTimelineItem-root:before': {
            flex: { xs: 0, md: 1 },
            padding: { xs: 0, md: '6px 16px' }
          }
        }}>
          {education.length === 0 ? (
            <TimelineItem>
              <TimelineOppositeContent color="text.secondary">
                <Typography variant="body2">No education records to display yet.</Typography>
              </TimelineOppositeContent>
              <TimelineSeparator>
                <TimelineDot color="grey" />
              </TimelineSeparator>
              <TimelineContent></TimelineContent>
            </TimelineItem>
          ) : (
            education.map((edu, index) => (
              <TimelineItem key={edu.id}>
                <TimelineOppositeContent
                  sx={{ m: 'auto 0' }}
                  align={index % 2 === 0 ? "right" : "left"}
                  variant="body2"
                  color="text.secondary"
                >
                  {edu.tahun}
                </TimelineOppositeContent>
                <TimelineSeparator>
                  <TimelineConnector sx={{ bgcolor: 'secondary.main' }} />
                  <TimelineDot color="secondary" />
                  <TimelineConnector sx={{ bgcolor: 'secondary.main' }} />
                </TimelineSeparator>
                <TimelineContent sx={{ py: '12px', px: 2 }}>
                  <Paper elevation={3} sx={{ p: 3, borderRadius: 2, bgcolor: 'background.paper' }}>
                    <Typography variant="h6" component="h3" color="text.primary">
                      {edu.jurusan}
                    </Typography>
                    <Typography>{edu.institusi}</Typography>
                  </Paper>
                </TimelineContent>
              </TimelineItem>
            ))
          )}
        </Timeline>
      </Section>

      {/* Activities Section */}
      <Section title="ACTIVITIES" subtitle="MY INVOLVEMENT BEYOND ACADEMICS AND WORK." bgcolor="background.default">
        <Grid container spacing={4} justifyContent="center">
          {activities.length === 0 ? (
            <Grid item xs={12}><Typography variant="body1" align="center" color="text.secondary">No activities to display yet.</Typography></Grid>
          ) : (
            activities.map((act) => (
              <Grid item xs={12} sm={6} md={4} key={act.id}>
                <Paper elevation={0} sx={{
                  p: 2,
                  borderRadius: 0,
                  border: `1px solid ${theme.palette.divider}`,
                  bgcolor: 'background.paper',
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  '&:hover': { transform: 'translateY(-3px)', transition: 'transform 0.2s' }
                }}>
                  {act.image && (
                    <Box sx={{ mb: 1, display: 'flex', justifyContent: 'center' }}>
                      <img src={act.image} alt={act.title} style={{ maxWidth: '100%', height: 'auto', borderRadius: '4px' }} />
                    </Box>
                  )}
                  <Typography variant="h6" color="text.primary" sx={{ fontWeight: 'medium' }}>
                    {act.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                    {act.time}
                  </Typography>
                  <Typography variant="body1" color="text.primary" sx={{ flexGrow: 1, mt: 1 }}>
                    {act.deskripsi}
                  </Typography>
                </Paper>
              </Grid>
            ))
          )}
        </Grid>
      </Section>

      {/* Articles Section */}
      <Section title="03 ARTICLES" subtitle="MY LATEST POSTS." bgcolor="background.default">
        {articles.length === 0 ? (
          <Typography variant="body1" align="center" color="text.secondary">No articles to display yet.</Typography>
        ) : (
          <Box sx={{ width: '100%' }}>
            <Slider {...articleSettings} ref={articleSliderRef}>
              {articles.map((article, index) => (
                <Box key={article.id} sx={{ px: 2 }}> {/* Padding between slides */}
                  <Card sx={{
                    borderRadius: 2,
                    boxShadow: 3,
                    bgcolor: 'background.paper',
                    display: 'flex',
                    flexDirection: 'column',
                    height: '100%', // Ensure consistent height
                    minHeight: '350px', // Min height for consistency
                    position: 'relative',
                    overflow: 'hidden',
                    '&:hover': {
                      transform: 'translateY(-5px)',
                      transition: 'transform 0.3s ease-in-out',
                      boxShadow: `0 8px 20px rgba(0,0,0,0.4)`
                    }
                  }}>
                    {article.image_url && (
                      <CardMedia
                        component="img"
                        image={article.image_url}
                        alt={article.title}
                        sx={{
                          height: 200, // Fixed height for image
                          objectFit: 'cover',
                          width: '100%',
                        }}
                      />
                    )}
                    <CardContent sx={{ flexGrow: 1, p: 3, textAlign: 'left' }}>
                      <Typography gutterBottom variant="h6" component="div" sx={{ fontWeight: 'bold', color: 'text.primary' }}>
                        {article.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {article.content}
                      </Typography>
                    </CardContent>
                    <CardActions sx={{ p: 2, justifyContent: 'flex-end' }}>
                      <MuiLink href="#" onClick={(e) => { e.preventDefault(); alert(`Reading more about: ${article.title}`); }} sx={{
                        display: 'flex',
                        alignItems: 'center',
                        textDecoration: 'none',
                        color: 'primary.main',
                        '&:hover': {
                          textDecoration: 'underline',
                          color: 'secondary.main',
                        }
                      }}>
                        Read More <OpenInNewIcon sx={{ ml: 0.5, fontSize: 'inherit' }} />
                      </MuiLink>
                    </CardActions>
                  </Card>
                </Box>
              ))}
            </Slider>
          </Box>
        )}
      </Section>


      {/* Footer */}
      <Box sx={{ textAlign: 'center', py: 5, bgcolor: '#22264b', color: '#eadfd0', borderTop: `1px solid ${theme.palette.divider}` }}>
        <Typography variant="body2">
          &copy; {new Date().getFullYear()} {heroText.name}. All rights reserved.
        </Typography>
        <Typography variant="body2" sx={{ mt: 0.5 }}>
          Designed and Built by Me
        </Typography>
      </Box>
    </Box>
  );
}
