import React from 'react';
import SectionHeader from './SectionHeader';

const EducationSection = ({ education }) => {
  return (
    <section className="py-16 px-4 bg-gray-100"> {/* Menggunakan bg-gray-100 yang default */}
      <SectionHeader
        title="Pendidikan"
        subtitle="Riwayat Pendidikan Formal dan Informal"
        titleColor="text-gray-800"
        subtitleColor="text-gray-600"
      />
      <div className="container mx-auto mt-8">
        {/* Garis tengah dengan warna indigo yang konsisten */}
        <div className="relative border-l-2 border-indigo-600 ml-4 pl-4">
          {education.map((edu, index) => (
            <div key={edu.id || index} className="mb-8 last:mb-0">
              {/* Titik pada garis dengan warna indigo dan border putih */}
              <div className="absolute w-4 h-4 bg-indigo-600 rounded-full -left-2 top-0 transform -translate-x-1/2 -translate-y-1/2 border-2 border-white"></div>
              <h3 className="text-xl font-semibold text-gray-800">{edu.jurusan}</h3>
              <p className="text-lg text-gray-700">{edu.institusi}</p>
              <p className="text-gray-500 text-sm">{edu.tahun}</p>
              <p className="text-gray-600 mt-2">{edu.deskripsi}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default EducationSection;
