import React, { useState, useEffect } from 'react';
import AddProjectForm from './AddProjectForm';
import AddSkillForm from './AddSkillForm';

// Komponen Dashboard untuk mengelola data portfolio (admin)
function Dashboard() {
  const [heroText, setHeroText] = useState({});
  const [aboutMe, setAboutMe] = useState({});
  const [projects, setProjects] = useState([]);
  const [skills, setSkills] = useState([]);
  const [education, setEducation] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [activities, setActivities] = useState([]);

  // States untuk mode edit item yang SUDAH ADA
  const [editingProject, setEditingProject] = useState(null);
  const [editingSkill, setEditingSkill] = useState(null);
  const [editingEducation, setEditingEducation] = useState(null);
  const [editingExperience, setEditingExperience] = useState(null);
  const [editingActivity, setEditingActivity] = useState(null);

  // States untuk mode TAMBAH item BARU
  const [showAddEducationForm, setShowAddEducationForm] = useState(false);
  const [newEducationItem, setNewEducationItem] = useState({ jurusan: '', institusi: '', tahun: '' });

  const [showAddExperienceForm, setShowAddExperienceForm] = useState(false);
  const [newExperienceItem, setNewExperienceItem] = useState({ posisi: '', tempat: '', tahun: '', deskripsi: '' });

  const [showAddActivityForm, setShowAddActivityForm] = useState(false);
  const [newActivityItem, setNewActivityItem] = useState({ title: '', time: '', image: '', deskripsi: '' });


  const [message, setMessage] = useState(''); // Pesan umum untuk notifikasi

  const API_BASE_URL = 'http://localhost:3001/api';

  // Fungsi untuk mengambil semua data dari backend
  const fetchData = async () => {
    setMessage(''); // Reset pesan

    try {
      const [
        heroRes, aboutRes, projectsRes, skillsRes,
        educationRes, experiencesRes, activitiesRes
      ] = await Promise.all([
        fetch(`${API_BASE_URL}/hero`),
        fetch(`${API_BASE_URL}/about`),
        fetch(`${API_BASE_URL}/projects`),
        fetch(`${API_BASE_URL}/skills`),
        fetch(`${API_BASE_URL}/education`),
        fetch(`${API_BASE_URL}/experiences`),
        fetch(`${API_BASE_URL}/activities`)
      ]);

      const heroData = await heroRes.json();
      const aboutData = await aboutRes.json();
      const projectsData = await projectsRes.json();
      const skillsData = await skillsRes.json();
      const educationData = await educationRes.json();
      const experiencesData = await experiencesRes.json();
      const activitiesData = await activitiesRes.json();

      setHeroText(heroData || { name: '', description: '' }); // Pastikan objek tidak null
      setAboutMe(aboutData || { deskripsi: '', gambar_url: '' });
      setProjects(projectsData || []);
      setSkills(skillsData || []);
      setEducation(educationData || []);
      setExperiences(experiencesData || []);
      setActivities(activitiesData || []);

    } catch (error) {
      console.error('Error fetching data:', error);
      setMessage('Error fetching data from server. Please ensure backend is running.');
    }
  };

  useEffect(() => {
    fetchData(); // Panggil saat komponen dimuat
  }, []);

  // --- Handlers untuk Hero Text ---
  const handleHeroTextChange = (e) => {
    const { name, value } = e.target;
    setHeroText(prev => ({ ...prev, [name]: value }));
  };

  const saveHeroText = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/hero`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(heroText)
      });
      if (response.ok) {
        setMessage('Hero text updated successfully!');
        fetchData(); // Refresh data
      } else {
        const errorData = await response.json();
        setMessage(`Error updating hero text: ${errorData.message || 'Something went wrong.'}`);
      }
    } catch (error) {
      console.error('Error saving hero text:', error);
      setMessage('Network error. Could not save hero text.');
    }
  };

  // --- Handlers untuk About Me ---
  const handleAboutMeChange = (e) => {
    const { name, value } = e.target;
    setAboutMe(prev => ({ ...prev, [name]: value }));
  };

  const saveAboutMe = async () => {
    try {
      // Pastikan ada ID, diasumsikan ID About Me selalu 1
      const idToUse = aboutMe.id || 1;
      const response = await fetch(`${API_BASE_URL}/about/${idToUse}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(aboutMe)
      });
      if (response.ok) {
        setMessage('About Me data updated successfully!');
        fetchData();
      } else {
        const errorData = await response.json();
        setMessage(`Error updating About Me: ${errorData.message || 'Something went wrong.'}`);
      }
    } catch (error) {
      console.error('Error saving About Me data:', error);
      setMessage('Network error. Could not save About Me data.');
    }
  };

  // --- Handlers CRUD Umum (untuk Project, Skill, Education, Experience, Activity) ---
  const handleDelete = async (endpoint, id) => {
    if (!window.confirm('Are you sure you want to delete this item?')) {
      return;
    }
    try {
      const response = await fetch(`${API_BASE_URL}/${endpoint}/${id}`, {
        method: 'DELETE'
      });
      if (response.ok) {
        setMessage('Item deleted successfully!');
        fetchData(); // Refresh data setelah penghapusan
      } else {
        const errorData = await response.json();
        setMessage(`Error deleting item: ${errorData.message || 'Something went wrong.'}`);
      }
    } catch (error) {
      console.error(`Error deleting item from ${endpoint}:`, error);
      setMessage('Network error. Could not delete item.');
    }
  };

  // --- Form specific handlers (untuk AddProjectForm & AddSkillForm) ---
  const handleSaveForm = () => {
    setEditingProject(null);
    setEditingSkill(null);
    // Pastikan tidak ada form "add new" lain yang terbuka jika save dari AddProjectForm/AddSkillForm
    setShowAddEducationForm(false);
    setShowAddExperienceForm(false);
    setShowAddActivityForm(false);
    fetchData(); // Refresh semua data setelah simpan
  };

  const handleCancelForm = () => {
    setEditingProject(null);
    setEditingSkill(null);
    // Batalkan form "add new" atau "edit inline"
    setEditingEducation(null);
    setShowAddEducationForm(false);
    setNewEducationItem({ jurusan: '', institusi: '', tahun: '' }); // Reset new item form

    setEditingExperience(null);
    setShowAddExperienceForm(false);
    setNewExperienceItem({ posisi: '', tempat: '', tahun: '', deskripsi: '' }); // Reset new item form

    setEditingActivity(null);
    setShowAddActivityForm(false);
    setNewActivityItem({ title: '', time: '', image: '', deskripsi: '' }); // Reset new item form

    setMessage(''); // Bersihkan pesan
  };


  // --- Handlers untuk Education ---
  // Fungsi untuk update data di form "add new" education
  const handleNewEducationItemChange = (e) => {
    const { name, value } = e.target;
    setNewEducationItem(prev => ({ ...prev, [name]: value }));
  };

  // Fungsi untuk update data di form "edit existing" education (inline)
  const handleEditingEducationChange = (fieldName, value) => {
    setEditingEducation(prev => ({ ...prev, [fieldName]: value }));
  };

  // Fungsi untuk menyimpan atau menambah entri pendidikan
  const handleSaveEducation = async (itemToSave) => {
    try {
      let response;
      if (itemToSave.id) { // Jika ada ID, ini adalah update (PUT)
        response = await fetch(`${API_BASE_URL}/education/${itemToSave.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(itemToSave)
        });
      } else { // Jika tidak ada ID, ini adalah penambahan baru (POST)
        response = await fetch(`${API_BASE_URL}/education`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(itemToSave)
        });
      }

      if (response.ok) {
        setMessage(`Education ${itemToSave.id ? 'updated' : 'added'} successfully!`);
        handleCancelForm(); // Reset semua form dan refresh data
      } else {
        const errorData = await response.json();
        setMessage(`Error saving education: ${errorData.message || 'Something went wrong.'}`);
      }
    } catch (error) {
      console.error('Error saving education:', error);
      setMessage('Network error. Could not save education.');
    }
  };


  // --- Handlers for Experience ---
  // Fungsi untuk update data di form "add new" experience
  const handleNewExperienceItemChange = (e) => {
    const { name, value } = e.target;
    setNewExperienceItem(prev => ({ ...prev, [name]: value }));
  };

  // Fungsi untuk update data di form "edit existing" experience (inline)
  const handleEditingExperienceChange = (fieldName, value) => {
    setEditingExperience(prev => ({ ...prev, [fieldName]: value }));
  };

  // Fungsi untuk menyimpan atau menambah entri pengalaman
  const handleSaveExperience = async (itemToSave) => {
    try {
      let response;
      if (itemToSave.id) { // Jika ada ID, ini adalah update (PUT)
        response = await fetch(`${API_BASE_URL}/experiences/${itemToSave.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(itemToSave)
        });
      } else { // Jika tidak ada ID, ini adalah penambahan baru (POST)
        response = await fetch(`${API_BASE_URL}/experiences`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(itemToSave)
        });
      }

      if (response.ok) {
        setMessage(`Experience ${itemToSave.id ? 'updated' : 'added'} successfully!`);
        handleCancelForm(); // Reset semua form dan refresh data
      } else {
        const errorData = await response.json();
        setMessage(`Error saving experience: ${errorData.message || 'Something went wrong.'}`);
      }
    } catch (error) {
      console.error('Error saving experience:', error);
      setMessage('Network error. Could not save experience.');
    }
  };

  // --- Handlers for Activities ---
  // Fungsi untuk update data di form "add new" activity
  const handleNewActivityItemChange = (e) => {
    const { name, value } = e.target;
    setNewActivityItem(prev => ({ ...prev, [name]: value }));
  };

  // Fungsi untuk update data di form "edit existing" activity (inline)
  const handleEditingActivityChange = (fieldName, value) => {
    setEditingActivity(prev => ({ ...prev, [fieldName]: value }));
  };

  // Fungsi untuk menyimpan atau menambah entri aktivitas
  const handleSaveActivity = async (itemToSave) => {
    try {
      let response;
      if (itemToSave.id) { // Jika ada ID, ini adalah update (PUT)
        response = await fetch(`${API_BASE_URL}/activities/${itemToSave.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(itemToSave)
        });
      } else { // Jika tidak ada ID, ini adalah penambahan baru (POST)
        response = await fetch(`${API_BASE_URL}/activities`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(itemToSave)
        });
      }

      if (response.ok) {
        setMessage(`Activity ${itemToSave.id ? 'updated' : 'added'} successfully!`);
        handleCancelForm(); // Reset semua form dan refresh data
      } else {
        const errorData = await response.json();
        setMessage(`Error saving activity: ${errorData.message || 'Something went wrong.'}`);
      }
    } catch (error) {
      console.error('Error saving activity:', error);
      setMessage('Network error. Could not save activity.');
    }
  };


  return (
    <div className="p-4 bg-gray-50 min-h-screen rounded-lg shadow-md">
      <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">Dashboard Admin</h1>

      {message && (
        <div className={`p-3 mb-4 rounded-md ${message.startsWith('Error') ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'}`}>
          {message}
        </div>
      )}

      {/* Hero Text Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Hero Section</h2>
        <div className="space-y-4">
          <div>
            <label htmlFor="heroName" className="block text-sm font-medium text-gray-700">Nama:</label>
            <input
              type="text"
              id="heroName"
              name="name"
              value={heroText.name || ''}
              onChange={handleHeroTextChange}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="heroDescription" className="block text-sm font-medium text-gray-700">Deskripsi:</label>
            <textarea
              id="heroDescription"
              name="description"
              value={heroText.description || ''}
              onChange={handleHeroTextChange}
              rows="3"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            ></textarea>
          </div>
          <div className="flex justify-end">
            <button
              onClick={saveHeroText}
              className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Simpan Hero Text
            </button>
          </div>
        </div>
      </section>

      {/* About Me Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">About Me Section</h2>
        <div className="space-y-4">
          <div>
            <label htmlFor="aboutMeDescription" className="block text-sm font-medium text-gray-700">Deskripsi:</label>
            <textarea
              id="aboutMeDescription"
              name="deskripsi"
              value={aboutMe.deskripsi || ''}
              onChange={handleAboutMeChange}
              rows="5"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            ></textarea>
          </div>
          <div>
            <label htmlFor="aboutMeImage" className="block text-sm font-medium text-gray-700">URL Gambar:</label>
            <input
              type="url"
              id="aboutMeImage"
              name="gambar_url"
              value={aboutMe.gambar_url || ''}
              onChange={handleAboutMeChange}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
          <div className="flex justify-end">
            <button
              onClick={saveAboutMe}
              className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Simpan About Me
            </button>
          </div>
        </div>
      </section>

      {/* Projects Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Proyek</h2>
        <button
          onClick={() => setEditingProject({})} // Buka form untuk proyek baru
          className="mb-4 px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
        >
          Tambah Proyek Baru
        </button>

        {editingProject && (
          <div className="mb-8">
            <AddProjectForm
              projectToEdit={editingProject.id ? editingProject : null}
              onSave={handleSaveForm}
              onCancel={handleCancelForm}
            />
          </div>
        )}

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nama Project</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Deskripsi</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Link</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Gambar URL</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {projects.map((project) => (
                <tr key={project.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{project.nama_project}</td>
                  <td className="px-6 py-4 whitespace-normal text-sm text-gray-500 max-w-xs overflow-hidden text-ellipsis">{project.deskripsi}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <a href={project.link} target="_blank" rel="noopener noreferrer" className="text-indigo-600 hover:text-indigo-900 truncate block max-w-[150px]">{project.link}</a>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {project.gambar && (
                      <img src={project.gambar} alt={project.nama_project} className="h-12 w-auto rounded-md object-cover" />
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button
                      onClick={() => setEditingProject(project)}
                      className="text-indigo-600 hover:text-indigo-900 mr-3"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete('projects', project.id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Hapus
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* Skills Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Keterampilan</h2>
        <button
          onClick={() => setEditingSkill({})} // Buka form untuk skill baru
          className="mb-4 px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
        >
          Tambah Skill Baru
        </button>

        {editingSkill && (
          <div className="mb-8">
            <AddSkillForm
              skillToEdit={editingSkill.id ? editingSkill : null}
              onSave={handleSaveForm}
              onCancel={handleCancelForm}
            />
          </div>
        )}

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nama Skill</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Level</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {skills.map((skill) => (
                <tr key={skill.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{skill.nama_skill}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{skill.level}%</td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button
                      onClick={() => setEditingSkill(skill)}
                      className="text-indigo-600 hover:text-indigo-900 mr-3"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete('skills', skill.id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Hapus
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* Education Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Pendidikan</h2>
        <button
          onClick={() => setShowAddEducationForm(true)} // Tampilkan form tambah baru
          className="mb-4 px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
        >
          Tambah Pendidikan Baru
        </button>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institusi</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Jurusan</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tahun</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {showAddEducationForm && ( // Baris untuk tambah pendidikan baru
                <tr>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    <input
                      type="text"
                      name="institusi"
                      value={newEducationItem.institusi}
                      onChange={handleNewEducationItemChange}
                      placeholder="Institusi"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <input
                      type="text"
                      name="jurusan"
                      value={newEducationItem.jurusan}
                      onChange={handleNewEducationItemChange}
                      placeholder="Jurusan"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <input
                      type="text"
                      name="tahun"
                      value={newEducationItem.tahun}
                      onChange={handleNewEducationItemChange}
                      placeholder="Tahun"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleSaveEducation(newEducationItem)}
                        className="text-green-600 hover:text-green-900"
                      >
                        Simpan
                      </button>
                      <button
                        onClick={handleCancelForm}
                        className="text-gray-600 hover:text-gray-900"
                      >
                        Batal
                      </button>
                    </div>
                  </td>
                </tr>
              )}
              {education.map((edu) => ( // Baris untuk pendidikan yang sudah ada
                <tr key={edu.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {editingEducation && editingEducation.id === edu.id ? (
                      <input
                        type="text"
                        name="institusi"
                        value={editingEducation.institusi || ''}
                        onChange={(e) => handleEditingEducationChange('institusi', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      edu.institusi
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {editingEducation && editingEducation.id === edu.id ? (
                      <input
                        type="text"
                        name="jurusan"
                        value={editingEducation.jurusan || ''}
                        onChange={(e) => handleEditingEducationChange('jurusan', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      edu.jurusan
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {editingEducation && editingEducation.id === edu.id ? (
                      <input
                        type="text"
                        name="tahun"
                        value={editingEducation.tahun || ''}
                        onChange={(e) => handleEditingEducationChange('tahun', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      edu.tahun
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    {editingEducation && editingEducation.id === edu.id ? (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleSaveEducation(editingEducation)}
                          className="text-green-600 hover:text-green-900"
                        >
                          Simpan
                        </button>
                        <button
                          onClick={handleCancelForm}
                          className="text-gray-600 hover:text-gray-900"
                        >
                          Batal
                        </button>
                      </div>
                    ) : (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => setEditingEducation(edu)}
                          className="text-indigo-600 hover:text-indigo-900"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete('education', edu.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Hapus
                        </button>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>


      {/* Experiences Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Pengalaman</h2>
        <button
          onClick={() => setShowAddExperienceForm(true)} // Tampilkan form tambah baru
          className="mb-4 px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
        >
          Tambah Pengalaman Baru
        </button>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Posisi</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tempat</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tahun</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Deskripsi</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {showAddExperienceForm && ( // Baris untuk tambah pengalaman baru
                <tr>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    <input
                      type="text"
                      name="posisi"
                      value={newExperienceItem.posisi}
                      onChange={handleNewExperienceItemChange}
                      placeholder="Posisi"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <input
                      type="text"
                      name="tempat"
                      value={newExperienceItem.tempat}
                      onChange={handleNewExperienceItemChange}
                      placeholder="Tempat"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <input
                      type="text"
                      name="tahun"
                      value={newExperienceItem.tahun}
                      onChange={handleNewExperienceItemChange}
                      placeholder="Tahun"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-normal text-sm text-gray-500 max-w-xs overflow-hidden text-ellipsis">
                    <textarea
                      name="deskripsi"
                      value={newExperienceItem.deskripsi}
                      onChange={handleNewExperienceItemChange}
                      placeholder="Deskripsi"
                      rows="2"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    ></textarea>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleSaveExperience(newExperienceItem)}
                        className="text-green-600 hover:text-green-900"
                      >
                        Simpan
                      </button>
                      <button
                        onClick={handleCancelForm}
                        className="text-gray-600 hover:text-gray-900"
                      >
                        Batal
                      </button>
                    </div>
                  </td>
                </tr>
              )}
              {experiences.map((exp) => ( // Baris untuk pengalaman yang sudah ada
                <tr key={exp.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {editingExperience && editingExperience.id === exp.id ? (
                      <input
                        type="text"
                        name="posisi"
                        value={editingExperience.posisi || ''}
                        onChange={(e) => handleEditingExperienceChange('posisi', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      exp.posisi
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {editingExperience && editingExperience.id === exp.id ? (
                      <input
                        type="text"
                        name="tempat"
                        value={editingExperience.tempat || ''}
                        onChange={(e) => handleEditingExperienceChange('tempat', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      exp.tempat
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {editingExperience && editingExperience.id === exp.id ? (
                      <input
                        type="text"
                        name="tahun"
                        value={editingExperience.tahun || ''}
                        onChange={(e) => handleEditingExperienceChange('tahun', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      exp.tahun
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-normal text-sm text-gray-500 max-w-xs overflow-hidden text-ellipsis">
                    {editingExperience && editingExperience.id === exp.id ? (
                      <textarea
                        name="deskripsi"
                        value={editingExperience.deskripsi || ''}
                        onChange={(e) => handleEditingExperienceChange('deskripsi', e.target.value)}
                        rows="2"
                        className="border border-gray-300 rounded-md p-1 w-full"
                      ></textarea>
                    ) : (
                      exp.deskripsi
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    {editingExperience && editingExperience.id === exp.id ? (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleSaveExperience(editingExperience)}
                          className="text-green-600 hover:text-green-900"
                        >
                          Simpan
                        </button>
                        <button
                          onClick={handleCancelForm}
                          className="text-gray-600 hover:text-gray-900"
                        >
                          Batal
                        </button>
                      </div>
                    ) : (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => setEditingExperience(exp)}
                          className="text-indigo-600 hover:text-indigo-900"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete('experiences', exp.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Hapus
                        </button>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* Activities Management */}
      <section className="mb-8 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Aktivitas</h2>
        <button
          onClick={() => setShowAddActivityForm(true)} // Tampilkan form tambah baru
          className="mb-4 px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
        >
          Tambah Aktivitas Baru
        </button>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Judul</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Waktu</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Gambar URL</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Deskripsi</th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {showAddActivityForm && ( // Baris untuk tambah aktivitas baru
                <tr>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    <input
                      type="text"
                      name="title"
                      value={newActivityItem.title}
                      onChange={handleNewActivityItemChange}
                      placeholder="Judul"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <input
                      type="text"
                      name="time"
                      value={newActivityItem.time}
                      onChange={handleNewActivityItemChange}
                      placeholder="Waktu"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <input
                      type="url"
                      name="image"
                      value={newActivityItem.image}
                      onChange={handleNewActivityItemChange}
                      placeholder="URL Gambar"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-normal text-sm text-gray-500 max-w-xs overflow-hidden text-ellipsis">
                    <textarea
                      name="deskripsi"
                      value={newActivityItem.deskripsi}
                      onChange={handleNewActivityItemChange}
                      placeholder="Deskripsi"
                      rows="2"
                      className="border border-gray-300 rounded-md p-1 w-full"
                    ></textarea>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleSaveActivity(newActivityItem)}
                        className="text-green-600 hover:text-green-900"
                      >
                        Simpan
                      </button>
                      <button
                        onClick={handleCancelForm}
                        className="text-gray-600 hover:text-gray-900"
                      >
                        Batal
                      </button>
                    </div>
                  </td>
                </tr>
              )}
              {activities.map((act) => ( // Baris untuk aktivitas yang sudah ada
                <tr key={act.id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {editingActivity && editingActivity.id === act.id ? (
                      <input
                        type="text"
                        name="title"
                        value={editingActivity.title || ''}
                        onChange={(e) => handleEditingActivityChange('title', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      act.title
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {editingActivity && editingActivity.id === act.id ? (
                      <input
                        type="text"
                        name="time"
                        value={editingActivity.time || ''}
                        onChange={(e) => handleEditingActivityChange('time', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      act.time
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {editingActivity && editingActivity.id === act.id ? (
                      <input
                        type="url"
                        name="image"
                        value={editingActivity.image || ''}
                        onChange={(e) => handleEditingActivityChange('image', e.target.value)}
                        className="border border-gray-300 rounded-md p-1 w-full"
                      />
                    ) : (
                      act.image && <img src={act.image} alt={act.title} className="h-10 w-auto rounded-md object-cover" />
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-normal text-sm text-gray-500 max-w-xs overflow-hidden text-ellipsis">
                    {editingActivity && editingActivity.id === act.id ? (
                      <textarea
                        name="deskripsi"
                        value={editingActivity.deskripsi || ''}
                        onChange={(e) => handleEditingActivityChange('deskripsi', e.target.value)}
                        rows="2"
                        className="border border-gray-300 rounded-md p-1 w-full"
                      ></textarea>
                    ) : (
                      act.deskripsi
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    {editingActivity && editingActivity.id === act.id ? (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleSaveActivity(editingActivity)}
                          className="text-green-600 hover:text-green-900"
                        >
                          Simpan
                        </button>
                        <button
                          onClick={handleCancelForm}
                          className="text-gray-600 hover:text-gray-900"
                        >
                          Batal
                        </button>
                      </div>
                    ) : (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => setEditingActivity(act)}
                          className="text-indigo-600 hover:text-indigo-900"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete('activities', act.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Hapus
                        </button>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

    </div>
  );
}

export default Dashboard;
