// Asumsi ini adalah file src/pages/Home.js atau src/Home.js Anda
import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import AboutMeSection from './AboutMeSection'; // Pastikan jalur import benar

function Home() {
  // ... state lainnya ...
  const [heroText, setHeroText] = useState({ name: '', role_text: '', main_title: '', year_text: '' });
  const [aboutMe, setAboutMe] = useState({ id: 1, deskripsi: '', gambar_url: '', name: '' }); // Pastikan 'name' juga di sini
  const [hobbies, setHobbies] = useState([]); // State untuk hobi

  const API_BASE_URL = 'http://localhost:3001/api';

  useEffect(() => {
    // ... fetch data lainnya ...
    const fetchHeroText = async () => { /* ... */ };
    const fetchAboutMeData = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/about`);
        setAboutMe(response.data || { id: 1, deskripsi: '', gambar_url: '', name: '' });
      } catch (error) {
        console.error('Error fetching about me data:', error);
      }
    };
    const fetchHobbiesData = async () => { // Fungsi fetch hobi
      try {
        const response = await axios.get(`${API_BASE_URL}/hobbies`);
        setHobbies(response.data);
      } catch (error) {
        console.error('Error fetching hobbies:', error);
      }
    };

    fetchHeroText();
    fetchAboutMeData();
    fetchHobbiesData(); // Panggil fungsi fetch hobi
  }, []);

  // ... sisa kode Home.js ...

  return (
    <div className="home-page">
      {/* ... HeroSection dan komponen lainnya ... */}
      <AboutMeSection
        heroText={heroText}
        aboutMe={aboutMe}
        hobbies={hobbies} // <--- Tambahkan prop hobbies di sini!
      />
      {/* ... komponen lainnya ... */}
    </div>
  );
}

export default Home;
