// src/components/Home.js
import React, { useEffect, useState, useRef } from "react";
import Slider from "react-slick";
import axios from "axios";
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Paper,
  LinearProgress,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Link as MuiLink,
  CircularProgress,
  useTheme,
  Avatar
} from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';

import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineOppositeContent,
  TimelineDot
} from '@mui/lab';

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function Home() {
  const theme = useTheme();
  const projectSliderRef = useRef(null); // Create a ref for the Slider component

  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [education, setEducation] = useState([]);
  const [activities, setActivities] = useState([]);
  const [heroText, setHeroText] = useState({ name: "Loading Name...", description: "Loading description..." });
  const [aboutMe, setAboutMe] = useState({ deskripsi: "Loading about me...", gambar_url: "" });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // === Data Fetching ===
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [
          skillsRes,
          projectsRes,
          experiencesRes,
          educationRes,
          activitiesRes,
          heroTextRes,
          aboutMeRes,
        ] = await Promise.all([
          axios.get("http://localhost:3001/api/skills"),
          axios.get("http://localhost:3001/api/projects"),
          axios.get("http://localhost:3001/api/experiences"),
          axios.get("http://localhost:3001/api/education"),
          axios.get("http://localhost:3001/api/activities"),
          axios.get("http://localhost:3001/api/hero"),
          axios.get("http://localhost:3001/api/about"),
        ]);

        setSkills(skillsRes.data);
        setProjects(projectsRes.data);
        setExperiences(experiencesRes.data);
        setEducation(educationRes.data);
        setActivities(activitiesRes.data);
        setHeroText(heroTextRes.data);
        setAboutMe(aboutMeRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Failed to load data.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // === Component for Section Header ===
  const Section = ({ title, subtitle, children }) => (
    <Box sx={{ py: 8, bgcolor: 'background.default', textAlign: 'center' }}>
      <Container maxWidth="md">
        <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'bold', color: 'text.primary' }}>
          {title}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 4 }}>
          {subtitle}
        </Typography>
        {children}
      </Container>
    </Box>
  );

  // === Projects Slider Settings ===
  const projectSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: '60px',
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerPadding: '40px',
        }
      }
    ],
    // Custom dot styling to be below the image and remove project index
    appendDots: dots => (
      <div style={{ position: "absolute", bottom: "0px", width: "100%", display: "flex", justifyContent: "center", paddingTop: "20px" }}>
        <ul style={{ margin: "0px", padding: "0px" }}> {dots} </ul>
      </div>
    ),
    customPaging: i => (
      <div
        style={{
          width: "10px",
          height: "10px",
          backgroundColor: theme.palette.text.secondary,
          borderRadius: "50%",
          display: "inline-block",
          margin: "0 5px",
        }}
      ></div>
    )
  };

  // Function to handle click on a project card to center it
  const handleProjectClick = (index) => {
    if (projectSliderRef.current) {
      projectSliderRef.current.slickGoTo(index);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', bgcolor: 'background.default' }}>
        <CircularProgress color="primary" />
        <Typography variant="h6" color="text.primary" sx={{ ml: 2 }}>Loading...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', bgcolor: 'background.default' }}>
        <Typography variant="h6" color="error">{error}</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ bgcolor: 'background.default', color: 'text.primary' }}>
      {/* Hero Section */}
      <Box sx={{
        bgcolor: 'background.hero',
        minHeight: '80vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        pt: 8,
        pb: 4,
        position: 'relative',
        overflow: 'hidden'
      }}>
        <Container maxWidth="md">
          <Typography variant="h2" component="h1" gutterBottom sx={{ fontWeight: 'bold', color: 'text.hero' }}>
            {heroText.name}
          </Typography>
          <Typography variant="h5" component="p" sx={{ mb: 4, color: 'text.hero' }}>
            {heroText.description}
          </Typography>
          <Button variant="contained" color="primary" size="large" sx={{ px: 4, py: 1.5, borderRadius: '25px' }}>
            Learn More
          </Button>
        </Container>
      </Box>

      {/* About Me Section */}
      <Section title="About Me" subtitle="A brief introduction.">
        <Grid container spacing={4} alignItems="center" justifyContent="center">
          {aboutMe.gambar_url && (
            <Grid item xs={12} md={4} sx={{ display: 'flex', justifyContent: 'center' }}>
              <Avatar
                alt="Profile Picture"
                src={aboutMe.gambar_url}
                sx={{ width: 200, height: 200, border: `3px solid ${theme.palette.primary.main}`, boxShadow: `0 0 15px ${theme.palette.primary.main}` }}
              />
            </Grid>
          )}
          <Grid item xs={12} md={aboutMe.gambar_url ? 8 : 12}>
            <Typography variant="body1" align="left" color="text.primary" sx={{ lineHeight: 1.8 }}>
              {aboutMe.deskripsi}
            </Typography>
          </Grid>
        </Grid>
      </Section>

      {/* Skills Section */}
      <Section title="My Skills" subtitle="Proficiencies and Expertise.">
        <Grid container spacing={3} justifyContent="center">
          {skills.length === 0 ? (
            <Grid item xs={12}><Typography variant="body1" align="center" color="text.secondary">No skills to display yet.</Typography></Grid>
          ) : (
            skills.map((skill) => (
              <Grid item xs={12} sm={6} md={4} key={skill.id}>
                <Paper elevation={0} sx={{
                  p: 3,
                  borderRadius: 2,
                  border: `1px solid ${theme.palette.divider}`,
                  bgcolor: 'background.paper',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  '&:hover': { transform: 'translateY(-3px)', transition: 'transform 0.2s' }
                }}>
                  <Typography variant="h6" color="text.primary" gutterBottom sx={{ fontWeight: 'medium' }}>
                    {skill.nama_skill}
                  </Typography>
                  <Box sx={{ width: '100%', mt: 1 }}>
                    <LinearProgress
                      variant="determinate"
                      value={skill.level}
                      sx={{
                        height: 10,
                        borderRadius: 5,
                        bgcolor: theme.palette.grey[300], // Background of the progress bar
                        '& .MuiLinearProgress-bar': {
                          bgcolor: theme.palette.primary.main, // Color of the progress itself
                        },
                      }}
                    />
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5, textAlign: 'right' }}>
                      {skill.level}%
                    </Typography>
                  </Box>
                </Paper>
              </Grid>
            ))
          )}
        </Grid>
      </Section>

      {/* Projects Section */}
      <Section title="My Projects" subtitle="A showcase of my work.">
        {projects.length === 0 ? (
          <Typography variant="body1" align="center" color="text.secondary">No projects to display yet.</Typography>
        ) : (
          <Slider {...projectSettings} ref={projectSliderRef}>
            {projects.map((project, index) => (
              <Box key={project.id} sx={{ px: 2 }} onClick={() => handleProjectClick(index)}> {/* Add onClick here */}
                <Card sx={{
                  borderRadius: 2,
                  boxShadow: 3,
                  bgcolor: 'background.paper',
                  display: 'flex',
                  flexDirection: 'column',
                  height: '100%',
                  position: 'relative',
                  overflow: 'hidden',
                  '&:hover': {
                    transform: 'translateY(-5px)',
                    transition: 'transform 0.3s ease-in-out',
                    boxShadow: `0 8px 20px rgba(0,0,0,0.4)`
                  }
                }}>
                  {project.gambar && (
                    <CardMedia
                      component="img"
                      image={project.gambar}
                      alt={project.nama_project}
                      sx={{
                        height: 200,
                        objectFit: 'cover',
                        width: '100%',
                      }}
                    />
                  )}
                  <CardContent sx={{ flexGrow: 1, p: 3, textAlign: 'left' }}>
                    <Typography gutterBottom variant="h6" component="div" sx={{ fontWeight: 'bold', color: 'text.primary' }}>
                      {project.nama_project}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {project.deskripsi}
                    </Typography>
                  </CardContent>
                  {project.link && (
                    <CardActions sx={{ p: 2, justifyContent: 'flex-end' }}>
                      <MuiLink href={project.link} target="_blank" rel="noopener" sx={{
                        display: 'flex',
                        alignItems: 'center',
                        textDecoration: 'none',
                        color: 'primary.main',
                        '&:hover': {
                          textDecoration: 'underline',
                          color: 'secondary.main',
                        }
                      }}>
                        View Project <OpenInNewIcon sx={{ ml: 0.5, fontSize: 'inherit' }} />
                      </MuiLink>
                    </CardActions>
                  )}
                </Card>
              </Box>
            ))}
          </Slider>
        )}
      </Section>

      {/* Experience Section */}
      <Section title="Work Experience" subtitle="My professional journey.">
        <Timeline position="alternate" sx={{
          '& .MuiTimelineItem-root:before': {
            flex: { xs: 0, md: 1 },
            padding: { xs: 0, md: '6px 16px' }
          }
        }}>
          {experiences.length === 0 ? (
            <TimelineItem>
              <TimelineOppositeContent color="text.secondary">
                <Typography variant="body2">No experiences to display yet.</Typography>
              </TimelineOppositeContent>
              <TimelineSeparator>
                <TimelineDot color="grey" />
              </TimelineSeparator>
              <TimelineContent></TimelineContent>
            </TimelineItem>
          ) : (
            experiences.map((exp, index) => (
              <TimelineItem key={exp.id}>
                <TimelineOppositeContent
                  sx={{ m: 'auto 0' }}
                  align={index % 2 === 0 ? "right" : "left"}
                  variant="body2"
                  color="text.secondary"
                >
                  {exp.tahun}
                </TimelineOppositeContent>
                <TimelineSeparator>
                  <TimelineConnector sx={{ bgcolor: 'primary.main' }} />
                  <TimelineDot color="primary" />
                  <TimelineConnector sx={{ bgcolor: 'primary.main' }} />
                </TimelineSeparator>
                <TimelineContent sx={{ py: '12px', px: 2 }}>
                  <Paper elevation={3} sx={{ p: 3, borderRadius: 2, bgcolor: 'background.paper' }}>
                    <Typography variant="h6" component="h3" color="text.primary">
                      {exp.posisi}
                    </Typography>
                    <Typography>{exp.tempat}</Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      {exp.deskripsi}
                    </Typography>
                  </Paper>
                </TimelineContent>
              </TimelineItem>
            ))
          )}
        </Timeline>
      </Section>

      {/* Education Section */}
      <Section title="Education" subtitle="My academic background.">
        <Timeline position="alternate" sx={{
          '& .MuiTimelineItem-root:before': {
            flex: { xs: 0, md: 1 },
            padding: { xs: 0, md: '6px 16px' }
          }
        }}>
          {education.length === 0 ? (
            <TimelineItem>
              <TimelineOppositeContent color="text.secondary">
                <Typography variant="body2">No education records to display yet.</Typography>
              </TimelineOppositeContent>
              <TimelineSeparator>
                <TimelineDot color="grey" />
              </TimelineSeparator>
              <TimelineContent></TimelineContent>
            </TimelineItem>
          ) : (
            education.map((edu, index) => (
              <TimelineItem key={edu.id}>
                <TimelineOppositeContent
                  sx={{ m: 'auto 0' }}
                  align={index % 2 === 0 ? "right" : "left"}
                  variant="body2"
                  color="text.secondary"
                >
                  {edu.tahun}
                </TimelineOppositeContent>
                <TimelineSeparator>
                  <TimelineConnector sx={{ bgcolor: 'secondary.main' }} />
                  <TimelineDot color="secondary" />
                  <TimelineConnector sx={{ bgcolor: 'secondary.main' }} />
                </TimelineSeparator>
                <TimelineContent sx={{ py: '12px', px: 2 }}>
                  <Paper elevation={3} sx={{ p: 3, borderRadius: 2, bgcolor: 'background.paper' }}>
                    <Typography variant="h6" component="h3" color="text.primary">
                      {edu.jurusan}
                    </Typography>
                    <Typography>{edu.institusi}</Typography>
                  </Paper>
                </TimelineContent>
              </TimelineItem>
            ))
          )}
        </Timeline>
      </Section>

      {/* Activities Section */}
      <Section title="Activities" subtitle="My involvement beyond academics and work.">
        <Grid container spacing={4} justifyContent="center">
          {activities.length === 0 ? (
            <Grid item xs={12}><Typography variant="body1" align="center" color="text.secondary">No activities to display yet.</Typography></Grid>
          ) : (
            activities.map((act) => (
              <Grid item xs={12} sm={6} md={4} key={act.id}>
                <Paper elevation={0} sx={{
                  p: 2,
                  borderRadius: 0,
                  border: `1px solid ${theme.palette.divider}`,
                  bgcolor: 'background.paper',
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  '&:hover': { transform: 'translateY(-3px)', transition: 'transform 0.2s' }
                }}>
                  {act.image && (
                    <Box sx={{ mb: 1, display: 'flex', justifyContent: 'center' }}>
                      <img src={act.image} alt={act.title} style={{ maxWidth: '100%', height: 'auto', borderRadius: '4px' }} />
                    </Box>
                  )}
                  <Typography variant="h6" color="text.primary" sx={{ fontWeight: 'medium' }}>
                    {act.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                    {act.time}
                  </Typography>
                  <Typography variant="body1" color="text.primary" sx={{ flexGrow: 1, mt: 1 }}>
                    {act.deskripsi}
                  </Typography>
                </Paper>
              </Grid>
            ))
          )}
        </Grid>
      </Section>

      {/* Footer */}
      <Box sx={{ textAlign: 'center', py: 5, bgcolor: 'background.paper', color: 'text.secondary', borderTop: `1px solid ${theme.palette.divider}` }}>
        <Typography variant="body2">
